css:
@-moz-document url-prefix("https://yourdomainhere.atlassian.net/secure"), url-prefix("https://yourdomainhere.atlassian.net/projects"), url-prefix("https://yourdomainhere.atlassian.net/plugins"), url-prefix("https://yourdomainhere.atlassian.net/browse"), url-prefix("https://yourdomainhere.atlassian.net/issues") {
/* Version 20170727 */
/* 
 * Overall 
 */

a {
    color: #4789d1 !important;
}
body,
h1,
h2,
h3,
h4,
h5,
div,
td,
table,
navigator-body,
strong,
pre,
li.drop {
    background: #222 !important;
    color: #DAD9C2 !important;
}
th {
    background: #333 !important;
    border: 1px solid #333 !important;
    color: #DAD9C2 !important;
}
.tipsy-inner {
    background: rgba(51, 51, 51, 0.9) !important;
}
.tipsy-arrow {
    border-left-color: rgba(51, 51, 51, 0.9) !important;
}
.tipsy {
    background: transparent !important;
}
blockquote {
    background: #333 !important;
    color: #DAD9C2 !important;
}
.aui-button,
.toolbar-trigger {
    background: #DAD9C2 !important;
    color: #444 !important;
}
.code-keyword {
    color: orange !important;
}
.code-tag {
    color: lightgreen !important;
}
.code-quote {
	color: green !important;
}
/* 
 * Awesome, dark scrollbars
 * Thanks https://gist.github.com/devinrhode2/2573411, accessed via https://userstyles.org/styles/116696/jira-dark-nightshift-eye-care-based 
 */

::-webkit-scrollbar {
    width: 12px;
    /* This is more usable for users trying to click it. */
    
    background-color: rgba(0, 0, 0, 0);
    -webkit-border-radius: 100px;
}
/* hover effect for both scrollbar area, and scrollbar 'thumb' */

::-webkit-scrollbar:hover {
    background-color: rgba(0, 0, 0, 0.09);
}
/* The scrollbar 'thumb' ...that marque oval shape in a scrollbar */

::-webkit-scrollbar-thumb:vertical {
    /* This is the EXACT color of Mac OS scrollbars. 
     Yes, I pulled out digital color meter */
    
    background: rgba(0, 0, 0, 0.5);
    -webkit-border-radius: 100px;
}
::-webkit-scrollbar-thumb:vertical:active {
    background: rgba(0, 0, 0, 0.61);
    /* Some darker color when you click it */
    
    -webkit-border-radius: 100px;
}
.atlassian-autocomplete .suggestions li {
    color: #DAD9C2 !important;
}
/* 
 * Nav Bars
 */

a.aui-header,
nav.aui-header {
    background: #000;
}
.aui-nav-link {
    color: white !important;
}
.item-link {
    color: #DAD9C2 !important;
    background: #222 !important;
}
.item-link:hover {
    background: #333 !important;
}
.aui-dropdown2.aui-style-default.aui-dropdown2-in-header a {
    color: #DAD9C2 !important;
    background: #222 !important;
}
.aui-dropdown2.aui-style-default.aui-dropdown2-in-header a:hover {
    background: #333 !important;
}
div.aui-sidebar-wrapper,
div.aui-sidebar-body,
.sidebar-content-container,
.aui-sidebar-group {
    background: #000 !important;
}
/* 
 * Dashboard Page Edits
 */

div.dashboard-shim {
    background: rgba(51, 51, 51, 0.5) !important;
}
div.gadget-menu {
    background: transparent !important;
}
.gadget.color1 .dashboard-item-title {
    background: #205081 !important;
}
.gadget.color2 .dashboard-item-title {
    background: #912e25 !important;
}
.gadget.color3 .dashboard-item-title {
    background: #c1562a !important;
}
.gadget.color4 .dashboard-item-title {
    background: #0f7023 !important;
}
.gadget.color6 .dashboard-item-title {
    background: #573e70 !important;
}
/* 
 * Ticket Page Edits 
 */

.ajax-activity-content,
.jira-issue-macro,
.editable-field:hover {
    background: #222 !important;
    color: #DAD9C2 !important;
}
.collapsed-comments,
.show-more-comments {
    background: #333 !important;
    color: #DAD9C2 !important;
}
.collapsed-comments:hover {
    background: #444 !important;
}
div.issue-drop-zone__target,
span.blender,
div.attachment-delete,
ul {
    background: transparent !important;
}
.activity-item-description pre {
    color: #DAD9C2;
}
/*
 * fix for time estimate/remaining graphs
 * Credit: Andrew Ayers
 */

table#tt_single_graph_orig tr.tt_graph > td {
    background-color: #89afd7 !important;
}
table#tt_single_graph_remain tr.tt_graph > td:nth-of-type(1) {
    background-color: #cccccc !important;
}
table#tt_single_graph_remain tr.tt_graph > td:nth-of-type(2) {
    background-color: #ec8e00 !important;
}
table#tt_single_graph_spent tr.tt_graph > td:nth-of-type(1) {
    background-color: #cccccc !important;
}
table#tt_single_graph_spent tr.tt_graph > td:nth-of-type(2) {
    background-color: #51a825 !important;
}
/* 
 * Input Fields 
 */

label,
.aui-list-item-link {
    color: #DAD9C2 !important;
}
textarea,
input,
select,
.drop-menu,
span.overlay-icon,
button.value-item {
    background: #111 !important;
    color: #DAD9C2 !important;
}
#sidebar-scope-filter-list a,
.aui-dropdown2.aui-style-default.aui-layer.aui-alignment-element.aui-alignment-side-bottom.aui-alignment-snap-right.aui-alignment-element-attached-top.aui-alignment-element-attached-right.aui-alignment-target-attached-bottom.aui-alignment-target-attached-right.aui-alignment-enabled a,
#opsbar-operations_more_drop a,
#opsbar-transitions_more_drop a,
#wiki-edit-dropdown2-other-wikiEdit0 a,
#wiki-edit-dropdown2-icon-picker-wikiEdit0 a,
#wiki-edit-dropdown2-icon-picker-wikiEdit1 a,
#wiki-edit-dropdown2-attachment-wikiEdit0 a,
#wiki-edit-dropdown2-link-wikiEdit0 a,
#wiki-edit-dropdown2-text-style-wikiEdit0 a,
#wiki-edit-dropdown2-text-style-wikiEdit1 a,
#wiki-edit-dropdown2-more-wikiEdit0 a {
    color: #DAD9C2 !important;
    background: #222 !important;
}
#sidebar-scope-filter-list a:hover,
.aui-dropdown2.aui-style-default.aui-layer.aui-alignment-element.aui-alignment-side-bottom.aui-alignment-snap-right.aui-alignment-element-attached-top.aui-alignment-element-attached-right.aui-alignment-target-attached-bottom.aui-alignment-target-attached-right.aui-alignment-enabled a:hover,
#opsbar-operations_more_drop a:hover,
#opsbar-transitions_more_drop a:hover,
#wiki-edit-dropdown2-other-wikiEdit0 a:hover,
#wiki-edit-dropdown2-icon-picker-wikiEdit0 a:hover,
#wiki-edit-dropdown2-icon-picker-wikiEdit1 a:hover,
#wiki-edit-dropdown2-attachment-wikiEdit0 a:hover,
#wiki-edit-dropdown2-link-wikiEdit0 a:hover,
#wiki-edit-dropdown2-text-style-wikiEdit0 a:hover,
#wiki-edit-dropdown2-text-style-wikiEdit1 a:hover,
#wiki-edit-dropdown2-more-wikiEdit0 a:hover {
    background: #333 !important;
}
/* annoying "hint" text on Edit Comment popup */

.jira-dialog .buttons-container .hint-container {
    color: #DAD9C2;
}
/* 
 * Board Edits 
 */

li.ghx-column {
    background: #222 !important;
}
}
