@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("akihabara.cz"){

/* Background - BLACK*/
body, #v6, #body_background_middle, #body_background_bottom, #body_background_top, #menu_global_lista_obsah, #header, #stats, #logo {
	background: #000000 url(http://www.colorhexa.com/000000.png) !important;
	border-color: red !important;}
/* Background - Dark Gray*/
#content, tbody, .t_white, .t_mezera, tr td, input, #footer_v3, select, #banery_content, #main, #obsah, td div {
	background: #1c1c1c url(http://www.colorhexa.com/1c1c1c.png) !important;
	border-color: red !important;}
/* Background - Dark GrayV2*/
.menu_cast, #menu_v4, #menu_v5, #menu_v6, .menu_nadpis {
	background: #1e1e1e url(http://www.colorhexa.com/1e1e1e.png) !important;
	border-color: red !important;}
/* Background - Dark RED*/
h4, h1, h2, h3, h5, h6, .anime_elicha td, th, .anime_esuda td, #menu_global_lista_obsah ul, #search_v3, .menu_seznam_mezera, form#prihlaseni input, #registrace, #heslo, .n_udalosti_nadpis, .p_hodnoceni {
	background: #420000 url(http://www.colorhexa.com/420000.png) !important;
	border-color: red !important;}
/* Background - RED*/
.ruzek_left, .ruzek_right, .menu_seznam_activ {
	background: red url(http://www.colorhexa.com/ff0000.png) !important;
	border-color: red !important;}
/*Text - gray*/
#content, input, #stats, select, td, p{
	color: gray !important;}
/*Text - cyan*/
h4, h1, h2, h3, h5, h6, form#prihlaseni input, #registrace, #heslo {
	color: cyan !important;}
/*TExt - gold*/
a, li { 
	color: gold !important;}}
