css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("email.seznam.cz"){
#wm #wm-list {
    background-color: #1c1c1c;
}
#wm,
#wm table, .disabled {
    color: #008af4 !important;
}
#wm {
    background-color: #1c1c1c;
}
#wm-list .wm-footer {
    color: #008af4;
    background-color: #1c1c1c;
    text-align: center;
    padding: 11px;
}
#wm #wm-top {
    background-color: #1c1c1c;
}
#wm #wm-filters .wm-filters-row.active {
    color: #008af4;
    background-color: #4b4242;
    font-weight: bold;
    border-top: 1px solid #1c1c1c;
}
#wm #wm-filters .wm-filters-row {
    color: #d9f400;
    border-top: 1px solid transparent;
}
#wm-list .list-empty-info {
    text-align: center;
    padding-top: 100px;
    font-size: 15px;
    color: red;
    line-height: normal;
}
#wms .wm-list-message {
    color: red;
    border-color: #c1c1c1;
    background-color: red;
}
#wm .wm-list-message {
    color: #00dfff;
    border-color: #005;
    background-color: #1c1c1c;
}
#wm .wm-list-message .name,
#wm .wm-list-message .subject,
#wm .wm-list-message.unread .name,
#wm .wm-list-message.unread .subject {
    color: #00dfff;
}
#wm.no-touch .wm-list-message:hover {
    background-color: #3a3a3a;
}
#wm-detail {
    overflow: auto;
    background-color: #1c1c1c;
    height: auto;
    -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
}
#wm-message-body {
    margin-top: 24px;
    padding-bottom: 8px;
    padding-left: 80px;
    color: #00fff9;
    background-color: #1c1c1c;
}
#wm-detail {
    overflow: auto;
    background-color: #1c1c1c;
    height: auto;
    -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
}
#wm-message-head {
    position: relative;
    border-bottom: 1px solid #1c1c1c;
    padding: 16px 18px;
}
#wm-message {
    background-color: #1c1c1c;
    overflow-y: hidden;
    overflow-x: auto;
    position: relative;
}
user agent stylesheet div {
    display: block;
}
#wm #wm-message-footer {
    background-color: #1c1c1c;
    border-top: 1px solid #1c1c1c;
}
#wm .wm-button,
#wm .wm-link-group {
    border: 1px solid black;
    border-radius: 5px;
    background: #1c1c1c;
    color: lime;
}
#wm #wm-compose {
    background-color: #1c1c1c;
}
#wm.compose-view #wm-content {
    border-color: #1c1c1c;
}
#wm-compose #wm-compose-area {
    background-color: #353634;
    border: 1px solid #353634;
    border-top: none;
    border-bottom: none;
    padding: 16px;
    overflow: auto;
    position: relative;
    zoom: 1;
}
#wm-message-text blockquote {
    border-left: 2px solid #003399;
    color: #008af4;
    padding-left: 10px;
    margin: 0;
}
#wm-quick-reply textarea {
    box-sizing: border-box;
    background-color: #2b2b2b;
    border: 1px solid #2b2b2b;
    border-color: #2b2b2b #2b2b2b #2b2b2b #2b2b2b;
    box-shadow: inset 1px 1px 0px #ececec;
    color: #808080;
    width: 100%;
    min-height: 64px;
    padding: 8px;
    height: 64px;
    resize: vertical !important;
}
#wm-detail #wm-sky-ad {
    position: absolute;
    width: 193px;
    right: 0;
    top: 0;
    bottom: 0;
    min-height: 635px;
    background: #1c1c1c;
}
#wm-search-form input {
    background-color: #1c1c1c;
    box-sizing: border-box;
    border-width: 0;
    width: 100%;
    height: 26px;
    padding-left: 5px;
    padding-right: 26px;
    font-size: 14px;
}
#wm-message-text a {
    text-decoration: underline !important;
    text-shadow: none !important;
    color: #9f754a;
}
#wm #wm-filters .wm-filters-row {
    color: #a3ff00;
    border-top: 1px solid transparent;
}
#wm #wm-top .disabled,
#wm #wm-top .disabled .wm-icon {
    color: #a3ff00;
}
#wm a {
    color: #a3ff00;
    text-decoration: none;
}
#wm .wm-button,
#wm .wm-link-group {
    border: 1px solid black;
    border-radius: 5px;
    background: #1c1c1c;
    color: red;
}
#wm .user-name {
    color: #00e2f4 !important;
}
#wm-list-message, div.name, textarea, #wm-top, #wm-commands-selection{
	color: #a3ff00 !important;
}
div.wm-list-message.wide.unread, textarea, div.editor-controls, .editor-controls {
	background-color: #301515 !important;
	border-color: #00e2f4 !important;
}
#wm-compose-area, div#wm-message-body {
	border-color: #00e2f4 !important;
	border-width: 1px !important;
}
td {
	background-color: #1c1c1c !important;
	color: cyan !important;
	border-color: cyan !important;
}}
