.window, 
button, 
html, 
input, 
select, 
textarea, 
.button-label, 
.opponent-name, 
.status-bar-border, 
.control-link,
.score-label
{
    font-family: "LuloCleanW01-OneBold" !important;
}

.button-label, 
.opponent-name, 
.status-bar-border, 
.control-link, 
.tab-button, 
.automatch-options-textcolumn, 
.player-table,
.lobby-button,
.tables-page,
.automatch-options-numberinput
{
    font-size: 70% !important;
}

input {
 	font-size: 100% !important;   
}

.full-card-name, 
.landscape-name, 
.pile-bottom-text, 
.types-text-full,  
.card-text, 
.landscape-text
{
    font-family: "NeuzeitGroteskW01-Regular" !important;
}

.landscape-text
{
    font-size: 2.5em !important;
    top: -10px !important;
}

.supply .pile-bottom-text, 
.supply .types-text-full {
    font-size: 5em !important;
    top: 0 !important;
}

.hand .pile-bottom-text, 
.hand .types-text-full {
    font-size: 4em !important;
    top: 0 !important;
}

.supply .types-text-full {
    font-size: 7em !important;
}

.new-card-counter-container , 
.coin-cost-mini-text, 
.coin-cost-full-text, 
.status-bar-text, 
.debt-cost-mini-text, 
.debt-cost-full-text, 
.card-text-vp-text,
.full-card-text-container div[style*='bold']
{
    font-family: "LuloCleanW01-One" !important;
}

.log-line,
.ui-grid-header,
.ui-grid-cell,
.game-log-results
{
 	font-family: "Monospace 821" !important;   
}

.new-card-counter-container {
 	font-size: 2em !important; 
    background-color: #4d4d4d !important;
    opacity: .5 !important;
}

.new-card-counter-container:hover {
    background-color: #aa0000 !important;
    opacity: 1.0 !important;
}

.new-card-counter-text {
    left: -45% !important;
    opacity: .5 !important;
}

.coin-cost-mini-text, .coin-cost-full-text, .debt-cost-mini-text, .debt-cost-full-text {
 	font-size: 5em !important;   
    left: 5px !important;
}

.status-bar-text {
    left: 2px !important;
}


.game-page,
.score-page,
.lobby-page
{
	background-image: none !important;
	
}

.game-page:after {
    content : "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
	background-size: cover;
    background-position: center;
    width: 100%;
    height: 100%;
    opacity : 0.75;
    z-index: -1;
    background-image: url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/53439ae7-dcc6-4d26-8d59-4dc736d4bb21/d138ngn-22158655-68d3-407f-a9da-72fbb1c79e58.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..kTnKXVAKXd_ap7Sv81wwgDpU5mjpGlWLPKJPQ0Wp8h0);
}

.score-page:after {
    content : "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
	background-size: cover;
    background-position: center;
    width: 100%;
    height: 100%;
    opacity : 0.75;
    z-index: -1;
    background-image: url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/113a1781-7c4e-426f-a5d6-0d62602c0b09/d14oq2j-a385f617-7b8c-47ab-bafc-1ca95c71e4fb.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..-QveqtlIX0smzgsZTHm3L7Vf05Wsh27cAQar6qK1ZF0);
}

.lobby-page:after
{
    content : "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
	background-size: cover;
    background-position: center;
    width: 100%;
    height: 100%;
    opacity : 0.75;
    z-index: -1;
    background-image: url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/i/42568f9d-e7f3-400c-9ae5-e5eeddc14b37/d300b2p-277e4f9d-e9a1-4227-abd9-569892f7cc09.jpg);
}



.side-bar {
    opacity : 0.5;
    width: 22% !important;
	/*background: transparent !important;*/
}
.side-bar:hover {
	z-index: 1000 !important;
    opacity : 0.75;
}

.game-log {
	/* width: 50% !important;	 */
	/* background: linear-gradient(to right, #8f0222 0%,#d3005f 37%,#8f0222 70%,#8f0222 70%,#6d0019 100%) !important; */
	right: 0 !important;
	left: auto !important;
	position: absolute !important;
}

.game-log:hover {
	/* width: 100% !important;	
	background: black !important; */
	z-index: 1000 !important;
}

.supply {
	left: 3% !important;
}

.opponent-vp-counter {
	display: none !important;
}

.opponent-name-counter-pane:hover {
 	z-index: 100;
}
.opponent-name-counter-pane {
    width: 100% !important;
}

.end-buys-button {
    width: 90% !important;
}

.button-label {
    font-size: 2vh !important;
}

.status-bar-border {
    font-size: 2vmin !important;
}

.supply .landscape .pile-vp-container {
 	width: 75% !important;   
}

.supply .landscape .card-text-vp-icon-container {
 	left: 50% !important;   
}

.mini-card .card-text-vp-icon-container {
 	left: 70% !important;   
}

.mini-card .card-text-vp-text {
	/* left: -30% !important;    */
    top: 13px !important
}



