css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(sueddeutsche.de)  {
  
  /* Zu verwenden in Kombination mit NoScript (sueddeutsche.de und sueddeutsche.com erlauben)*/
  
  /* Müll in der rechten Spalte */
  #sz-newspilot {
    display:none!important;
  }
  
  /* Hinweis auf neue Artikel (Overlay) */
  #updateShimebox, #updateLightbox {
    display:none!important;
  }
  
  /* Eigenwerbung */
  .wide-column {
    display:none!important;
  }
  
  /* Werbung */
  .ad, .adbox, #iqadtile1, #iqadtile2, #iqadtile3, #iqadtile4 {
    display:none!important;
  }
  
  /* Das Beste aus der zeitung */
  .interactiveeditorial {
    display:none!important;
  }
  
  /* Bilder Verschleierung */
  .iqadgalleryshim, .iqadgalleryoverlay, #iqadoverlay, #iqadcontainer {
    display:none!important;
  }

  /* Leser lesen aktuell */
  .entry-activevisits-1, .entry-activevisits-2, .entry-activevisits-3, .entry-activevisits-4 {
    display:none!important;
  }

  /* Teaser im Artikel */
  .teaserable-layout.teaserable-layout--teaser, .teaserable-layout.teaserable-layout--banderole, .flexible-teaser {
    display:none!important;
  }
  
  /* Teaser für SZ-Plus auf Startseite */
  .oneliner {
    display:none!important;
  }

  /* Newsletter bestellen */
  .basebox.embed, .basebox.include {
    display:none!important;
  }

  /* Teaser Verlagsangebote*/
  .teaser-list.teaser-list--bonus {
    display:none!important;
  }

}
