css:
@-moz-document domain("backlog.jp") {
.core-wrapper.-project .comment-editor.-fixed-footer {
    background-color: #666;
}
.comment-editor__change-statuses-properties label,
.comment-editor__change-statuses-properties a,
.comment-editor--full.-fixed-footer.is_comment-all-collapsed .comment-editor__expand-button .comment-editor__expand-button-text {
    color: #fff;
    font-weight: bold;
}
.comment-editor__expand-button-icon * {
    fill: #fff;
}
#project-header {
    background-color: #666;
    color: #fff;
    position: fixed;
    top: 50px;
    right: 0;
    height: 45px;
}
.ticket__date {
    background-color: #fff;
    position: fixed;
    top: 100px;
    right: 5px;
    z-index: 600;
    font-weight: bold;
}
.header-icon-set__name {
    color: #fff!important;
}
.content-main.-with-head-line-pager {
    padding-top: 49px;
}
#globalRecentIssueLink {
    font-weight: bold;
    color: #069;
    background-color: #fff;
    border-radius: 20px;
}
}
