css:
@-moz-document domain("kinox.to") {
html {
     background-image: url(https://media.giphy.com/media/26DOA12cT1GGixHeo/source.gif) !important;
    background-attachment: fixed !important;
    background-size: 100% 100% !important;
}

body {
    background: transparent !important;
}

* {
text-shadow: none !important;
color: #fff !important;}
#map-report div.header,
#map-report div.gmnoscreen {
    display: none;      
}
A:visited { color: #7c7c7c ! important 
}
a:hover {
  background-color:#ff0000;}

img[src="http://kinox.to/cs/themes/default/Header/Default.png"] {
    display: none; 
    }
    img[src="http://kinox.to/cs/themes/girly/Header/Default.png"] {
    display: none; 
}
    
    img[src="http://kinox.to/cs/themes/dark/Header/Default.png"] {
    display: none; 
}
#header {
    opacity: 0.8;
}

#dontbeevil,
.ModuleDefault,
.MirrorModule,
.CommentArea,
#CoverFlow>*,
.ModuleContent,
.beep {
    background-color: rgba(0, 0, 0, 0.2) !important;
    background-image: none !important;
    color: white !important;
}

.browse.right {
    background-image: none !important;
    width: 0px;
    height: 0px;
    -webkit-transform: rotate(360deg) !important;
    border-style: solid !important;
    border-width: 15px 0 15px 26.0px !important;
    border-color: transparent transparent transparent rgba(0, 0, 0, 0.4) !important;
    transition: 1s !important;
}

.browse.right:hover {
    border-color: transparent transparent transparent rgba(0, 0, 0, 0.7) !important;
}

.browse.left {
    background-image: none !important;
    width: 0px;
    height: 0px;
    -webkit-transform: rotate(360deg) !important;
    border-style: solid !important;
    border-width: 15px 26.0px 15px 0 !important;
    border-color: transparent rgba(0, 0, 0, 0.4) transparent transparent !important;
    transition: 1s !important;
}

.browse.left:hover {
    border-color: transparent rgba(0, 0, 0, 0.7) transparent transparent !important;
}

#navigation,
#mainmenu {
    background-image: none !important;
    border: none !important;
}

.BL,
.DropDown,
li {
    border: none !important;
}

.head.mHead,
.ModuleHead.mHead,
tr,
.top_navi,
.bottom_navi,
#navigation{
    background-image: none !important;
    background-color: rgba(0, 0, 0, 0.6) !important;
}

.display.SearchTable.SortMe.CommonTable,
.display.SearchTable {
    background: transparent !important;
}

.top_navi {
    background-image: none !important;
    background-color: rgba(0, 0, 0, 0.6) !important;
    text-decoration: none !important;
    text-shadow: none !important;
    color: white !important;
}

#mainmenu {
    background-color: rgba(0, 0, 0, 0.6) !important;
    color: white !important;
}

.sub>span,
.item>a {
    color: lightblue !important;
}

.Odd.parentalguidiance,
.ModuleAnnounceMent,
.MiniEntry {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
    color: white !important;
}

a,
legend {
    color: lightblue !important;
}

.Even.parentalguidiance,
.foot,
.Details.StyleA {
    background-color: rgba(0, 0, 0, 0.6) !important;
    background-image: none !important;
}

.OverlayLabel,
label {
    color: white !important;
}

.a4dbf {
    display: none !important;
}

#AjaxStream>a>img {
    height: 100px !important;
    background-color: black !important;
    opacity: 0.2 !important;
}

.first.paginate_button.paginate_button_disabled,
.previous.paginate_button.paginate_button_disabled,
.next.paginate_button.paginate_button_disabled,
.last.paginate_button.paginate_button_disabled,
#first,
#prev,
#next,
#last {
    background-color: rgba(0, 0, 0, 0.6) !important;
}

.LetterExtension {
    background-color: rgba(0, 0, 0, 0.4) !important;
}

.paginate_active,
.LetterMode.LetterMode_active,
.selected {
    background-color: rgba(255, 255, 255, 0.3) !important;
    color: black !important;
}

.paginate_button {
    background-color: rgba(0, 0, 0, 0.3) !important;
    transition: 1s !important;
}

.paginate_button:hover {
    background-color: rgba(0, 0, 0, 0.8) !important;
}

ul.PopupMenu {
    background: rgba(0, 0, 0, 0.7) !important;
    box-shadow: none !important;
}

tbody,
.entry_description {
    color: white !important;
}

#search,
.CommonModuleTable{
    background: rgba(255, 255, 255, 0.3) !important;
}

#Block_aed34b9f60ee115dfa7918b742336277,
.FullModuleTable.CommonTable{
    background: rgba(255,255,255,0.1) !important;
}

.Specialnews.parentalguidiance:hover{
    background: rgba(255,255,255,0.3) !important;
}
}
