css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("noco.tv") {

  /* FOND */
  #site {
    background-color: #212121; /* #282828; */
  }
  
  /* HEADER / Menu horizontal */
  #header {
    background: #212121;
  }
  
  /* Petite bande horizontale en haut, en dessous du menu */
  body {
    background-color: #212121;
  }
  
  /* TAGS */
  #tags-list {
    background-color: #3a3a3a; /* #474747; */
  }
  
  h1 {
    color: black;
  }
  
  #filtres {
    background-color: #3a3a3a;
  }
  
  #filtres h2 {
    color: black;
  }
  
  #filtres .tag.disabled {
    background: #595959;
  }
  
  #tags-list.fixed-tags-list {
    background-color: rgba(66, 66, 66, 0.9);
  }
  
  /* VIDEOS */
  
  .list .item-wrapper {
    background-color: #3a3a3a;
  }
  
  .content a {
    color: #b7b7b7;
  }
  
  /* Bordure du bloc video */
  .list .item-wrapper {
    box-shadow: 0px 0px 3px 0px #212121;
  }
  
  /* Tags de la video */
  .list .tag a {
    background: #2C2929;
  }
  
  /* Videos vues */
  .list .item .item-wrapper.mark_read {
    background-color: #3a3a3a;
  }
  
  /* PAGE VIDEO */
  /* Description */
  #description-programme {
    background: #3a3a3a;
    color: #ABABAB;
  }
  
  /* Tags */
  #description-programme .tag a, #download .tag a, #mes-notifications .notification .text .tag a {
    background-color: #595959;
    color: #ABABAB;
  }
  
    /* ----------------- */
  
  #abonnement .step {
    background-color: black;
  }
  
  #abonnement #partners .partner {
    background-color: #262525;
  }
  
  #products .product {
    background-color: #262525;
  }
  
  .modal-body {
    background-color: black;
    color: white;
  }
  
  .modal-footer {
    background-color: #262525;
  }
}
