css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plerb.com") {


body {
    font-size: 13px;
    line-height: 1;
    color: #6B418D;
    font-family: Arial,Helvetica,sans-serif;
}

a {
    background-color: transparent;
    color:#6B418D
}

.custom-background-1 {
    background: #17091A;
        background-color: rgb(23, 9, 26);
        background-image: none;
        background-repeat: repeat;
        background-attachment: scroll;
        background-clip: border-box;
        background-origin: padding-box;
        background-position-x: 0%;
        background-position-y: 0%;
        background-size: auto auto;
}

.custom-anchor-1, .custom-anchor-1:visited {
    color: #A68ABE !important;
}

.custom-anchor-2, .custom-anchor-2:visited {
    color: #A68ABE !important;
}
.custom-button-4 {
  color: #A68ABE;
  cursor: pointer;
}

.custom-form-2 input, .custom-form-2 select, .custom-form-2 textarea {
    border: 1px solid #aaa;
    padding: 8px 10px;
    border-radius: 4px;
    font-size: 13px;
    line-height: 1.42857143;
    color: #6B418D;
    width: 100%;
    box-sizing: border-box;
    margin: 0;
    max-width: 520px;
    background-color: #EBDFF6;
}

.custom-form-1 input[type="submit"], .custom-form-2 input[type="submit"] {
    background: #EBDFF6;
    line-height: 13px;
}

.custom-color-1 {
    color: #6B418D;
}
.custom-mobile-title-1 {
    background: #17091A;
    padding: 15px;
    border-bottom: 1px solid #e5e5e5;
    font-size: 15px;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}
    
#custom-footer {
    background: #17091A;
    padding: 15px;
    text-align: center;
    margin-top: 10px;
    border-radius: 4px;
    margin-bottom: 20px;
}
    
    .custom-border-bottom-1 {
    border-bottom: 0px solid #e1e8ed;
}
    
    }
