css:
@-moz-document domain("wikipedia.org") {
/*
 * Wikipedia Website Dark Theme
 *
 * (c) Fabien Crassat <fabien@crassat.com>
 *
 * Apply to URLs matching the following domain:
 *  wikipedia.org
 *
 * Tested only with Google Chrome
 *
 * Source code:  https://github.com/fabiencrassat/UserStyles/
 * Distribution: https://userstyles.org/styles/137798/
 * Licence:      https://creativecommons.org/publicdomain/zero/1.0/
 */

/**************/
/* HTML Style */
/**************/
html, body {
  background-color:#566068;
  background-image:none;
  color:#EEEEEE !important
}
a, a:visited {
  color:inherit !important
}
a:active {
    color: #FAA700 !important;
}
Input {
  background-color:#DDDDDD
}
ul {
  list-style-image:none;
  list-style-type:disc
}
table, table.wikitable, table.wikitable th {
  color:inherit;
  background-color:inherit
}
.etable a {
    color: #CCFF00 !important;
}
.etable td.empty {
    color: #3C3C3C !important;
}
code {
  color:#566068
}
h1, h2, h3, h4, h5, h6 {
  color:inherit !important
}
fieldset {
  border-color: #CCFF00
}
.selected, abbr, .mw-plusminus-pos {
  color:#CCFF00
}

/**********/
/* HEADER */
/**********/
#mw-page-base {
  background:none
}
#mw-head {
  background-color:inherit
}
#mw-head-base {
  height:1px;
  margin-top:0
}

/* Tab */
div.vectorTabs ul li{
  background-image:none;
  background-color:inherit
}
div.vectorTabs li.selected {
  background-image:none;
  background-color:#777777
}
div.vectorTabs span {
  color:inherit !important
}
/* Menu */
div.vectorMenu ul {
  background-color:inherit
}
#ca-validation {
  background-color:#566068 !important
}

/**********/
/* FOOTER */
/**********/
#footer {
  color:inherit
}
#footer ul li {
  color:inherit
}

/***********/
/* CONTENT */
/***********/
#content {
  background-color:inherit
}
#contentSub {
  color:inherit
}
.mw-headline {
  color:inherit !important
}
.mw-body {
  color:inherit !important
}
#bodyContent h2, #bodyContent h3, #bodyContent h4, #bodyContent h5, #bodyContent h6 {
  background-color:#777777;
  border-top:1px solid #AAAAAA;
  padding:6px 3px 3px
}
#bodyContent > h2 {
  margin:30px 0 0 -5px
}
#bodyContent > p a, #bodyContent > ul > li > span > a {
  color:#CCFF00 !important
}
#bodyContent > p a.new {
  color:#FFCC00 !important
}
.mw-editsection {
  float:right
}
#mw-content-text > p > a {
  color:#CCFF00 !important;
}

/* Thumb */
div.thumbinner {
    background-color:inherit;
}

/* Categories */
#mw-pages table {
  background-color:inherit;
  color:inherit
}
.catlinks {
  background-color:#777777
}

/* Toc */
#toc {
  font-weight:bold;
  background-color:#777777;
  color:inherit
}
.tocnumber {
    color:inherit;
}
.toclevel-2 , #bodyContent h3 {
  color:#CCFF00 !important
}
.toclevel-3 , #bodyContent h4  {
  color:#FFCC00 !important;
  font-weight:normal
}

/* Models */
.detail, .warning {
  background-color:#777777
}
.new {
  color:#FFCC00 !important
}
.autocomment {
  color:#AAAAAA;
}
div.VersionTemplate {
  color: #444444;
}

/* Diff */
table.diff {
  background-color:#777777
}
td.diff-otitle, td.diff-ntitle {
  background-color:inherit
}
td.diff-addedline, td.diff-deletedline {
  color:#566068
}
td.diff-addedline {
  background-color: #a3d3ff;
}
td.diff-deletedline {
  background-color: #ffe49c;
}
.mw-plusminus-neg {
  color:#FF6666;
}

/* Preference */
#preferences, #preferences legend, #preftoc li.selected a, #preferences td.htmlform-tip {
  background-color:inherit;
  background-image:none;
  color:inherit
}

/* History */
#pagehistory li {
  border:medium none
}
#pagehistory li.selected {
  background-color:#777777;
  border:medium none;
}

/***********/
/* SPECIAL */
/***********/

/* Search */
.mw-search-profile-tabs {
  background-color:inherit
}
fieldset#mw-searchoptions{
  background-color:inherit
}

/* Bandeau */
#bandeau-portail {
    background-color:#777777;
}
.bandeau-niveau-ebauche,
.bandeau-niveau-information,
.bandeau-niveau-modere
{
    background-color:inherit;
}

/* Nav Box */
.navbox,
.navbox-abovebelow,
.navbox-subgroup,
.navbox-subgroup .navbox-group
{
    background-color:inherit;
}
td.navbox-even,
th.navbox-group,
th.navbox-title
{
    background-color:#777777;
}

/* Accueil */
.accueil-droite {
    background-color:inherit;
}
.degrade {
    background-color:inherit !important;
}
}
