css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thedailywtf.com") {
    
    #navigation {
        background-color: #f8f8f8 !important;
        box-shadow: 0 8px 6px -6px #252525 !important;
    }

    a, h4, h2, .article-container .author .fakeLink, .article-container .comments .fakeLink, .discourse-link, .topic-map .avatars, .topic-map .links, .topic-map .information, .topic-map .map .number, .topic-map .map i, p > code, li > code, pre > code, .d-header, .extra-info-wrapper .topic-link, .navbar.dailywtf li > a, a.mention {
        color: #ccc;
    }
    
    a.comments {
        background-color: #252525 !important;
    }
    
    .container, body, #articlePage p, #articlePage .articleWrapper h1, pre code, pre .subst, pre .tag .title, pre .lisp .title, pre .clojure .built_in, pre .nginx .title, code[class*="language-"], pre[class*="language-"], .hljs, nav.post-controls button:hover, nav.post-controls .show-replies:hover span.badge-posts, aside.quote .title, .quote aside blockquote, .quote aside .onebox, .quote aside .onebox-result, .quote aside .quote, .quote aside .title {
        color: #aaa !important;
    }
    
    .quote aside .quote, .quote aside .title {
        color: #555;
    }
    
    .hljs-title {
        color: #eaab00;
    }
    
    .keyword, .hljs-keyword {
        color: #529fd5;
    }
    
    pre .number, pre .date, pre .regexp, pre .literal, pre .smalltalk .symbol, pre .smalltalk .char, pre .go .constant, pre .change, pre .markdown .bullet, pre .markdown .link_url {
        color: #7bd996;
    }
    
    pre .string, pre .title, pre .constant, pre .parent, pre .tag .value, pre .rules .value, pre .rules .value .number, pre .preprocessor, pre .ruby .symbol, pre .ruby .symbol .string, pre .aggregate, pre .template_tag, pre .django .variable, pre .smalltalk .class, pre .addition, pre .flow, pre .stream, pre .bash .variable, pre .apache .tag, pre .apache .cbracket, pre .tex .command, pre .tex .special, pre .erlang_repl .function_or_atom, pre .markdown .header, .token.property, .token.tag, .token.boolean, .token.number, .token.function-name, .token.constant, .token.symbol, .hljs-string {
        color: #fb8e70;
    }
    
    .side-bar-list > ul > li, div.about, .articleList li, pre code, :not(pre) > code[class*="language-"], pre[class*="language-"], .hljs, nav.post-controls .show-replies:hover {
        background-color: #222 !important;
    }
    
    #wrapper, .hideNonDesktop, .da-space, blockquote, .comment blockquote, .badge-notification.clicks, .topic-map, nav.post-controls .show-replies, p > code, li > code, pre > code, body code pre, body samp, .d-header, a.mention {
        background-color: #333;
    }
    .article-container, body, #article-page.content {
        background-color: #191919 !important;
    }
  
    .heading, #nav3, #articlePage .author, aside.quote .title, .quote aside .quote, .quote aside .title, .quote aside blockquote, .quote aside .onebox, .quote aside .onebox-result {
        background-color: #555 !important;    
    }
    
    .article-container, blockquote, .comment blockquote {
        border-color: #555;
    }
    
    .articleList li::after {
        background: linear-gradient(to right, rgba(0, 0, 0, 0) 0%, #222 100%) repeat scroll 0 0 rgba(0, 0, 0, 0)
    }
    
    .token.operator, .token.entity, .token.url, .token.variable {
        background: none repeat scroll 0 0 rgba(80, 80, 80, 0.5)
    }
    
    .discourse-link {
       background-color: #2d3868;    
    }
    
    #topic-progress h4 {
        color: #222 !important;
    }
    
    #wrapper .content-container .content #article-feed .article {
        box-shadow : 0 8px 6px -6px #404040;
    }
    #sidebar {
        background-color: #333 !important;
    }
    #wrapper #home-page.content #sidebar .sidebar-section {
        background-color: #252525 !important;
    }
    hr {
        border: 1px solid #555 !important;
    }
    #sidebar .sidebar-section label {
        border-bottom: #555 !important;
    }
    #sidebar article, .author-box {
        background-color: #252525 !important;
    }
    #sidebar p, #sidebar a, #sidebar span {
        color: #888;
    }
    #article-feed .article {
        background-color: #191919 !important;
        border-right: 10px solid #2c2c2c !important;
    }
    #article-feed .article.code:hover {
        border-right: 10px solid #c1103b !important;
    }
    #article-feed .article.soapbox:hover {
        border-right: 10px solid #95397a !important;
    }
    #article-feed .article.errord:hover {
        border-right: 10px solid #e4a838 !important;
    }
    #article-feed .article.featured:hover {
        border-right: 10px solid #2db8c2 !important;
    }
    #wrapper #top-banner.content img {
        background-color: #2c2c2c;
    }
    .archives-link {
        background-color: #252525;
        box-shadow: 0 8px 6px -6px #353535;
    }
    input#comment-name, textarea#comment-body, input.input {
        background-color: #444 !important;
        color: #ccc !important;
    }
}
