css:
@-moz-document regexp('https?://.*:8384/.*') {
  
  /* CUSTOM FONT */
  *:not(.fa) {
    font-family: Roboto !important;
  }
  
  
  /* TASTEFUL PADDING */
  .panel-heading, .panel-body {
    padding: 5px 10px !important;
  }
  .panel-footer {
    padding: 5px !important;
    border-top: 1px solid rgb(221, 221, 221) !important;
  }
  .btn-default .ng-scope {
    margin-left: 2px !important;
  }
  
  
  /* CHANGE TEXT COLORS */
  /* text */
  .navbar-top .ng-scope, .ng-binding {
    color: #666 !important;
  }
  h3.ng-scope {
    color: #bbb !important;
  }
  .panel-title-text, .panel-title-text span {
    color: #333 !important;
  }
  .panel-body .ng-scope {
    color: #333 !important;
  }
  .btn-default .ng-scope {
    color: #333 !important;
  }
  .navbar-fixed-bottom li * {
    color: #666 !important;
  }
  /* values */
  .panel-body td, .panel-body span {
    color: #333 !important;
  }
  
  
  /* CHANGE ICON COLORS */
  .navbar-top .fa {
    color: #bbb !important;
  }
  h4 .fa {
    color: #999 !important;
  }
  .panel-icon * {
    fill: #999 !important;
  }
  #content th .fa {
    color: #999 !important;
  }
  .btn-default .fa {
    color: #999 !important;
  }
  .navbar-fixed-bottom .fa {
    color: #bbb !important;
  }
  
  
  /* CHANGE BOX COLORS */
  body {
    background: #fff !important; /* #ffffff */
  }
  .navbar-top {
    background: #f8f8f8 !important; /* #f8f8f8 */
  }
  .navbar-fixed-bottom {
    background: #f8f8f8 !important; /* #f8f8f8 */
  }
  .panel-heading {
    background: #f8f8f8 !important; /* #f5f5f5 */
  }
  .panel-body {
    background: #fdfdfd !important; /* #ffffff */
  }
  .panel-footer {
    background: #f8f8f8 !important; /* #f5f5f5 */
  }
  .btn-default {
    background: none !important; /* #ffffff */
  }
  
  
  /* REMOVE BORDERS */
  .navbar-default, .btn-default {
    border: none !important;
  }
  /* .panel, .panel-heading, .panel-body, .panel-footer {
    border: none !important;
  } */
  
  
  /* REMOVE ALTERNATING ROW COLORS */
  tr {
    background: none !important;
  }
}
