css:
@-moz-document url-prefix(https://old.player.me/) {
/*CHANGES TO THE FEED*/
/*transparene, square containers*/
.theme--darker #page-feed .card, .theme--darker .card, .theme--darker .filter-bar, .theme--darker .new-post--container, .theme--darker .post--container {
    background: rgba(0, 0, 0, 0.6);
   border-radius: 0px;
    
      
   
}

.btn-primary{
    border: 0px;
background:  #EC1702;
    border-radius: 0%;
    color: #000000;
}
.theme--darker .post--container .comment--list {
    background: rgba(0, 0, 0, 0.7);
    border-radius: 0px;
   
}
.theme--darker .post--container .comment--list .comment--odd {
     background: rgba(0, 0, 0, 0.5);
    border-radius: 0px;
    
}
.theme--darker .post--container .comment--writer-container{
     background: rgba(0, 0, 0, 0.7);
    border-radius: 0%;
     
}
.theme--darker .post--container .post--content-checkin-container, .theme--darker .post--container .post--meta, .theme--darker .post--container .post--show-more-content{
     background: rgba(	236, 23, 2, 0.4);
    border-radius: 0px;
}
.theme--darker .feed-sidebar .stats{
    
    border-top: 0px;
}
.theme--darker .feed-sidebar .online-users li {
    border-top: 0px;
}
/*Shadowtext*/

.post--content {
    overflow: Hidden;
    Color: #cacaca;
    text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 1px 1px #000, -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000;
    }
 /*margins*/

div.post--container.post--source-playerme{
   border-top: 5px solid #EC1702;
    border-left: 5px solid #EC1702;
    border-radius: 0%;
    margin: 20px;
}

/*avatars*/
.theme--darker .post--container .post--info-avatar-image{
    border: 4px solid #EC1702;
    width: 70px;
    height: 70px;
    border-radius: 0%;
    position: relative;
    left: -47px;
    top: -2px;
    
    
}

.post--info-username{
    
    Font-size: 30px;
    text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 1px 1px #000, -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000;
    
   
}
.post--info-container{
     text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 1px 1px #000, -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000;
position:relative;
    left: -45px;
    top: -15px;
}


.post--source{
    position:relative;
    top: -5px;
}

.theme--darker .post--container .comment--avatar-image { border: 2px solid #EC1702;
width: 40px;
height: 40px;
border-radius: 0px;}


.feed-sidebar .profile .avatar{
    border: 3px solid #EC1702;
    border-radius: 0%;
}

/*PROFILE PAGE*/

.profile--section-count-container, .profile--section-players.group--is-pending .group--accept, .profile--section-players.group--is-pending .group--deny {
    border-radius: 0%;
    border: 0px;
    background:  #EC1702;
    
}
.profile--section-count, .profile--section-label, .profile--section-players.group--is-pending .group--accept, .profile--section-players.group--is-pending .group--deny{
    color: #000000;
}
.theme--darker .dropdown-menu, .theme--darker .mentions, .theme--darker .messaging--rename-container, .theme--darker .notifications--container, .theme--darker .select3-dropdown, .theme--darker .notifications--toolbar, .theme--darker .see-all{
     background: rgba(0, 0, 0, 0.7);
   border-radius: 0px;
    text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 1px 1px #000, -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000;
    border: 0px;
}

.theme--darker .messaging--header, .theme--darker .messaging--messages, .messaging--wrapper, .theme--darker .messaging--container{
    background: rgba(0, 0, 0, 0.7);
   border-radius: 0px;
    text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 1px 1px #000, -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000;
    border: 0px;
}
.theme--darker .messaging--messages .messaging--line .messaging--avatar, .top-bar--profile-avatar, .theme--darker .notification-image img{
    border: 2px solid #EC1702;
    border-radius: 0px;
}
.theme--darker .new-post--container .comment--writer.mirror, .theme--darker .new-post--container .comment--writer.real,.theme--darker .post--container .comment--writer-container .comment--writer.mirror, .theme--darker .post--container .comment--writer-container .comment--writer.real{
    background: rgba(5, 5, 5, 1);
    border-radius: 0%;
    border-color: #dbdbdb;
}
.feed-sidebar .profile .avatar {
    border-radius: 0%;
}
.feed-sidebar .online-users .avatar, .feed-sidebar .suggestion .avatar{
    border: 1px solid #EC1702;
    border-radius: 0%;
}
.theme--darker .top-bar--container .navbar--search-input, .theme--darker .messaging--input .message--writer.mirror, .theme--darker .messaging--input .message--writer.real, .add-friend-modal-container .popup-search, .messaging-scrollbar-container .messaging-page-actions-container .message-search-container .messaging-input-search{
    border: 0px;
    border-radius: 0%;
    background: rgba( 5, 5, 5, .5);
}
                                            
 .theme--darker .messaging--input{
              border-top: 1px solid #dbdbdb;
              background: rgba(15,15,15,0.8);
          }
.theme--darker .post--container .comments--show-all-container .comments--show-all, .theme--darker .messaging-page--toolbar-wrapper, .messaging-scrollbar-container .messaging-page-actions-container{
    border-bottom: 1px solid #dbdbdb;
    background: rgba(1,1,1,1);
    border-top: 1px solid #dbdbdb;
}
.theme--darker .top-bar--container{
    BACKGROUND: rgba(0,0,0,.8);
}
.theme--darker .messaging-scrollbar-container{
    background: #000000;
}
.theme--darker #page-user-notification .notification--filter{
    border-radius: 0px;
    
    background: #000000;
}
.badge-collection-container .badge-collection{
    border: 2px solid #EC1702;
    border-radius: 25%;
}
.profile--header-avatar-image{ 
     border: 5px solid #EC1702;
    border-radius: 0%;
    width: 150px;
    height: 150px;
}
.img-circle{
    border: 2px solid #ec1702;
    border-radius: 0%;
    
}
.tabbed--container{
    BACKGROUND: rgba(0,0,0,.7);
}
.theme--darker .qtip, .theme--darker .qtip .qtip-tip{
    border-radius: 0px;
    text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 1px 1px #000, -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000;
    background: rgba(0, 0, 0, 0.5);
}
}
