css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imgur.com") {
 
  #topbar {
    width: 100%;
    background-color: #14141E;
    font-size: 15px;
    height: 50px;
    z-index: 101;
    box-shadow: 0 0 11px 1px #14141E;
    position: relative;
    top: 0;
}
  body, html {
    background-color: #14141E;
}
  .posts {
    position: relative;
    padding: 16px;
    background-color: #14141E;
    box-shadow: 0 0 11px 1px #14141E;
    -webkit-box-shadow: 0 0 11px 1px #14141E;
    margin-bottom: 50px;
}
  .post, .post .hover {
    border: 2px solid #14141E;
}
  #past-preview img:hover, .post:hover {
    border-color: #14141E;
}
  #outside-tagging-showhide {
    width: 30px;
    overflow: hidden;
    background-color: #191926;
    top: 20px;
    right: -32px;
    position: absolute;
    padding-left: 3px;
    -webkit-border-radius: 0 5px 5px 0
}
  #top-comments #outside-tagging {
    width: 385px;
    border-radius: 10px;
    -webkit-border-radius: 10px;
      ;
    -webkit-box-shadow: 0 0 11px 1px #14141E;
}
  #outside-tagging {
    background-color: #14141E;
    width: 200px;
    border-radius: 0 10px 10px 0;
    -webkit-border-radius: 0 10px 10px 0;
}
  .post .post-info {
    background-color: #14141E;
    padding: 7%;
    position: absolute;
    width: 87.5%;
    bottom: 0;
    left: 0;
    color: #FFFFFF;
    border-top: 1px solid #14141E;
}
  .post .hover {
    display: none;
    padding: 7%;
    width: 120%;
    z-index: 2;
    background-color: #14141E;
    top: 101%;
    margin-left: -19%;
    border-radius: 4px;
    -webkit-border-radius: 4px;
    -webkit-box-shadow: 0 2px 5px #000;
    box-shadow: 0 2px 5px #000;
    text-align: left;
}
  .post-header {
    box-sizing: border-box;
    width: 728px;
    padding: 20px;
    background: #14141E;
    border-radius: 3px 3px 0 0;
    box-shadow: 0 4px 4px -2px rgba(0,0,0,.2);
    position: relative;
}
  .post-image-meta {
    padding: 20px 20px 35px;
    background: #14141E;
}
  .btn-action {
    background: #14141E;
    border: none;
}
  .btn-action:hover {
    background: #14141E;
}
  .navPrev {
    background-color: #14141E;
    margin-right: 5px;
    padding-top: 11px;
}
  .navPrev:hover {
    background-color: #14141E;
}
  .post-action-border {
    border-top: 2px solid #222033;
    padding-top: 20px;
}
  .post-action {
    padding: 20px;
    margin-top: -1px;
    min-height: 45px;
    background-color: #14141E;
}
  .post-image {
    text-align: center;
    background: #14141E;
    position: relative;
}
  .aqua {
    color: #43D177 !important;
}
  .post-image-container--spacer {
    padding-bottom: 20px;
    background: #14141E;
}
  form.caption-create {
    background: #14141E;
    position: relative;
    padding: 15px;
    border: 2px solid #14141E;
    border-radius: 4px;
    cursor: text;
}
  .js-post-truncated {
    padding: 20px 20px 0;
    background: #14141E;
    text-align: center;
    font-size: 12px;
    position: relative;
}
  .social-icon.icon-f {
    background-color: #0000;
}
  .social-icon.icon-twttr {
    background-color: #0000;
}   
    .social-icon.icon-pin {
    background-color: #0000;
}
  .social-icon.icon-reddit {
    background-color: #0000;
}
  .comments-info {
   display:none !important;
}
  .comment[data-level="0"] {
margin-left: -12px;
display: none;
visibility: hidden !important;
}
.bad-captions {
display: none;
visibility: hidden !important;
}
  .core-dark {
    background: #14141E;
}
  input[type="text"], input[type="password"], input[type="email"], textarea {
    font-family: inherit;
    border: 1px solid transparent;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.1);
    background-color: #14141E;
    outline: 0;
    box-sizing: border-box;
    padding: 5px 10px;
    font-size: .9em;
    line-height: 1.5em;
    resize: none;
    color: #f2f2f2;
    margin-bottom: 10px;
    letter-spacing: .02em;
}
  .signin.facebook:hover {
    background-color: #14141E;
}
  .signin.facebook {
    border-bottom-color: #14141E;
    font-size: 21px;
}
  .signin.twitter:hover {
    background-color: #14141E;
}
  .signin.twitter {
    border-bottom-color: #14141E;
}
  .signin.google:hover {
    background-color: #14141E;
}
  .signin.google {
    border-bottom-color: #14141E;
}
  .signin.yahoo:hover {
    background-color: #14141E;
}
  .signin.yahoo {
    border-bottom-color: #14141E;
}
  .lvl1-dark {
    background: #14141E;
}
  .forgot-password {
    position: absolute;
    top: 0;
    right: 0;
    display: inline-block;
    padding: 9px 10px;
    cursor: pointer;
    border-radius: 0 5px 5px 0;
    background: #14141E;
}
  #content, .panel {
    background-color: #14141E;
}
  .textbox {
    background-color: #14141E;
    padding: 10px;
}
  .album .cover img, .album .info, .album .metadata, .privacy-dot-container {
    background-color: #14141E;
}
  .profile.access {
    outline: #14141E dashed 1px;
    cursor: pointer;
    border-radius: 0;
}
.privacy-dot.red {
    background-color: #b22246;
}
  .privacy-dot.orange {
    background-color: #FFC600;
}
  .panel .thumbs img {
    margin: 3px;
    width: 135px;
    height: 135px;
    border: 3px solid #14141E;
}
}
