css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com") {
  /* TEXT */

  .content .link .title a.title,
  .thing.link.stickied .title a.title
  {
    color:#CECECE!important;
  }

  .content .link .title a.title:visited
  {
    color:#989898!important;
  }



  /* BACKGROUND */

  body
  {
    background-color:#323232!important;
  }

  .content .link
  {
    background-color:#323232!important;
  }



  /* SIDE */

  .side
  {
    background-color:#2A2A2A !important;
  }



  /* LINKS POST */

  .comment .flat-list.buttons a,
  .content .flat-list.buttons a
  {
    color:#B7B7B7!important;
  }
  
  .expando-button {  
    border: 1px solid #5c5c5c;
    border-radius: 3px;
  }

  
  
  /* ADDITIONS */

  /* Opacity of links */
  .listing-page:not(.moderator) .link .buttons .first ~ li {
    opacity:0.5;
  }
  
/*
  .content {
    margin-left:200px;
  }
*/
  
  /* Search field */
  #search {
    background-color: #c2c2c2;
  }
  #search input[type="text"] {
    color: black!important;
  }
  
  /* Info advanced search */
  .infobar {
    background-color: #6c6c6c;
  }
}
