css:
@-moz-document url-prefix("http://www.wakfu.com") {
.container .ak-main-page {
    background: none repeat scroll 0 0 rgba(10,10,10,0.4);
}

/*-- FORUM --*/

h1 {
    color: #bbb;
    background: #303030 url(http://staticns.ankama.com/g/modules/masterpage/common/wakfu/h1.png) left top no-repeat;
    border-bottom-color: #3b7c7e;
    text-shadow: 0 2px 2px rgba(0,0,0,0.1);
}

.ak-panel {
    background: none;
    color: #999;
}

.ak-table-thread .ak-table tr.ak-not-read {
    background: #3B3B3B;
}

.ak-table tr td {
    border-bottom-color: #303030;
    color: #362712;
}

.ak-table tr:first-child td {
    border-top: 1px solid #3B3B3B;
}

.ak-table-thread .ak-table tr th {
    background: #303030;
    color: #bbb;
}

.ak-table-thread .ak-table tr th:first-child a {
    color: #bbb;
}

.ak-table-thread .ak-table tr td .ak-title-thread a {
    color: #40b2b5;
}

/*---  ---*/

aside .ak-panel .ak-panel-title {
    background: #303030;
    text-shadow: none;
}

aside .ak-panel .ak-panel-content .ak-form {
    border: 1px solid #3B3B3B;
}

.ak-panel>.ak-panel-title {
    background: #303030 url(http://staticns.ankama.com/g/modules/components/panel/wakfu/simple-title.png) left top no-repeat;
    background-size: 60px auto;
    color: #bbb;
    border-bottom-color: #303030;
}

aside .ak-form {
    background-color: #3B3B3B;
}

.ak-tree ul li.level-1 {
    background: #3B3B3B;
}

.ak-tree.ak-tree-forum li.level-2 {
    background: #424242;
    border-bottom: 1px solid #3B3B3B;
}

.ak-tree ul li.level-1:hover {
    background: #3B3B3B;
}

.ak-tree ul li.level-1:first-child {
    border-bottom: 1px solid #303030;
}

.ak-tree ul li.level-1 a {
    color: #bbb;
}

.ak-tree ul li.level-1:hover{
    background: red;
}

.ak-tree.ak-tree-forum li.level-1 a:hover {
    background: #353535;
}

aside .ak-aside-search .ak-form input[type="text"] {
    background: #303030;
    color: #bbb;
    border: 1px solid #303030;
}

.ak-thread .ak-table tr td .ak-form .form-group select, .ak-followed .ak-table tr td .ak-form .form-group select {
    background: #303030;
    color: #bbb;
    border-color: #303030;
}

aside .ak-form form select.form-control {
    background: #323232;
    color: #bbb;
    border-color: #303030;
}

.ak-table-thread .ak-table tr td .form-group select {
    background: #323232;
    color: #bbb;
    border-color: #303030;
}

.ak-thread .ak-table tr th, .ak-followed .ak-table tr th {
    background: #303030;
    color: #bbb;
}

.ak-thread .ak-table tr th:first-child {
    color: #bbb;
}

/*----*/

.ak-thread .ak-table tr:first-child {
    border-bottom: 1px solid #303030;
    background: #3B3B3B;
}

.ak-thread .ak-table tr:first-child:hover {
    border-bottom: 1px solid #303030;
    background: #3B3B3B;
}

.ak-thread .ak-table tr.ak-not-read, .ak-followed .ak-table tr.ak-not-read {
    background: #3B3B3B;
}

.ak-thread .ak-table tr.ak-not-read:hover, .ak-followed .ak-table tr.ak-not-read:hover {
    background: #3B3B3B;
}

.ak-table tr {
    border-color: #303030;
    color: #bbb;
}

.ak-table tr td {
    border-bottom-color: #303030;
    color: #40b2b5;
}

.ak-table tr td a {
    color: #40b2b5;
}

.ak-thread .ak-table tr.ak-not-read td .ak-text-topic .ak-title-topic, .ak-followed .ak-table tr.ak-not-read td .ak-text-topic .ak-title-topic {
    color: #088da5;
}

.ak-thread .ak-table tr td, .ak-followed .ak-table tr td {
    border-color: #303030;
}

/*--topic--*/

.ak-main-container .ak-admin-text {
    background: #3B3B3B;
    color: #bbb;
}

.ak-title-topic.ak-title-container h1 {
    background: #303030;
}

.ak-title-topic.ak-title-container h1 .ak-table-cell .ak-text {
    color: #bbb;
}

.ak-title-container h1 .ak-subtitle a {
    color: #40b2b5;
}

.ak-title-topic.ak-title-container h1 .ak-table-cell .ak-text .ak-subtitle {
    color: #999;
}

.ak-panel.ak-first-post .ak-action-button {
    border-top: 1px solid #303030;
}

/*--reactions--*/

.ak-posts-list.ak-nocontentpadding .ak-panel-title, .ak-panel-form-post .ak-panel-title {
    background: #303030;
    color: #bbb;
}

.ak-panel .ak-panel-header-list-post {
    background: #3B3B3B;
    border-bottom: none;
}

@media (min-width: 768px)
    {
    .ak-posts-list.ak-nocontentpadding .ak-post .col:first-child {
        background: #3B3B3B;
	}
}

.ak-posts-list.ak-nocontentpadding .ak-post {
    border-bottom: 2px solid #3B3B3B;
    border-right: none;
}

.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child {
    background: #404040;
}

.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-content-post .ak-date .ak-date-post {
    color: #40b2b5;
}

.ak-panel-content .ak-panel.ak-ankama-speech>.ak-panel-title {
    color: #bbb;
    background: #303030;
}

.ak-form select, .ak-form select.form-control {
    background: #303030;
    color: #bbb;
    border-color: #303030;
}

.ak-pagination ul li.disabled>a, .ak-pagination ul li.disabled>span {
    background-color: #282828;
    color: #bbb;
    border-color: #282828;
}

.ak-pagination ul li>a, .ak-pagination ul li>span {
    background-color: #303030;
    border: 1px solid #282828;
    color: #bbb;
}

.ak-pagination ul li.disabled>a:hover, .ak-pagination ul li.disabled>span:hover {
    background-color: #282828;
    color: #bbb;
    border-color: #282828;
}

.ak-pagination ul li>a:hover, .ak-pagination ul li>span:hover {
    background-color: #282828;
    border: 1px solid #282828;
    color: #bbb;
}

.ak-pagination ul>li:first-child>a {
    border-left: 1px solid #282828;
}

.ak-nb-answers {
    background: #40b2b5;
    color: #fff;
}

/*--remember--*/

.ak-admin-text .ak-block-remember {
    border: none;
    background: #303030;
}

/*--quotes--*/

.ak-posts-list.ak-nocontentpadding .ak-post .quote {
    background: #383838;
    border: 1px solid #303030;
    color: #606060;
}

@media (min-width: 768px){
    .ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-content-post .ak-text {
        padding-right: 20px;
        padding-left: 20px;
    }
}

.ak-posts-list.ak-nocontentpadding .ak-comments-list {
    background: #3B3B3B;
}

.ak-main-container .ak-admin-text a {
    margin-left: 20px;
}

.ak-main-content .ak-action-button .ak-post-response {
    margin-left: 20px;
}

.ak-main-content .ak-action-button .ak-post-response:hover {
    background: url(http://staticns.ankama.com/forums_ng/modules/assets/wakfu/response.png) 8px 11px no-repeat transparent;
    color: #40b2b5;
}

/*--ui connection--*/

.ak-modal-wrapper .ui-dialog-titlebar {
    background: #303030;
    color: #bbb;
}

.ak-modal-wrapper {
    box-shadow: 0 0 5px 0 #000000;
    background: #3B3B3B;
}

.ak-login-page {
    background: #3B3B3B;
}

@media (min-width: 768px){
	.ak-login-page .ak-login-account {
    	border-right: 1px solid #303030;
	}
}

.ak-panel-stack .ak-panel, .ak-nocontentpadding .ak-panel {
    background: #3B3B3B;
}

.ak-login-page .ak-login-account .control-label {
    color: #40b2b5;
}

.ak-form-topic-post .ak-panel-content .ak-bbcode-editor-forum {
    border: 1px solid #303030;
    background: #3B3B3B;
    color: #555;
}

.btn-default {
    background: #303030;
    border-color: #303030;
    color: #BBB;
}

.ak-aside-problems{
    background: #3B3B3B;
}

aside .ak-aside-problems ul li, aside .ak-aside-problems ul li a {
    color: #BBB;
}

.ak-tree.ak-tree-forum li.level-2 li.level-3 {
    background: #484848;
    border-top: 1px solid #3B3B3B;
}

.ak-thread .ak-table tr td .ak-dropdown-actions, .ak-followed .ak-table tr td .ak-dropdown-actions {
    background-color: #3B3B3B;
    border-color: #3B3B3B;
}

.ak-thread .ak-table tr td .ak-dropdown-actions:hover, .ak-followed .ak-table tr td .ak-dropdown-actions:hover {
    background-color: #3B3B3B;
    border-color: #3B3B3B;
}

table .dropdown-menu {
    background: #303030;
}

/*--side widget--*/

.ak-listtiny ul li a .ak-image {
    border: none;
}

}

@-moz-document url-prefix("http://www.wakfu.com/fr/mmorpg"), url-prefix("http://www.wakfu.com/fr/mmorpg/actualites/news/") {
/**/
/**/
/*----news----*/
/**/
/**/

.ak-item-list .ak-item-elt-title {
    background: #303030;
}

.ak-item-list .ak-item-elt-title a {
    color: #40b2b5;
}

.ak-item-list .ak-item-elt-inner {
    background: #3B3B3B;
    color: #bbb;
}

.ak-item-list .ak-ellipsis-text {
    background: linear-gradient(to bottom, rgba(248,248,248,0) 0%, #303030 80%, #303030 100%);
}

.ak-item-list .ak-bottom {
    border-top: 1px solid #303030;
    background: #303030;
}

.ak-item-list .ak-item-elt-desc strong {
    color: #40b2b5;
}

.ak-block-shop .ak-shop-article-container {
    background-color: #3b3b3b;
}

.ak-block-shop .ak-shop-article-title a {
    color: #40b2b5;
}

.ak-listtiny ul li {
    color: #bbb;
    border-bottom-color: #303030;
    background-color: #3B3B3B;
}

.ak-listtiny ul li:hover {
    background-color: #3F3F3F;
}

.ak-listtiny ul li .ak-content .ak-title {
    color: #BBB;
}

.ak-panel .ak-panel-bottomlink .ak-bottom-link {
    border-top: 1px solid #303030;
    color: #40b2b5;
    background: #303030;
}

.ak-admin-text .ak-item-nav {
    border-top-color: #303030;
    color: #cfcfcf;
    background: #303030;
}

.ak-panel-stack.ak-glue>div.ak-container+div.ak-container {
    border-top: 1px solid;
    border-top-color: #303030;
}

.ak-panel-stack.ak-glue>div.ak-container+div.ak-container:before {
    border-top-color: #282828;
}

.ak-social {
    background: url(http://staticns.ankama.com/g/modules/components/social/wakfu/bg-title.png) 0 15px no-repeat #303030;
    border-color: #303030;
}

.ak-forum-post-panel.ak-panel#ak-block-posts .ak-panel-title {
    background: #303030;
}

.ak-forum-post-panel.ak-panel#ak-block-posts {
    background: #303030;
}

.ak-panel-stack .ak-panel>.ak-panel-title{
    color: #40b2b5;
}

.ak-forum-post-panel.ak-panel#ak-block-posts .ak-panel-title .ak-legend {
    background: #2B2B2B;
    border-color: #2B2B2B;
}

.ak-forum-post-panel.ak-panel#ak-block-posts .ak-panel-content {
    background: #3B3B3B;
    color: #BBB;
}

.ak-forum-post-list .ak-comments-row {
    border-top: 1px solid #303030;
}

.ak-forum-post-list .ak-subpost .ak-see-more-comments a {
    color: #3fa0a3;
    text-decoration: none;
    background: #353535;
    border-top: 1px solid #353535;
}

.ak-forum-post-list .ak-subpost {
    border-left: 2px solid #353535;
}

.ak-forum-post-form {
    border-top: 1px solid #303030;
    background: #303030;
}

.ak-forum-post-form .ak-comment-container {
    background: #303030;
}

.ak-forum-post-form .ak-comment textarea {
    color: #BBB;
    background: #3B3B3B;
    border-color: #2B2B2B;
}

.btn-primary, .btn-blue, .btn-info {
    color: #404040;
}

.ak-list-options {
    background: #3B3B3B;
}

.ak-title-container h1 {
    border-bottom: 1px solid #303030;
}

.ak-list-options .btn-group.open .dropdown-toggle, .ak-list-options .ak-displaycount .ak-triggeraction {
    background: #303030;
    border-color: #303030;
    color: #BBB;
}

/*--filter--*/

@media (min-width: 992px){
    .ak-filters .ak-title-filters {
        background: #303030 url(http://staticns.ankama.com/g/modules/masterpage/common/wakfu/h1.png) left top no-repeat;
        background-size: 60px auto;
        border-bottom-color: #303030;
        color: #40b2b5;
    }
}

.ak-filters .ak-filter-selection {
    background: #353535;
    color: #BBB;
    clear: both;
    border-bottom: 1px solid #353535;
}

.ak-list-filters-inner {
    background: #3B3B3B;
    border-bottom: 1px solid #303030;
}

.ak-list-filters-check li {
    background: #3B3B3B;
    border-bottom: 1px solid #353535;
    color: #BBB;
}

.ak-list-filters-check li:hover {
    background: #383838;
    border-bottom-color: #353535;
    color: #40B2B5;
}

/**/

.ak-list-filters-check li.ak-selected {
    background: #383838;
    color: #40B2B5;
}

.ak-list-filters-active {
    background: #3B3B3B;
}

.ak-list-filters-active li {
    border-bottom: 1px solid #353535;
    color: #BBB;
}
}

@-moz-document url-prefix("http://www.wakfu.com/fr/mmorpg/actualites/maj") {
/*--maj list--*/

.ak-item-list .ak-item-elt-content {
    background: #303030;
}

.ak-item-list .ak-highlight .ak-highlight-title span {
    color: #BBB;
}

.ak-panel {
    background: #3B3B3B;
    color: #BBB;
}

.ak-content-list .ak-list-element {
    color: #BBB;
    border-bottom: 1px solid #303030;
}

.ak-content-list .ak-list-element .ak-title a {
    color: #BBB;
}

.ak-item-list .ak-item-mini .ak-item-elt-title .ak-text a, .ak-item-list .ak-item-mini .ak-item-elt-title .ak-text .ak-publication {
    color: #BBB;
}
}

@-moz-document url-prefix("http://www.wakfu.com/fr/mmorpg/actualites/maj/") {
/*--maj spec--*/

.ak-page-menu {
    background: #3B3B3B;
}

select {
    color: #bbb;
    background-color: #303030;
    border-color: #303030;
}

.ak-admin-text .ak-block-remember {
    background: #353535;
    border-color: #353535;
}

.ak-page-menu a {
    color: #c1caca;
}


.ak-admin-text .ak-item-mid .ak-item-top-nav .ak-item-top-nav-toggle {
    background-color: #303030;
    color: #BBB;
}

.ak-admin-text .ak-item-mid .ak-item-top-nav {
    border-bottom-color: #303030;
}

.ak-admin-text .ak-item-mid .ak-item-top-nav .ak-item-top-nav-toggle:hover {
    background-color: #303030;
}

.ak-admin-text .ak-item-top-nav {
    border-bottom: 1px solid;
}

}

@-moz-document url-prefix("http://www.wakfu.com/fr/mmorpg/encyclopedie") {
/**/
/**/
/*--encyclopédie--*/
/**/
/**/

.ak-title-container h1 {
    border-bottom: 1px solid #303030;
}

.ak-content-list .ak-list-element {
    color: #BBB;
    border-bottom: 1px solid #303030;
}

.ak-content-list .ak-list-element .ak-title a {
    color: #BBB;
}

.ak-panel-stack .ak-panel>.ak-panel-title {
    color: #40b2b5;
    border-bottom: 2px solid #303030;
    padding-bottom: 10px;
}

.ak-content-list{
    background: #353535;
}

.ak-table tr.ak-bg-odd{
    background: #353535;
}

.ak-table tr.ak-bg-even{
    background: #333333;
}

.ak-table tr.ak-bg-odd:hover, .ak-table tr.ak-bg-even:hover{
    background: #373737;
}

.ak-table tr th {
    background: #303030;
    color: #BBB;
}

.ak-table tr th a {
    color: #BBB;
}

.ak-tabs {
    background: #282828;
}

.nav-tabs {
    border-bottom: 1px solid #303030;
}

.ak-tabs li a {
    background: #3fa0a3;
    border-color: #3fa0a3;
    /*color: #3fa0a3;*/
    color: #303030;
}

.ak-tabs li.active a {
    background: #303030;
    border-color: #303030;
    color: #40b2b5;
}

.ak-pagination select {
    background-color: #303030;
    border: 1px solid #303030;
    color: #bbb;
}

/*--filtrer--*/

.ak-filters .ak-title-filters {
    background: #f29c06;
    border-bottom-color: #f29c06;
    color: #303030;
}

.ak-filters .ak-title-filters, .ak-filters-clone .ak-title {
    background: #f29c06;
    border-bottom: 1px solid #f29c06;
    color: #303030;
}

.ak-button-click {
    background: #303030;
}

.ak-filters .ak-filter-selection {
    background: #303030;
    color: #BBB;
    border-bottom: 1px solid #282828;
}

.ak-list-filters-inner {
    background: #3B3B3B;
    border-bottom: 1px solid #282828;
}

input {
    -webkit-appearance: textfield;
    background-color: #505050;
    -webkit-rtl-ordering: logical;
    user-select: text;
    cursor: auto;
    padding: 1px;
    border-width: 2px;
    border-style: none;
    border-color: #303030;
    border-image: initial;
    color: #BBB;
}



.ak-list-filters-check li {
    background: #353535;
    border-bottom: 1px dashed #303030;
    color: #BBB;
}

.ak-filters select {
    border: 1px solid #303030;
    color: #BBB;
    background: #303030;
}

.ak-filters select:hover {
    border: 1px solid #303030;
    color: #BBB;
    background: #303030;
}

.ak-list-filters-check li:hover {
    background: #353535;
    border-bottom: 1px solid #303030;
    color: #BBB;
}

.ak-list-filters-check li.ak-inline-li:hover {
    border-bottom: none;
    background: #353535;
    color: #BBB;
}

.ak-list-options {
    background: #3B3B3B;
    color: #BBB;
}

.ak-list-options .ak-list-info {
    color: #CCC;
}

.ak-list-options .btn-group.open .dropdown-toggle, .ak-list-options .ak-displaycount .ak-triggeraction {
    background: #303030;
    border-color: #303030;
    color: #BBB;
}

.ak-responsivetable td.item-level {
    color: #838179;
}

.ak-encyclo-block-info, .ak-object-rarity {
    background: #3B3B3B;
    border-color: #303030;
    color: #CCC;
}

.ak-panel .ak-panel-content {
    color: #BBB;
}

.ak-content-list.ak-displaymode-image-col .ak-list-element .ak-main .ak-main-content {
    background: #353535;
    border: 1px solid #303030;
}

.ak-content-list {
    background: #3B3B3B;
}
}

@-moz-document url-prefix("http://www.wakfu.com/fr/mmorpg/encyclopedie") {
.qtip.ak-linker-tooltip .ak-tooltip-linker {
    background: #303030;
    box-shadow: 0 0 5px #000000;
}

.qtip.ak-linker-tooltip .ak-tooltip-linker .ak-top {
    background: #303030;
    border-color: #303030;
}

.qtip.ak-linker-tooltip .ak-tooltip-linker .nav-tabs li.active a {
    color: #303030;
    background: #40B2B5;
    border-color: #40B2B5;
    border-bottom-color: transparent;
}

.qtip.ak-linker-tooltip .ak-tabs-container .tab-content {
    border-left: 2px solid #40B2B5;
    border-right: 2px solid #40B2B5;
    border-bottom: 2px solid #40B2B5;
    border-top: 2px solid #40B2B5;
}

.ak-encyclo-detail-illu {
    border-top-color: #202020;
    border-right-color: #202020;
    border-bottom-color: #202020;
    border-left-color: #202020;
    border: 4px solid;
}
}

@-moz-document url-prefix("http://www.wakfu.com/fr/mmorpg/encyclopedie/monstres") {
/**/
/**/
/*--bestiaire--*/
/**/
/**/

.ak-list-options {
    background: #3B3B3B;
    color: #BBB;
}

.ak-list-options .ak-list-info {
    color: #CCC;
}

.ak-list-options .btn-group.open .dropdown-toggle, .ak-list-options .ak-displaycount .ak-triggeraction {
    background: #303030;
    border-color: #303030;
    color: #BBB;
}

.ak-filters {
    background: none;
}

.ak-filters-move-target .ak-filters {
