css:
@-moz-document domain("my.ivytech.edu") {
.aui .navbar-fixed-top,
.aui .navbar-static-top {
    background: #333333;
}
.aui .navbar .nav>li>a {
    float: none;
    padding: 10px 15px 10px;
    color: #555;
    text-decoration: none;
    text-shadow: 0 0px 0 #1b1b1b;
}
.aui body.skin-ivytech .nav > li > a {
    color: #8dc63f;
}
.aui .navbar-form-custom input[name="_3_keywords"] {
    margin: 0;
    background: transparent;
    box-shadow: none;
    color: #8dc63f;
}
.aui body.skin-ivytech {
    background: #333333;
}
.aui body.skin-ivytech .btn-primary {
    background-color: #8bc540;
    color: #ffffff;
    border-color: #8bc440;
}
.aui body.skin-ivytech .nav-header {
    background: #333333;
}
.aui body.skin-ivytech #page-wrapper {
    background: #333333 !important;
}
.aui a {
    color: #ffffff;
    text-decoration: none;
}
.aui body {
    color: #8dc63f;
}
.aui .panel-body {
    padding: 15px;
    background-color: #333333;
}
.aui body.skin-ivytech.mini-navbar .nav-header {
    background-color: #333333 !important;
}
.aui body.mini-navbar .nav-header {
    padding: 40px 0px 5px 12px !important;
}
.aui .portlet-borderless-container,
.aui .portlet.portlet-container {
    background-color: transparent;
}
.footable-odd {
    background-color: #545454;
}
.footable > thead > tr > th,
.footable > thead > tr > td {
    background-color: #dce9f9;
    background-image: linear-gradient(to bottom, #333333, #333333);
}
.portlet-luminis-announcements table.user_view_announcement_table thead tr th,
table.user_view_announcement_table tfoot tr th {
    background-color: #333333;
    color: #8bc53f;
}
.footable > thead > tr > th,
.footable > thead > tr > td {
    text-shadow: 0 0px 0 rgba(255, 255, 255, 0.5);
}
.footable > tbody > tr:hover {
    background: #777777;
}
.portlet-luminis-announcements .footable {
    color: #fff;
}
}
