css:
@-moz-document domain("gaiaonline.com") {
.hero-main {
	position: relative;
	margin: 0 auto 0;
	width: 950px;
	height: 420px;
	background: url(http://oi65.tinypic.com/nfi43d.jpg) no-repeat;
}
.hero-main-messaging {
	position: absolute;
	right: 38px;
	top: 28px;
	color: orange;
	font: 15px/1.3 arial, sans-serif;
	width: 290px;
	height: 360px;
	border-radius: 6px;
	-moz-border-radius: 6px;
	-webkit-border-radius: 6px;
	background: #3e2851;
	background-image: linear-gradient(bottom, #000 35%, #3e2851 65%);
	background-image: -o-linear-gradient(bottom, #000 35%, #3e2851 65%);
	background-image: -moz-linear-gradient(bottom, #000 35%, #3e2851 65%);
	background-image: -webkit-linear-gradient(bottom, #000 35%, #3e2851 65%);
	background-image: -ms-linear-gradient(bottom, #000 35%, #3e2851 65%);
	background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0.35, #000), color-stop(0.65, #3e2851));
}
}
