css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://online.sberbank.ru/CSAFront/index.do") {
 
  html, body{
    height: 100%!important;
    width: 100%!important;
    overflow: hidden!important;
    background: #101010!important;
  }
  
   /*Login*/

.b-bg .bg_slide.css3transition{
    display: none;
  }
  #Backgrounds, #Slides{
    display: none;
  }
  
/*auth*/
  .b-auth{
    background: #101010;
    border: 1px solid #15a063;
    width: 350px;
  } 
  
  .g-side{
    margin: 10% auto!important;
    float: none!important;
  }
  
  .b-field .field_select, .b-field .field_input{
    color:#f5f5f5;
  }
  
/*Soveti*/
  .b-secure
  {
    display: none;
  }
  
  /*Reg*/
  .b-reg .reg_bg{
    background: none!important;
  }
  .b-field .field_inner{
    background: #333;
  }
  
  .b-reg .reg_link{
    color: #333!important;
  }
  
  .b-reg ._link{
    color: #333!important;
  }
  
/*auth*/
  .auth_body{
    width: 350px;
  }
}
