css:
@-moz-document domain("trello.com") {

/*
.list-wrapper {
    width: 24.375%;
    margin: 0 0.3125%;
}

 @media (min-width: 1280px) {
 .list-wrapper {
    width: 20%;
    margin: 0;
 }
}
*/

.list-wrapper {
    width: 300px;
}
  
.list-card-title {
    font-weight: bold;
    font-size: 13px;
}

.list-card-labels .card-label {
	opacity: .8;
    height: 2px;
}

#trellists .show-all,
#trellists .hide-all {
	color: #ccc;
}

.badge.is-due-now,
.badge.is-due-now .badge-icon {
    background-color: transparent;
    color: #CF513D;
}

.badge.is-due-past,
.badge.is-due-past .badge-icon {
    background-color: transparent;
    color: #EC9488;
}

.badge {
    padding: 0 2px;
}

.badges {
    margin-left: -4px;
    margin-top: -2px;
    padding-bottom: 0;
}

.badge-text {
    font-size: 11px;
}

.badge .icon-sm {
    font-size: 10px;
    width: 14px;
}

.member {
    border-radius: 50%;
}

.list-card-members .member {
    margin-bottom: 2px;
    margin-top: -4px;
    display: inline-block;
    max-width: inherit;
}

#board .member,
#board .member-avatar {
    width: 18px;
    height: 18px;
}

.member-initials {
    height: 18px;
    line-height: 18px;
    position: relative;
}

/* no scrolling lists */

body {
    font-family: -apple-system, 'BlinkMacSystemFont', Roboto, Helvetica, Arial, sans-serif;
    font-weight: 300;
    -webkit-font-smoothing: subpixel-antialiased;
}

.list {
    max-height: inherit;
}

#board {
    overflow-y: auto;
}

/* bright style */

body,
.body-board-view #header {
    background: #fff !important;
    /*box-shadow: 0 2px 8px 0 rgba(0,0,0,0.11);*/
}

.board-header,
#board,
.list {
    background: #E7ECEE;
}

#board {
    padding-bottom: 0;
    margin-bottom: 0;
}

.header-btn,
.board-header-btn {
    color: #333;
    opacity: .8;
}

.board-header-btn {
    font-size: 12px;
}

.header-btn:hover {
    color: inherit;
    opacity: 1;
}

.board-header-btn-icon,
.icon-lg.light {
    color: rgba(0,0,0,.4);
}

.list-card {
    /*border: 1px solid #F1F2F4;*/
    border: none;
    border-radius: 6px;
    box-shadow: 0 1px 1px 0 rgba(0,0,0,0.10);
    margin-bottom: 10px;
}

.list-card-title {
    font-weight: 500;
    letter-spacing: 0.2px;
    font-size: 12px;
    margin-top: 4px;
    margin-bottom: 4px;
}

.list-card-details {
    padding: 8px 8px 2px 12px;
}

.list-header-num-cards,
.open-card-composer {
    font-size: 11px;
}

#trellists {
	border: 1px solid #eaeaea;
    background: none;
}

#trellists .show-list {
    color: #333;
}

#board::-webkit-scrollbar { 
	height: 3px;
}

.card-detail-item-block {
    background: #fff;
    padding: 9px 11px;
    box-shadow: 0 1px 1px 0 rgba(0,0,0,0.10);
    border-radius: 6px;
}

textarea.mod-list-name {
    text-transform: uppercase;
    font-size: 11px;
    letter-spacing: 0.05em;
}

.editing .edit .field, .editing .edit .field:active {
    background: none;
    border: transparent;
    box-shadow: none;
}

/* Extension: List Sperator */

.list-separator {
    box-shadow: none !important;
    width: inherit !important;
    margin: inherit !important;
}

/* Extension: List Highlighter */

.bmko_header-card-applied {
    box-shadow: none !important;
}

.bmko_normal-list, 
#classic-body.bmko_do-not-dim-lists .bmko_untagged-list,
#classic-body.bmko_do-not-dim-lists .bmko_low-list {
    background: none !important;
}

}
