css:
@-moz-document domain("7cups.com") {
	/* creator:  @joe2017 */
	/* Version: 1.0.1 */
	/* changes our background colour */
	body {
		background: linear-gradient(to right, #f2f2f2 0%, #e6e6e6 100%) !important;
	}
	#MessageScrollDiv {}
	.feedInfo {
		color: #fcbdd2;
	}
	#related-community-holder {
		display: none;
	}
	/* changes our chat / room button hover */
	a.list-group-item:hover {
		background-color: #ffe0ea;
		transition: 0s;
	}
	/* alters PR button styling */
	.btn-warning {
		border-color: white;
		background-color: white;
		color: pink;
	}
	/* alters PR button styling */
	.btn-warning {
		border-color: white;
		background-color: white;
		color: pink;
	}
	/*changes our nav drop down text color */
	.dropdown-item {
		color: white;
	}
	/*changes our nav drop down styling */
	.dropdown-menu {
		background-color: #fcbdd2;
	}
	/* changes members u/n colour in group rooms....Note that Guest usernames are a different shade. */
	.chatBox .details a {
		color: #ff6699;
	}
	/* changes heavyload message / GR bar note: 10 GRs +*/
	#bottomStatus.heavyLoad {
		background: linear-gradient(to right, #ff751a 10%, #ff3377 90%) !important;
	}
	/* Changes our send button colour */
	#sendButton {
		background-color: #ff6699;
	}
	/* changes our 1-1 / group support link text colour*/
	.nav-tabs.chat-tabs .nav-link.active {
		border-bottom: solid .0625rem #ffb3cc;
		color: #ffb3cc;
	}
	/*changes our full screen background colour*/
	.chatRow.expanded {
		background: linear-gradient(to right, #f2f2f2 0%, #e6e6e6 100%) !important;
	}
	.note {
		color: #ffb3cc;
	}
	/*changes our "group discussion" button colour*/
	.btn-warning {
		color: #ffb3cc;
		background-color: transparent;
		border-color: #ffb3cc;
	}
	/* changes our Link colour*/
	a {
		color: blue;
	}
	a:hover {
		color: #EB95E2;
		transition: 1s;
	}
	.chatBox .card-title {
		color: #ffb3cc;
	}
	/* button appearance */
	.btn-simple.btn-warning {
		color: #ec971f;
		border: solid .0625rem #ffb3cc;
	}
	/* changes our alert bubble colour */
	.alertCounter {
		background-color: #ffb3cc
	}
	.navbar-primary-rev ul.navbar-nav .compassionJar {
		background-color: #F36695;
	}
	/* warning button appearance */
	.btn-simple.btn-warning {
		color: #ffb3cc;
	}
	btn-warning {
		color: #fcfcfc;
		background-color: #ff6699;
		border-color: #ff6699;
	}
	/* changes our group support / 1 on 1 tab colours */
	.chat-tabs li a {
		color: #ffb3cc;
	}
	/* 
ssages / conversation styling */
	#bottomStatus .btn-default {
		background-color: #fff;
		border: solid .0625rem #fff;
		color: #ff6699
	}
	#bottomStatus {
		background: linear-gradient(to right, #ffb3cc 0%, #ff6699 100%);
	}
	/*changes our nav gradiant */
	.gradient-happy,
	.navbar-primary-rev.navbar-fixed-top {
		background: linear-gradient(to right, #ffb3cc 0%, #ff6699 100%);
	}
	/* your message bubble colour*/
	.chatBox .meWrap {
		background-color: #ffb3cc;
	}
	/* others message styling*/
	.chatBox .youWrap {
		Background-color: #ff6699;
		color: black;
		border-top-left-radius: .5em;
		border-bottom-left-radius: .0em;
		position: relative;
		top: .2em;
	}
	/* Changes member image back to circle*/
	.img-fluid,
	.figure>img,
	.carousel-inner>.carousel-item>img,
	.carousel-inner>.carousel-item>a>img {
		border-radius: 10em;
	}
	/* compassion heart */
	.compassionHeartFade {
		color: #ff6699;
	}
	/* button styling */
	.btn-info {
		Background-color: #ffb3cc;
		border-color: #ffb3cc;
	}
	/*sets hover effect for above button */
	.btn-info:hover {
		Background-color: #ff6699;
		border-color: #ff6699;
		transition: 1s;
	}
	/* sets certain button styling */
	.btn-primary {
		Background-color: #ffb3cc;
		border-color: #ffb3cc;
	}
	/*sets hover effect for above button */
	.btn-primary:hover {
		Background-color: #ff6699;
		border-color: #ff6699;
		transition: 1s;
	}
	.btn-success {
		background-color: #ffb3cc;
		border-color: #ffb3cc;
	}
	.btn-success:hover {
		Background-color: #ff6699;
		border-color: #ff6699;
		transition: 1s;
	}
	.btn-simple.btn-primary {
		color: white;
		border: #ffb3cc;
	}
	.btn-simple.btn-primary:hover {
		Background-color: #ff6699;
		border-color: #ff6699;
		transition: 1s;
	}
	/* chat time/message time etc */
	.chatBox .details {
		color: #ffb3cc;
	}
	.fa-star {
		color: #ff6699;
	}
	.fa-check {
		color: #ff6699;
	}
	.fa-check:before {
		color: #ff6699;
		font-size: 17px;
		padding-left: 50px;
	}
	.listenerData .overall,
	.fa-star,
	.fa-star-half-o,
	.fa-star-o {
		color: #ff6699;
	}
	.pointer {
		color: #ff6699;
	}
	.listenerData .overall {
		color: #ff6699;
	}
	.badge-listener {
		background-color: #fcbdd2;
	}
	.gradient-footer,
	footer {
		background: linear-gradient(to left, #ffb3cc 0%, #ff6699 100%);
		;
	}
	footer a {
		color: white;
	}
}

@-moz-document url-prefix("https://www.7cups.com/listener/connect") {
	h3 {
		color: #ff6699;
	}
	td {
		color: #ff6699;
	}
	.btn-warning {
		color: #fff;
		background-color: transparent;
		border-color: #fff !important;
	}
}
