@media screen {
    p, a {
        font-family: -apple-system, BlinkMacSystemFont, Roboto, "Helvetica Neue", "Apple LiGothic Medium", "Microsoft YaHei", Sans-Serif !important;
        line-height: 1.5em !important;
        font-weight: 400 !important;
    }
    strong {
        font-weight: 700 !important;
    }
    [class*="code"] {
        font-family: 'Roboto Mono', "Apple LiGothic Medium", "Microsoft YaHei", monospace !important;
        font-weight: 400 !important;
        line-height: 1.0em !important;
        padding: 2px 4px;
        border-radius: 0px;
        font-size: 0.8rem !important;
    }
    h1, h1 code {
        font-size: 30px !important;
    }
    h2, h2 code {
        font-size: 22px !important;
    }
    code {
        font-family: 'Roboto Mono', "Apple LiGothic Medium", "Microsoft YaHei", monospace !important;
        font-weight: 400 !important;
        line-height: 1.0em !important;
        padding: 2px 4px;
        border-radius: 0px;
        font-size: 0.8rem !important;
    }
    p code {
        font-family: 'Roboto Mono', "Apple LiGothic Medium", "Microsoft YaHei", monospace !important;
        font-weight: 400 !important;
        line-height: 1.0em !important;
        color: #00695C !important;
        border: none !important;
        background: rgba(236, 239, 241, 0.56) !important;
        padding: 2px 4px;
        border-radius: 0px;
        font-size: 0.8rem !important;
    }
    a,
    input,
    li,
    ol,
    p,
    ul {
        font-size: 16px !important;
    }
    ol.linenums li, span.lit {
        font-family: 'Roboto Mono', "Apple LiGothic Medium", "Microsoft YaHei", monospace !important;
        font-weight: 400 !important;
        line-height: 1.0em !important;
        font-size: 0.8rem !important;
    }
}
@media print {
