css:
@-moz-document domain("store.steampowered.com"), domain("steamcommunity.com") {
#global_header {
    position: fixed;
    left: 0;
    top: 0;
    z-index: 900;
    width: 100%;
}
.responsive_store_nav_ctn_spacer {
    z-index: 20;
}
#es_wallet {
    color: green;
}
.takeover_mini {
    display: none;
}
.home_actions_ctn {
    display: none;
}
.es_progress_wrap {
    display: none;
}
.content {
    z-index: 1;
}
.store_nav {
    z-index: 10;
}
.store_nav_bg {
    z-index: 10;
}
.store_header_btn {
    z-index: 0;
}
body {
    margin-top: 120px;
    z-index: 0;
    background-image: none;
}
}
