css:
@-moz-document domain("www.yify-movies.net") {
html,
body,
body.thumbnail,
div[class="background-container"] {
    background-color: #000 !important;
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg") #000000 fixed repeat !important;
}
div[class="logo"],
div[class="rblock"] h3 {
	display: none!important;
}
div[class="header"],
div[class="headertop_desc"],
div[class="content"],
div[class="footer"],
div[class="section group"] {
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652799/styles/trans_back.png")!important;
    background-repeat: repeat-y !important;
    color: #fff !important;
}
h3,
div[class="content_top"],
div[class="content_bottom"],
div[class="header_bottom_left"],
div[class="nav_list"],
div[class="account_desc"]{
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/cellpic3.gif") !important;
    line-height: 40px;
    background-repeat: repeat-x;
    background-color: #ccc !important;
    border: none !important;
}
div[class="product_desc"] {
    background-color: #ccc !important;
}
div[class="search_box"] {
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652799/styles/trans_back.png")!important;
}
h2, div[class="desc span_3_of_2"]{
    background-color: #ccc !important;
    color: #000 !important;
    border: none !important;
}
p {
    color: #000 !important;
}
a:link,
a:visited {
    color: #ccc !important;
    background-color: #000 !important;
    /* padding: 8 !important; */
}
div[id="reviews"],
div[class="product_desc"] {
    padding: 15px;
}
}
