css:
@-moz-document url-prefix("http://php.net/manual/"), url-prefix("https://secure.php.net/manual/") {
/* font style for main article */

#layout-content {
    font-family: 'Yu Mincho', "YuMincho", "georgia", serif;
    font-weight: bolder;
    line-height: 1.25em;
    letter-spacing: 1px;
}
/* end of font style*/
/* you can change header's color */

nav#head-nav {
    background-color: #3333ff;
}
/* example:

#head-nav {
  background-color: red;
}

#head-nav {
  background-color: #d7b740;
}



*/
/* end of the header's color */

.navbar {
    border-style: none;
}
#head-nav {
    height: 55px;
    display: block;
    margin: 0px;
    z-index: 10001;
}
ul.nav li {
    list-style: none;
    margin: 0px;
}
ul.nav li a {
    color: white;
    text-shadow: none;
}
ul.nav li a:hover {
    color: white;
}
ul.nav li.active a {
    color: white !important;
    text-shadow: none !important;
    background-color: black !important;
    box-shadow: none !important;
}
#top-search {
    height: 50px;
    margin: 0px;
    padding: 0px;
}
div.headsup {
    background-color: #ffffff;
    padding-top: 10px;
    padding-bottom: 2rem;
    margin: 0px;
    top: 0px;
    position: relative;
    display: block;
    border: none;
}
div.headsup a {
    color: #3333ff;
    font-family: "meiryo", "arial", sans-serif;
    font-weight: bolder;
}
#layout-content {
    background-color: #fff !important;
    position: relative;
    padding-top: 50px;
    top: 0px;
}
#layout-content:not(:only-child) {
    border-right: 0px solid black;
}
#layout-content .page-tools + .sect1 {
    padding-top: 1em;
}
.info {
    padding-bottom: 1em;
}
h1,
h2,
h3 {
    font-family: "meiryo", "arial", sans-serif;
}
.example-contents {
    font-weight: normal;
}
#breadcrumbs a {
    font-family: "meiryo", "arial", sans-serif;
}
#breadcrumbs a:link {
    color: #3333ff;
}
#breadcrumbs a:hover {
    color: #9999ff;
    text-decoration: underline;
}
#breadcrumbs a:visited {
    color: #999999;
}
.layout-menu {
    opacity: 0.6;
}
.layout-menu:hover {
    opacity: 1;
}
.parent-menu-list > li {
    list-style: none;
}
.parent-menu-list > li > a {
    margin-bottom: 1rem !important;
    font-weight: bolder;
}
.parent-menu-list li ul a {
    border-style: none !important;
}
.parent-menu-list a {
    font-family: "meiryo", "arial", sans-serif;
}
.parent-menu-list a:link {
    color: #000000;
}
.parent-menu-list a:hover {
    color: #999999;
    text-decoration: underline;
}
.parent-menu-list a:visited {
    color: #999999;
}
.parent-menu-list .child-menu-list li {
    padding-bottom: 0.8em;
}
.parent-menu-list .child-menu-list li a {
    border-style: none;
}
header.title,
h1:after,
h2:after,
h3:after {
    border-style: none !important;
    display: block;
}
body {
    background-color: white;
}
pre {
    line-height: 1em;
}
li {
    list-style-type: disc;
}
ul.chunklist_book li,
ul.chunklist_set li,
#layout-content ol li {
    margin-top: 1em;
}
ul.chunklist_children,
ul.itemizedlist li {
    padding-top: 0.3em;
}
ul.chunklist_children li {
    margin-top: 0.2em;
    list-style-type: none;
}
.title,
h1 {
    color: #3333FF;
}
h2 {
    color: #6666FF;
}
h1,
h2,
h3 {
    margin-top: 2.5em;
    margin-bottom: 1em;
}
h1,
h2 {
    font-weight: normal;
}
#layout-content a {
    text-decoration: none;
    border-style: none;
}
#layout-content a:link {
    color: #3333ff;
}
#layout-content a:hover {
    color: #9999FF;
}
#layout-content a:visited {
    color: #c3c;
}
#layout-content strong {
    font-family: sans-serif;
    font-weight: bold !important;
}
div.text {
    opacity: 1 !important;
}
.parameter {
    color: #3bb!important;
    font-weight: normal!important;
}
#usernotes {
    margin-top: 6em;
}
#usernotes h3 {
    padding-top: 0em;
}
.footmenu li a:link {
    color: #999999;
}
.footmenu li a:hover {
    color: #999999;
    text-decoration: underline;
}
.footmenu li a:visited {
    color: #cccccc;
}
/*  adjutifying the position of feedly popup */

#feedly-mini,
#feedly-mini-popup,
#feedly-mini-popup-arrow {
    margin-bottom: 44px !important;
    margin-right: 4px !important;
}
}

@-moz-document regexp("https?://(secure\\.)?php.net/manual/[a-zA-Z^/]{1,6}/resource.php") {
/* closing and opening aside menu for some pages for fine layout */

.layout-menu:after {
    content: "▼　Open Menu list";
    display: block;
    color: black;
}
.layout-menu {
    background-color: white !important;
    opacity: 1 !important;
    display: block;
    height: 320px;
    position: relative;
    z-index: 10000;
}
.layout-menu ul::-webkit-scrollbar {
    background: white;
}
.layout-menu ul::-webkit-scrollbar-thumb {
    background: #666;
}
.layout-menu > ul {
    display: none;
}
.layout-menu:hover > ul {
    display: block;
    overflow-y: scroll;
    height: 300px;
}
.layout-menu:hover:after {
    display: none;
}
}
