css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.islamnews.ru/namaz/") {
  body{
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif!important;
    background: #101010!important;
  }
  
  /*content*/
  .table-content__table{
    background: #333;
    border: 1px solid #000;
  }
  
  /*color*/
  .table-content__table tr{
    color:#fff!important;
    text-shadow:none!important;
  }
  
  .table-content__table{
    box-shadow: none!important;
  }
  
  .table-content__content td{
    color:#fff!important;
  }
  
  .table-content__content tr.Friday{
    background: #6698cb!important;
    color:#111!important;
    font-style: bold!important;
  }
  
  .table-content__content tr.last-day td{
    color:#fff!important;
  }
  
  .table-content__content thead{
    background: #fff!important;
  }
  
  .contentArea table tr:nth-child(2n+1) {
    background: #111;
}
  
  .table-title{
    background: none!important;
    text-shadow: none!important;
  }
  
  .todayVal{
    font-size: 1em!important;
  }
  /*Disable box shadow*/
  .selecter-selected, .table-title label{
    text-shadow: none!important;
  }
  
  .home .frontWidget.dynamicLeft{
    box-shadow: none!important;
  }
  
  .title{
    background: none!important;
    font-style: normal!important;
    font-size: 1.2em!important;
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif!important;
    text-transform: uppercase!important;
    text-shadow: none!important;
    height: 100%!important;
  }

.home .frontWidget.dynamicLeft{
  background: none!important;
}
}
