body {
    font-family: 'Source Sans Pro', sans-serif;
    background: #e7e7e7;
}
.scol-entete {
    min-height: 120px;
    overflow-y: auto;
    background: url(http://blog.hdwallsource.com/wp-content/uploads/2016/01/school-wallpaper-25052-25735-hd-wallpapers.jpg);
    background-size: cover;
    background-color: rgba(0, 0, 0, 0.5);
    background-blend-mode: darken;
    border-style: solid;
    border-width: 0 0 4px 0;
    border-color: #fefefe;
}
.menu-principal {
    z-index: 10;
    height: 50px;
    clear: both;
    position: relative;
    width: 100%;
    padding: 0 1em;
    -moz-box-shadow: 0 1px 2px #babcbc;
    border: none;
}
.menu-principal .niv1 li {
    float: left;
    position: relative;
    height: 50px;
    padding: 0;
    margin-bottom: 0;
    border-style: solid;
    border-color: #f7f7f7;
    border-width: 0 1px 0 0;
}
.menu-principal .niv1 li.avec-niv2 a {
    background-repeat: no-repeat;
    background-position: center right;
    background-position-y: 18px;
    padding-right: 26px;
}
.menu-principal .niv1 li.avec-niv2 a {
    background-repeat: no-repeat;
    background-position: center right;
    background-position-y: 18px;
    padding-right: 26px;
}
.menu-principal.pos-fixe-page {
    background-color: #fff;
    background-image: none;
    background-repeat: repeat-x;
    background-position: 0 100%;
}
.menu-principal .niv1 a {
    padding: 15px 18px 15px 18px;
    border: solid #f1f1f1;
    border-width: 0 1px;
}
.bt-deconnexion {
    color: #ea4040!important;
    background: none!important;
    position: absolute;
    top: 6px;
    right: 1em;
    padding: 7px;
    border: #ea4040 1px solid;
    border-radius: 7px;
    transition: all 0.2s ease;
}
.bt-deconnexion:hover {
    color: #fff;
    background: #ea4040;
}
.scol-entete .identification .selectionne {
    background: rgba(255, 255, 255, 0.15);
    border-width: 1px 1px 0 1px;
    border-color: rgba(255, 255, 255, 0);
    border-style: solid;
}
.scol-entete .identification li a {
    font-weight: bold;
    color: #fff;
    text-decoration: none;
    line-height: 1.20em;
}
.scol-entete .identification li {
    float: left;
    margin: 30px 4em 0 .33em;
    padding: 2px .66em 0 .66em;
    text-align: center;
    height: 42px;
}
.scol-entete h1 {
    float: left;
    margin-left: 4em;
    margin-top: 35px;
}
.menu-principal .niv1 li:first-child {
    border-width: 0 1px;
    border-left: none;
    margin-left: 5em;
}
.bt-deconnexion {
    color: #ea4040!important;
    background: none!important;
    position: absolute;
    top: 6px;
    right: 1em;
    padding: 7px;
    border: #ea4040 1px solid;
    border-radius: 7px;
    transition: all 0.2s ease;
    margin-right: 4em;
}
.pieddepage {
    background: #fff;
    border-style: solid;
    border-color: #ffffff;
    border-width: 1px 0 0 0;
}
.pos-ligne {
    overflow: auto;
    margin-left: 6em;
    margin-right: 6em;
}
.corps {
    clear: both;
    padding: 4px 8px 34px 8px;
    margin-top: 30px;
}
.pos-volet-container {
    padding: .66em 8px;
    background: #fafafa;
    border: 1px solid #ffffff;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}
.pos-central {
    float: left;
    transition-property: all;
    transition-duration: .0s;
    transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
    background: #fff;
    padding: 20px;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
    border-radius: 3px;
}
.pos-volet-btn-fermeture {
    display: block;
    background: #ff5e5e;
    width: 100%;
}
.ui-datepicker .ui-datepicker-header {
    position: relative;
    padding: .2em 0;
    background: #ffffff;
    color: #5a5a5a;
}
.ui-datepicker .ui-datepicker-title {
    margin: 0 2.3em;
    line-height: 1.8em;
    text-align: center;
    font-size: 1.2em!important;
    text-shadow: none;
}
.ui-datepicker.ui-widget-content {
    border-color: #fafafa;
}
.ui-datepicker .ui-datepicker-header {
    position: relative;
    padding: .2em 0;
    background: #fafafa;
    color: #5a5a5a;
}
.ui-widget-header {
    border: none;
    background: #abbde6;
    color: #fff;
    font-weight: bold;
}
h1,
h2,
h3,
h4,
h5,
h6,
p,
span {
    font-family: 'Source Sans Pro', sans-serif;
}
h2 {
    margin-bottom: .33em;
    margin-left: 1em;
    color: #5E78B5;
    font-size: 1.33em;
    font-family: 'Source Sans Pro', sans-serif;
}
.menu-principal .niv1 li:first-child {
    border-width: 0 1px;
    border-left: none;
    margin-left: 5em;
}
.corps {
    clear: both;
    padding: 4px 8px 34px 8px;
    margin-top: 30px;
    background: #fff;
    padding: 20px;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
    margin-left: 6em;
    margin-right: 6em;
}
.tdata .tdata-entete {
    background: none;
    border-bottom: 3px solid #a7a7a7;
    font-weight: bold;
}
.tdata td,
.tdata th {
    border-style: solid;
    border: none;
    border-width: 0 1px 1px 1px;
    height: 50px;
    vertical-align: middle;
}
.tdata th {
    border-width: 1px 0 0 0;
    padding: 3px 0 0 0;
    background: #ffffff;
}
.tdata .tdata-lgn-alt {
    background: #f7f7f7;
}
.cel-moyenne img {}
