.tdata th {
    border-style: solid;
    border: none;
    border-width: 0 1px 1px 1px;
    height: 50px;
    vertical-align: middle;
    text-align: center;
}
.abouton {
    display: inline-block;
    height: 40px;
    padding: 10px 20px;
    margin-right: .66em;
    cursor: pointer;
    font-weight: bold;
    vertical-align: top;
    background: #40a2ea;
    color: #ffffff;
    border: none;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}
.abouton:hover {
    background: #40a2ea;
    color: #ffffff;
}
.abouton .icone {
    display: none;
}
select {
    padding: 5px;
    height: 40px;
    background: #ffffff;
    cursor: pointer;
    font-weight: bold;
    max-width: 440px;
    border: #e7e7e7 solid 2px;
    border-radius: 2px;
}
.tdata .tdata-entete {
    background: none;
    border-bottom: 3px solid #a7a7a7;
    border-top: none;
    font-weight: bold;
    border-left: none;
    border-right: none;
}
.acc-entete {
    overflow: auto;
    padding: 2em 2em 0 2em;
    background: url(http://blog.hdwallsource.com/wp-content/uploads/2016/01/school-wallpaper-25052-25735-hd-wallpapers.jpg);
    background-size: cover;
    background-color: rgba(0, 0, 0, 0.5);
    border-bottom: none;
    background-blend-mode: darken;
}
#accueil h1 {
    margin-bottom: 2em;
    margin-left: 20px;
}
#accueil h2 {
    font-size: 2em;
    font-family: 'Source Sans Pro', sans-serif;
    line-height: 1em;
    border: none;
}
#accueil h3 {
    font-size: 1em;
    margin-left: 25px;
}
.acc-entete .presentation ul {
    font-weight: bold;
    margin-top: 3px;
    margin-left: 2em;
    width: 100%;
}
.acc-entete a {
    font-size: 0.9em;
    color: #FFFFFF;
    text-shadow: 0px 1px 0px #4d4d4d;
    text-decoration: none;
    text-transform: capitalize;
}
.acc-entete a {
    font-size: 0.9em;
    color: #e4e4e4;
    text-shadow: 0px 1px 0px #4d4d4d;
    text-decoration: none;
    text-transform: capitalize;
    line-height: 0;
}

.acc-corps {
    float: left;
    padding: 0 2em 2em 2em;
    display: none;
}
.abouton-jaune, .ui-state-default.ui-button.abouton-jaune {
    font-family: 'Source Sans Pro', sans-serif;
	background: none;
    border: none;
    color: #fff !important;
}
}
