css:
@-moz-document domain("planfix.ru"), domain("planfix.com"), domain("planfix.ua") {
/*
ПланФикс - Комментарий, яркая форма (by Kirr)
2018-12-06
*/


.task-card-wrapper > #action-create-div {
    border: #e0e0e0 solid 3px;
    border-radius: 7px;
    margin-right: 40px;
    padding-left: 7px;
    padding-right: 7px;
    background-color: #FEFED9;
}


/* для цветного заголовка */
.b-notified-list-v2 .b-notified-list-v2-srings {
    border-top: none;
    margin-left: -8px;
    margin-right: -8px;
    border-radius: 5px 5px 0px 0px;
    /* overflow: hidden; 2018-12-06 */
}    

.b-notified-list-v2 .b-notified-list-v2-srings > div {
    padding-left: 8px;
    padding-right: 8px;
}

/* это для отступов внутри окна редактирования, чтобы отодвинуть текст от жёлтых бордюров */

#action-create-div #cke_ActionDescription > .cke_inner > .cke_contents {
    padding-left: 10px;
    padding-right: 10px;
}
/* убираем серую черту под формой — она не нужна, т.к. есть рамка */

.task-card-wrapper > #action-create-div > #newActionForm {
    padding-bottom: 0;
    border-bottom: none;
}

.actions-quick-add-block > .actions-quick-add-target {
    border: #e0e0e0 solid 3px !important;
    border-radius: 7px;
    background-color: #FEFED9;
    width: 352px !important;    
}

tr.tr-actions-body-edit-form td.td-actions-wrapper {
    border-bottom: #e0e0e0 solid 1px;
    padding-left: 10px !important;
    padding-right: 10px !important;
    padding-top: 12px !important;
    background-color: #FEFED9 !important;
}

tr.tr-actions-body-edit-form td.td-actions-wrapper #cke_ActionDescription > .cke_inner > .cke_contents {
    padding-left: 10px;
    padding-right: 10px;
}
}
