@-moz-document url-prefix("https://stackedit.io/") {
/* ============ Scroll Ajust Styles ============ */
/* setting here: font-size,line-height,padding,margin white-space,word-wrap,text-align */

/* HTML Contents */
h1, h2, h3, h4, h5, h6 {
/* 	Style of head lines are together under. */
	margin-top: 1.2em;
	margin-bottom: 1.0em;
	line-height: 1.25em !important;
	text-align: start;
}

/* h1+p, h2+p, h3+p, h4+p, h5+p, h6+p {
	margin-top: 1.2em;
}
h1+ul, h2+ul, h3+ul, h4+ul, h5+ul, h6+ul {
	margin-top: 1.0em;
}
h1+ol, h2+ol, h3+ol, h4+ol, h5+ol, h6+ol {
	margin-top: 1.0em;
}
 */
/* 	Style of head lines are together under. */

p {
/* 	margin: 0 0 1em; */
	margin-bottom: 1.5em;
}


li > p {
	margin: 0 0;
}

li {
	line-height: 1.5em;
}

ul, ol {
	padding-left: 1.5em;
	margin-bottom: 1.5em;
}

ul ul, ol ul, ul ol, ol ol {
	margin-bottom: 1.5em;
}

pre code, pre.prettyprint code {
	font-size: 0.95em;
  text-align: start;
	line-height: 1.4em;
/*   padding: 0.5em 1.5em; */
	padding: 0.05em 0.4em;
/*   padding: 0em 0em; */
/* 	margin-top: 0.8em; */
	margin-bottom: 1em;
}

pre.prettyprint {
  text-align: start;
  border: 0;
  padding: 0em 0em;
/* 	margin: 0em; */
/* 	margin-top: 0em; */
	margin-bottom: 0.5em;
}

pre, code {
	font-size: 1em;
}

code {
/*   padding: 2px 4px; */
	padding: 0.1em 0.4em;
	margin: 0em 0.2em;
  white-space: nowrap;
}

blockquote {
	padding: 0.65em 1em;
	margin: 0em 0em 1.25em 0em;
}

blockquote::before, blockquote::after {
	content: "";
}

blockquote p {
	margin-bottom: 1.1em;
	font-size: 1em;
	font-weight: normal;
	line-height: 1.4em;
}

blockquote p:last-child {
	margin-bottom: 0;
}

img {
	max-width: 100%;
	vertical-align: middle;
	margin-top: 0.8em;
}

/* Preview div.preview-panel Contents */

.preview-panel {
	line-height: 1.5em;
	letter-spacing: normal;
}

#preview-contents {
	font-size: 1.0em;
  white-space: normal;
  word-wrap: break-word;
  word-break: normal;
	text-align: start;
}

/* Editor pre#wmd-imput */

#wmd-input {
	font-size: 1.0em;
	/*   font-family: "consolas",sans-serif; */
	line-height: 1.5em;
	letter-spacing: normal;
	white-space: pre-wrap;
	word-wrap: break-word;
	word-break: normal;
}

.h1, .h2, .h3, .h4, .h5, .h6 {
/* 	Style of head lines are together under. */
	font-family: inherit;
	line-height: 1.5em !important;
	color: inherit;
	margin: 0.05em 0.0em;
}

/* Editor and Preview headlines */

h1 {
	font-size: 2.0em;
	font-weight: 600;
	border-left: 10px solid rgba(120, 120, 120, 0.85);
	padding-left: 5px;
}

#wmd-input .h1 {
	font-size: 2.0em;
	font-weight: 600;
	border-left: 10px solid rgba(120, 120, 120, 0.85);
	padding-left: 0px;
}

h2 {
	font-size: 1.7em;
	font-weight: 600;
	border-left: 10px solid rgba(163, 73, 164, 0.5);
	padding-left: 5px;
}

#wmd-input .h2 {
	font-size: 1.7em;
	font-weight: 600;
	border-left: 10px solid rgba(163, 73, 164, 0.5);
	padding-left: 0px;
}

h3 {
	font-size: 1.5em;
	font-weight: 600;
	border-left: 10px solid rgba(63, 72, 204, 0.5);
	padding-left: 5px;
}

#wmd-input .h3 {
	font-size: 1.5em;
	font-weight: 600;
	border-left: 10px solid rgba(63, 72, 204, 0.5);
	padding-left: 0px;
}

h4 {
	font-size: 1.3em;
	font-weight: 600;
	border-left: 10px solid rgba(153, 217, 234, 0.5);
	padding-left: 5px;
}

#wmd-input .h4 {
	font-size: 1.3em;
	font-weight: 600;
	border-left: 10px solid rgba(153, 217, 234, 0.5);
	padding-left: 0px;
}

h5 {
	font-size: 1.15em;
	font-weight: 600;
	border-left: 10px solid rgba(160, 204, 22, 0.5);
	padding-left: 5px;
}

#wmd-input .h5 {
	font-size: 1.15em;
	font-weight: 600;
	border-left: 10px solid rgba(160, 204, 22, 0.5);
	padding-left: 0px;
}

h6 {
	font-size: 1em;
	font-weight: 600;
	border-left: 10px solid rgba(90, 90, 90, 0.5);
	padding-left: 5px;
}

#wmd-input .h6 {
	font-size: 1em;
	font-weight: 600;
	border-left: 10px solid rgba(90, 90, 90, 0.5);
	padding-left: 0px;
}

/* ============ More Styles ============ */
/* without: font-size,line-height,padding,margin white-space,word-wrap,text-align */
/* More inline styles */
body {
	tab-size: 2;
}

pre code, pre.prettyprint code {
	font-family: "consolas", sans-serif;
/* 	color: rgb(0, 0, 0); */
  border: 0;
  border-radius: 6px;
/* 	background-color: transparent; */
}

pre.prettyprint {
  border-radius: 6px;
}

pre, code {
	font-family: "consolas", sans-serif;
}

code {
  color: #000000;
/*   color: #ffffff; */
  background-color: rgba(128,128,128,0.075);
/* 	background-color: transparent; */
  border: 0.01em solid rgba(32,32,128,0.75);
  border-radius: 4px;
  border-radius: 0px;
}

/* source copy to clipboard */
.hl-header {
  color: rgb(195, 65, 65);
  text-align: center;
/*   margin: 0em 0em 0.5em; */
	background-color: transparent;
/*     background-color: rgb(244, 244, 244); */
  border-radius: inherit;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.hl-lang {
	font-size: 0.9em;
  padding: 0em 1.0em;
}

.hl-copybtn {
	font-size: 0.6em;
/* 	border: 1px solid rgba(3, 3, 3, 0.5); */
	border-radius: 3px;
  box-shadow: 0px 10px 5px #fff inset,
              0px 0px 2px RGBA(0, 0, 0, 0.15);
}
/* source copy to clipboard */

blockquote {
	color: rgb(128, 128, 128);
	background-color: rgba(128, 128, 128, 0.05);
	border-left-width: 10px;
	border-left: 10px solid rgba(128, 128, 128, 0.1);
	border-top-right-radius: 5px;
	border-bottom-right-radius: 5px;
}

blockquote code {
	background-color: transparent;
}

/* 文中追記、補足など*/
div.comment{
	position: relative;
	width: 95%;
	margin: 0.7em 0.3em 1em 1em;
	padding: 0.5em 1em 0.5em 0.7em;
/* 	padding: 0; */
	color: rgba(120, 150, 150,1);
	font-size: 0.9em;
	background-color: rgba(128, 128, 128, 0.05);
	border: 0.1em solid rgba(128, 128, 128, 0.3);
	border-right: 0.1em solid rgba(128, 128, 128, 0.7);
	border-bottom: 0.1em solid rgba(128, 128, 128, 0.7);
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
  border-bottom-right-radius: 10px;
  border-bottom-left-radius: 10px;
	box-shadow: 0 0 0;
}
div.comment::before {
	content: "Note";
	display: block;
	position: relative;
	float: right;
	top: -1.2em;
	right: -1.8em;
/* 	left: 15px; */
/* 	bottom: -9px; */
	margin: 0.0em 0.0em -1.2em -1.0em;
	padding: 0.5em 0.6em 0.3em 0.6em;
	line-height: 1.0em;
	color: rgba(128, 128, 128,0.8);
	font-size: 1.0em;
	background-color: rgba(245, 245, 245, 1);
	border: 0.12em solid rgba(128, 128, 128, 0.5);
  border-radius: 5px;
}

img {
	border: 0;
  box-shadow: 0px 10px 5px #fff,
              inset 0px 0px 10px RGBA(128, 128, 128, 0.5);
}

/* kbd: action by operator ユーザー入力や選択のスタイル */
kbd {
	font-size: 0.9em;
/* 	font-weight: bold; */
	padding: 0.0em 0.4em;
	margin: 0em 0.3em;
	display: inline-block;
	white-space: nowrap;
	line-height: 1.3em !important;
	border: 0.1em solid rgba(63, 63, 63, 1);
	border-radius: 3px;
	box-shadow: 0 0 0;
	background-color: #fff
}
kbd.wh-key {
	font-size: 0.9em;
	font-weight: bold;
  color: #000000;
	padding: 0.01em 0.4em;
	margin: 0em 0.3em;
	display: inline-block;
	white-space: nowrap;
	line-height: 1.1em !important;
	border: 0.01em solid rgba(235, 235, 235, 1);
	border-radius: 1px;
	box-shadow:
		0.03em 0.03em 0em 0.08em rgba(180, 180, 180, 1),
		inset 0.1em 0.1em 0em 0.0em rgba(245, 245, 245, 1);
/* 	background:linear-gradient(to top left, #cccccc 0%, #dddddd 45%); */
	background:
		radial-gradient(ellipse, #ffffff 35% , #f5f5f5 70%, #f0f0f0 90% );
	text-shadow:
		-0.05em -0.05em 0em rgba(255,255,255,1),
		0.1em 0.1em 0em rgba(255,255,255,1);
}
kbd.bk-key {
	font-size: 0.9em;
	font-weight: bold;
  color: #ffffff;
	padding: 0.0em 0.4em;
	margin: 0em 0.3em;
	display: inline-block;
	white-space: nowrap;
	line-height: 1.1em !important;
	border: 0.01em solid rgba(100, 100, 100, 1);
	border-radius: 1px;
	box-shadow:
		0.03em 0.03em 0em 0.08em rgba(10, 10, 10, 1);
/* 		inset 0.1em 0.1em 0em 0.0em rgba(255, 255, 255, 1); */
/* 	background:linear-gradient(to top left, #cccccc 0%, #dddddd 45%); */
	background:
		radial-gradient(ellipse, #666666 35% , #444444 70%, #333333 80% );
/* 	text-shadow:
		-0.05em -0.05em 0em rgba(255,255,255,1),
		0.1em 0.1em 0em rgba(255,255,255,1); */
}
kbd.select {
	font-size: 1em;
	font-weight: 600;
	color: #222222;
	padding: 0.2em 0.2em 0.0em 0.2em !important;
	margin: 0.1em 0.3em 0.1em 0.3em !important;
	display: inline-block;
	white-space: nowrap;
	text-shadow: -1px -1px 0px #ffffff, 1px 1px 0px #ffffff;
	border: 0.1em solid rgba(140, 140, 140, 0);
	background-color: rgba(210, 210, 210, 1.0);
	border-radius: 0px;
	box-shadow: 0 0 0;
/*  	text-shadow:
		-0.05em -0.05em 0em rgba(200,200,200,1),
		0.01em 0.01em 0em rgba(200,200,200,1); */
}
kbd.icon {
	font-size: 1em;
	font-weight: normal;
	color: #222222;
	padding: 0.2em 0.2em 0.0em 0.2em !important;
	margin: 0.1em 0.2em 0.1em 0.2em !important;
	position: relative;
	display: inline-block;
	white-space: nowrap;
	border: 0.05em solid rgba(140, 140, 140, 1);
/* 	background-color: rgba(210, 210, 210, 1.0); */
	border-radius: 3px;
	box-shadow: 0 0 0;
/*  	text-shadow:
		-0.05em -0.05em 0em rgba(200,200,200,1),
		0.01em 0.01em 0em rgba(200,200,200,1); */
}
kbd.icon::before {
	content: "";
	display: inline-block;
	position: relative;
	margin: 0.0em 0.4em 0.1em 0.0em !important;
	font-size: 1.0em;
	font-weight: 600;
	font-family: "consolas", sans-serif;
	color: #d82a1a;
	text-align: center;
	width: 1.2em;
	height: 1.2em;
/*   border: 0.1em solid rgba(30, 122, 232,0.75); */
	border-top: 0.05em solid rgba(100, 100, 100, 1);
	border-right: 0.08em solid rgba(100, 100, 100, 1);
	border-bottom: 0.08em solid rgba(100, 100, 100, 1);
	border-left: 0.05em solid rgba(100, 100, 100, 1);
	border-radius: 0.2em;
	box-shadow:	0.08em 0.08em 0em 0.05em rgba(210, 210, 210, 1);
/* 		,inset 0.1em 0.1em 0em 0.0em rgba(245, 245, 245, 1); */
/* 	background:linear-gradient(to top left, #cccccc 0%, #dddddd 45%); */
/* 	background:
		radial-gradient(ellipse, #ffffff 35% , #f5f5f5 70%, #f0f0f0 90% );
	 */
}
kbd.icon::after {
	content: url('data:image/svg+xml;charset=utf-8;base64,');
	
	position: absolute;
	bottom: 0.2em;
	left: 0.5em;
  display: inline-block;
  width: 1em;
  height: 1em;
/*   stroke-width: 1; */
/*   stroke: currentColor; */
  fill: #990000; 
	color: #0F52BA;

}


kbd.input {
	font-size: 1em;
	font-family: "consolas", sans-serif;
  color: #000000;
/*   padding: 2px 4px; */
	padding: 0.0em 0.3em;
	margin: 0em 0.3em;
  white-space: nowrap;
/*   color: #ffffff; */
/*   background-color: rgba(214, 235, 255, 1); */
	background-color: rgba(240, 240, 240, 0.75);
/* 	background-color: transparent; */
  border: 0.1em solid rgba(30, 122, 232,0.75);
/* 	box-shadow:
		0.03em 0.03em 0em 0.08em rgba(50, 100, 255, 1); */
/* 		inset 0.1em 0.1em 0em 0.1em rgba(50, 255, 255, 1); */
  border-radius: 0px;
}

a.footnote:before {
	content: "*";
}
a.footnote:after {
	content: "";
}

/* for kbd style */
.rotate_90 {
	padding: 0.0em 0.1em 0.0em 0.1em !important;
	margin: 0.1em 0.3em 0.1em 0.3em !important;
	transform: rotate(90deg);
}
.rotate_180 {
	transform: rotate(180deg);
}
.rotate_270 {
	padding: 0.0em 0.1em 0.0em 0.1em !important;
	margin: 0.1em 0.3em 0.1em 0.3em !important;
	transform: rotate(-90deg);
}


/* Preview div.preview-panel Contents */
.preview-panel {
	font-family: "Verdana", sans-serif;
	color: rgb(0, 0, 0);
	background-color: #fcfcfc;
}

#preview-contents [class^="icon-"]:before, #preview-contents [class*=" icon-"]:before {
	color: #3434aa;
}

/* Editor */
#wmd-input {
	/*   font-family: "consolas",sans-serif; */
	color: rgb(0, 0, 0);
	/*   background-color: #fcfcfc; */
	background-color: rgb(245, 245, 240);
}

#wmd-input.font-rich-monospaced * {
	font-family: "consolas", sans-serif !important;
	/* 	font-size: 1.1em; */
}

#wmd-input.font-monospaced * {
	font-family: "consolas", sans-serif !important;
}

#wmd-input .code {
	font-size: 1em;
	font-family: "consolas", sans-serif;
	color: rgb(0, 0, 0);
	padding: 0.05em 0.2em;
}

#wmd-input .tag {
	color: #3434aa;
	font: bold 1em "consolas", sans-serif;
}
}
