css:
@-moz-document domain("www.bible.com") {
/* dark theme foreva */
html,
body,
[moment-note],
#site-footer .footer-logo {
    color: white;
    background-color: black;
}

h1,
h2,
h3,
h4,
h5,
h6,
.a_show ul.no-bullets a,
.c_plans.a_sample ul.no-bullets a {
	color: white;
}

input[type='text'],
input[type='search'],
input.search,
textarea,
select,
.bcv-button-box a.prev,
.bcv-button-box a.next,
.bcv-button-box a,
.moment,
.moment-comments-self-box,
#read_chapter h2,
.hollow-button.gray,
md-menu-content.md-default-theme,
md-menu-content {
    color: white;
    background-color: #333;
}

/* I wouldn't do this if I had sass okay */
input[type='text']:hover,
input[type='text']:focus,
input[type='search']:hover,
input[type='search']:focus,
input.search:hover,
input.search:focus,
textarea:hover,
textarea:focus,
select:hover,
select:focus,
.bcv-button-box a.prev:hover,
.bcv-button-box a.prev:focus,
.bcv-button-box a.next:hover,
.bcv-button-box a.next:focus,
.bcv-button-box a:hover,
.bcv-button-box a:focus,
#read_chapter h2:hover,
#read_chapter h2:focus,
.hollow-button.gray:hover,
.hollow-button.gray:focus {
    background-color: #555 !important;
}


/* risky move */
.white {
	background-color: #111;
}

/* fix plaintext notes */
[moment-note] {
    white-space: pre-wrap;
}
}
