css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("basketsession.com") {


    .g-wrap.g-spot{
    background:#111
  }
  
  
  body,
  .g-main
  {
    background:#222;
    box-shadow:0px 0 0 #fff,0px 0 0 #fff
  }
  
  /* Menu */
  .main-bar .m-submenu{
    background:#333
    
  }
  .main-bar .m-submenu .as-parent > li.more-contents ul li a{
    color:#ccc;
  }
  .main-bar .m-submenu .as-parent > li.see-more a{
    color:#ccc;
    background:#333
    
  }
  .main-bar .m-burger ul{
    background:#333
  }
  .main-bar .m-burger ul li a{
        color:#ccc;
    
  }
  /* End menu */
  
  /*boxscores*/
  .l-boxscores{
    background:#444
    
  }
  /* end boxscores */
  
  /*post*/
  .post{
    background:#333
  }
  .post.i-universe h3 a{
    color:#ccc
  }
  .post.i-post h3 a{
    color: #ddd;
  }
  
  .post.i-post .i-content{
    color: gray
  }
  /* end post */
  
  /* Article */
  
  .article h1 a,
  .article .a-excerpt,
  .article .a-content, .article .a-shopping,
  .l-chronolist header h2,
  .w-lastcategory header h3,
  .w-lastcategory .see-more a,
  #idc-container div div div,
  #idc-container .idc-message{
    color:#ccc;
    
  }
  
  .post.i-chrono{
    background:#333;
  }
  .post.i-chrono h3 a,
  .article .a-time time{
    color:#aaa;
  }
  /* End article */
  
  /* Table */
  table tr:nth-child(2n+1){
    background-color:#444;
  }
  table tr:nth-child(2n){
    background-color:#555;
    
  }
  /* end table */
  
  /* */
  .article .a-content .f-caption, .article .a-shopping .f-caption{
    color:gray;
    border:1px solid gray;
  }
  /* */
  
  /* Author */
  
  .l-author-title, header.l-title,
  .article .a-footer hr{
    display: none;
  }
  /* end author */
  
  /* Comment */
  #idc-container .idc-c .idc-i{
    background:#333;
    
  }
  /* End comment */
  
  /* Adds */
  .post.i-link,
  img.alignnone.size-full.wp-image-360729{
    display: none;
  }
  /* End adds */
  
}
