css:
/* TAB TREE STYLE */

/* Removing white background */
.treestyletab-tabbar-toolbar .tab-background{
	background:none !important;
	border:none !important;
}

/* Removing border */
.treestyletab-tabbar-toolbar .tabbrowser-tabs .tabbrowser-tab{
	border:none !important;
}

/* Changing the black border for the selected tab */
.treestyletab-tabbar-toolbar .tab-background-middle{
	border:none !important;
}

/* Applying style from global/tabbox.css minus magin/padding */
.treestyletab-tabbar-toolbar{
	-moz-appearance: none !important;
	border: 1px solid rgb(0,0,0) !important;
	border-radius: 0px !important;
	background: rgb(40,40,40) !important;
	color: rgb(166,166,166) !important;
}

.treestyletab-tabbar-toolbar tab{
	-moz-appearance: none !important;
	border-radius: 0px !important;
	border-top:1px solid rgb(0,0,0) !important;
	border-right:1px solid rgb(0,0,0) !important;
	border-left:1px solid rgb(0,0,0) !important;
	border-bottom:1px solid rgb(0,0,0) !important;
	color: rgb(166,166,166) !important;
}

.treestyletab-tabbar-toolbar tab:hover{
	color: rgb(255,255,255) !important;
}

.treestyletab-tabbar-toolbar tab[selected="true"]{
	color: rgb(0,173,238) !important;
	border-top: 1px solid rgb(0,0,0) !important;
	border-right: 1px solid rgb(0,0,0) !important;
	border-left: 1px solid rgb(0,0,0) !important;
	border-bottom:1px solid rgb(0,0,0) !important;
}

.treestyletab-tabbar-toolbar tab:-moz-focusring > .tab-middle {
	/* Don't specify the outline-color, we should always use initial value. */
/* edited by FT DeepDark's author */
	text-shadow: 0px 0px 3px rgba(0,173,238,.5) !important;
	transition: color .2s ease-in !important;
}

.treestyletab-tabbar-toolbar tab[beforeselected="true"]:-moz-locale-dir(ltr),
.treestyletab-tabbar-toolbar tab[selected="true"]:-moz-locale-dir(rtl) + tab {
	border-right: none !important;
	border-top-right-radius: 0 !important;
}

.treestyletab-tabbar-toolbar tab[selected="true"]:-moz-locale-dir(ltr) + tab,
.treestyletab-tabbar-toolbar tab[beforeselected="true"]:-moz-locale-dir(rtl) {
	border-left: none !important;
	border-top-left-radius: 0 !important;
}


/* Tabs background colors */
/* Removing border */
.treestyletab-tabbar-toolbar[treestyletab-mode="vertical"] .tabbrowser-tab{
	box-shadow: 0 1px 1px -1px #000 inset,
				1px 0 1px -1px #000 inset,
				-1px 0 1px -1px #000 inset
				!important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="vertical"] .tabbrowser-tab:last-child{
	box-shadow: 0 1px 1px -1px #000 inset,
				0 -1px 1px -1px #000 inset,
				1px 0 1px -1px #000 inset,
				-1px 0 1px -1px #000 inset
				!important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="vertical"] tab[selected="true"]{
	background: rgba(0,0,0,.5) !important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="vertical"] tab[selected="true"]:hover{
	background: rgba(0,0,0,.5) !important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="vertical"] tab{
	background: rgb(40,40,40) !important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="vertical"] tab:hover{
	background: rgb(60,60,60) !important;
}

.treestyletab-tabbar-toolbar[treestyletab-mode="horizontal"] tab[selected="true"]{
	background: rgba(0,0,0,.5) !important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="horizontal"] tab[selected="true"]:hover{
	background: rgba(0,0,0,.5) !important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="horizontal"] tab{
	background: rgb(40,40,40) !important;
}
.treestyletab-tabbar-toolbar[treestyletab-mode="horizontal"] tab:hover{
	background: rgb(60,60,60) !important;
}
