css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://oper.ru") {

body {
    background-color: #1a1a1a !important;
    background-image: none !important;
}
body * {
    max-width: 990px !important;
    font-family: 'Verdana' !important;
}
* {
    font-weight: inherit !important;
}
a {
    color: #898989 !important;
}
a:hover {
    color: #a9a9a9 !important;
}
.usfscrpt hr {
    background-color: #444 !important;
    margin: 7px 0;
    border: 1px solid #444;
}
div#wrapper {
    background-color: #101010 !important;
    background-image: none !important;
    max-width: 1000px !important;
    min-width: 960px !important;
    margin: 0 auto;
    border-width: 10px 0px 75px;
    border-style: solid;
    border-color: #aa3434 #101010 #101010 #101010;
}
div#wrapper>a {
    display: none;
}
div#middle dt>a,
div#middle h1 a,
table.comment a {
    font-weight: inherit !important;
}
a {
    color: #666 !important;
    /* border-bottom:1px solid #222;*/
}
div#middle h1 a,
table.comment a {
    color: #063 !important;
    border: none;
}
div.body a {
    color: #898989 !important;
    border-bottom: 1px dotted #aaa !important;
}
div.body a:hover {
    color: #a9a9a9 !important;
    border-bottom: 1px solid #aaa !important;
}
div#middle dt>a,
div#middle h1 a {
    color: #cdcdcd !important;
    border-bottom: none !important;
}
div#middle dt>a:hover,
div#middle h1 a:hover {
    color: #efefef !important;
    border-bottom: none !important;
}
p.categories a {
    color: #898989 !important;
    border-bottom: 1px dotted #aaa !important;
}
p.categories a:hover {
    color: #d1d1d1 !important;
    border-bottom: 1px solid #a2a2a2 !important;
}
body {
    color: #777 !important;
}
/*div>font b {color:#888 !important;}*/
/* * {background-color:#101010;} */

ul.tablist {
    background-color: #101010;
    /*border:1px solid #fff;*/
    
    height: inherit;
}
ul.tablist li.current > a {
    background-color: #101010;
    color: #adadad !important;
    text-shadow: none !important;
    /* border-bottom:3px solid #fff;*/
}
ul.tablist li.current > a:hover {
    background-color: #101010;
}
ul.tablist li {
    margin-right: 30px;
    margin-left: 10px;
}
ul.tablist li > a {
    background-color: #101010;
    color: #adadad !important;
    text-shadow: none !important;
    padding: 0;
    /*border-bottom:3px solid #fff;*/
}
ul.tablist li > a:hover {
    background-color: #101010;
    color: #fff !important;
    border-bottom-color: #ddd !important;
}
ul.tablist li > a img {
    display: none !important;
}
#container {
    background-color: #101010 !important;
    padding: 0 !important;
}
#container>div {
    height: 1px !important;
}
#container table td {
    background-color: #101010 !important;
}
#container>table>tbody>tr {
    background-color: #101010 !important;
}
#headermenut {
    background-image: none !important;
    height: 30px !important;
}
#headermenut a img {
    width: 125px !important;
    height: 35px !important;
}
div#headermenul,
div#headermenul * {
    background-color: #101010 !important;
}
#headermenul a {
    color: #844 !important;
}
div.body {
    color: #adadad;
    padding: 0 !important;
    margin: 0 !important;
}
div#middle dt,
div#middle h1 {
    padding: 10px 0px;
    border-width: 1px;
    border-color: #333 #101010 #101010 #101010 !important;
    color: #444 !important;
    background-color: #101010 !important;
}
div#middle dd {
    padding: 0 !important;
}
div#middle dl p.meta {
    font-size: 16px;
}
div#middle dl p.meta a font {
    color: inherit !important;
}
div#middle {
    color: #666 !important;
    background-color: #101010;
    font-size: 16px !important;
    margin-top: 15px;
    width: 750px !important;
    padding: 0 !important;
}
div#middle h1 a {
    text-shadow: none !important;
}
div#middle dl {
    margin-bottom: 20px;
}
div#middle blockquote {
    color: #bbb !important;
    background-color: #212121 !important;
    line-height: 1.4;
    padding: 30px 30px 30px 50px;
    margin: 10px 0;
}
.likely > div.likely__widget {
    background-color: #292929 !important;
    color: #c1c1c1 !important;
    border: 1px solid #363636;
}
/* Комментарии */

table.comment a {
    color: #870 !important;
}
table.comment a:hover {
    color: #981 !important;
    border-bottom: 1px solid #650 !important;
}
table.comment {
    background-color: #101010 !important;
    color: #adadad;
    border-collapse: collapse;
    border-spacing: 0px;
}
table.comment {
    border: 1px solid #272727;
}
table.comment .quoted {
    color: #555 !important;
}
span.masked {
    color: #a00 !important;
}
/* Forum */

div#middle table.forum td.opened {
    background-color: #333 !important;
    color: #aaa;
}
table.forum {
    /*border:1px solid #ccc;*/
    
    color: #333;
}
table.forum td.text13>b {
    background-color: #333;
    padding: 3px 7px;
}
span.quoted {
    color: #666;
}
span.text13,
span.posted {
    font-size: 14px !important;
}
font.verdana > div {
    font-size: 16px !important;
}
p.meta a,
span.text13 a,
td.text13 a {
    color: #898989 !important;
    border-bottom: 1px dotted #565656 !important;
}
p.meta a:hover,
span.text13 a:hover,
td.text13 a:hover {
    color: #adadad !important;
    border-bottom: 1px solid #aaa !important;
}
/* Form */

form[name="commentform"] input {
    background: #000;
    color: #aaa;
    border: 1px solid #333;
    padding: 5px 7px;
}
/*.block{border-color:#fff !important;}*/

#right {
    background-color: #101010 !important;
}
#right .block {
    padding: 0;
    border: none;
}
#right .block a {
    color: #898989 !important;
    display: block;
}
#right .block a:hover {
    color: #a9a9a9 !important;
}
#right .block h3 a {
    color: #94870a !important;
    display: none;
}

#right .block img {
    border: none;
}

div#i font {
    color: #555 !important;
}
.bottomp a {
    color: #555 !important;
    border-bottom: 1px dotted #aaa !important;
}
.bottomp a:hover {
    color: #063 !important;
    border-bottom: 1px solid #063 !important;
}
.bottomh {
    color: #888 !important;
}
.bottomh a {
    color: #444 !important;
}
/* other */

hr {
    color: #ccc !important;
}
/* Удалить */

li.search {
    display: none;
}
li.special {
    display: none;
}
div.mega_block {
    display: none;
}
#soc {
    display: none !important;
}
#container > div:first-child {
    display: none !important;
}
div#topbanner {
    display: none !important;
}
td[width="120"][rowspan="2"] {
    display: none !important;
}
/* iframe.twitter-timeline-rendered {display:none !important;} */
/*div#right>iframe {display:none !important;}*/

div#vk_groups {
    display: none !important;
}
div.fb-page {
    display: none !important;
}
div.fb_reset {
    display: none !important;
}
.banner {
    display: none !important;
}
yatag {
    display: none !important;
}
/* v.1.0.10 */
}
