css:
@-moz-document domain("ottoneu.fangraphs.com") {
body {
    color: #626a6a;
}
main {
    margin-top: 12px;
    padding: 0px 16px;
}
a {
    color: #4b8162;
}
a, a:hover, a:visited, a:link, a:active {
    box-shadow: none;
}
table {
    font-size: 12px;
    font-family: monospace;
}
td,
th,
table {
    white-space: normal;
    border-color: #f2f2f2;
}
th,
td {
    padding: 3px;
}
tr.even {
    background-color: #eff5f5;
}
.messages-item {
    padding: 4px 8px;
    font-size: 13px;
}
h4.messages-byline {
    margin: 0px 4px;
    font-size: 11px;
}
.messages-list,
li.messages-item,
li.messages-item:not(:last-child) {
    border-color: #f2f2f2;
}
li.messages-item p {
    margin-bottom: 4px;
}
.section-container-header {
    border: 0px;
    background-color: inherit;
    padding-left: 2px;
}
.section-container-header h3 {
    padding: 0px;
}
main #notifications {
    margin-bottom: 20px;
    line-height: 18px;
}
main #notifications p {
    margin: 0 0 8px 0;
}
main .notifications {
    margin-bottom: 12px;
}
main .sidebar-layout__primary td {
    white-space: nowrap;
    font-size: 10px;
}
main .sidebar-layout__secondary table {
    border: 0px;
    font-size: 12px;
}
main .sidebar-layout__secondary td {
    border-color: #f2f2f2;
}
table.lineup-table {
    text-align: right;
    width: 720px;
}
table.lineup-table td.player-name {
    text-align: left;
}
tr:hover {
    background-color: #ffffb3;
    color: #111;
}
tr:hover a {
    color: #264031;
}

#cutPlayers td,
#hitters td,
#pitchers td {
    font-size: 13px;
}
td.empty_slot a {
	color: #d1e0d7;
}
td.empty_slot a:hover {
	color: #4b8162;
}
tr.pending-trade a {
	color: #99c9cc;
    font-weight:600;
}
.page-header, .page-header__secondary, .page-header__section:not(:last-child), .page-header__section--justify:not(:last-child), .page-header__section--split:not(:last-child) {
	border: 0px;
    vertical-align: middle;
}


}
