css:
@-moz-document domain(apidog.ru) {
*{
  color:#fff;
  /*[[font]]*/
}
html{
	background:#171717 url("/*[[bg]]*/") center fixed !important;
	background-size:cover !important;
	background-repeat:no-repeat !important;
}
a, a:visited, .a, a:focus, a strong, a .bold{color: #666;}
textarea,input,select,select option{color:black;}
.loader-line{border:5px solid #fff;border-right-color:transparent;}
.hat{
	background:#222;
	box-shadow:0 0 2px 1px rgba(0,0,0,.7);
}
.hat-menu-icon{border-right: 1px solid #333;}
.hat-profile:hover,.hat-page-name:hover{background:rgba(0,0,0,.25);}
.hat-photo{border:1px solid #444;}
.add-audio, .live-audio, .text-audio, .share-audio, .repeat-audio, .find-audio{
    -webkit-filter: saturate(0) !important;
	filter: saturate(0) !important;
}
.menu, .page{background: rgba(0,0,0,.2);box-shadow: 0 0 2px 1px rgba(0,0,0,.7);}
.menu aside a:hover, .profile-lists a:hover{background:rgba(255, 255, 255, 0.05);}
._notloaded .loadScreen-wrap {
    background: #333;
}
.menu aside svg{
    -webkit-filter:brightness(2)saturate(0)!important;
    filter:brightness(2)saturate(0)!important;
}
.online,.online-verify,.wall-icons,.imdialog-icon-general,.feed-delete,.audio-sprite,.list-icon-add,.docs-delete{
    -webkit-filter: saturate(0)!important;
    filter: saturate(0)!important;
}

.footer:after{color:#888;content:"byKIM (id971718)";font-size:x-small;}
.menu-notify-wrap{background:rgba(255,255,255,.1);}
.menu-notify-wrap strong{color: #999;}
.menu a i{background: #333;}
.hider-title{
    background:#222;
    border-top:1px solid #333;
    border-bottom:1px solid #333;
}
 blockquote{
   border-left: 9px solid rgba(70, 70, 70, 0.5);
   border-bottom: 2px solid rgba(51, 51, 51, 0.5);
   background: rgba(39, 39, 39, 0.3);
}
.topNotification-wrap {background: rgb(25, 25, 25);border-bottom: 1px solid rgba(255, 255, 255, 0.2);}
.hider-title *{color: #999;}
.profile-gotofullinfo{background:rgba(255,255,255,.07);}
.profile-lists a{border-bottom: 1px solid #333;}
.wall-new{
	background:rgba(255,255,255,.03);
	border-bottom:1px solid #333;
}
button, input[type=button], input[type=submit], input[type=reset], .btn {
	background: #333!important;
    text-shadow:0 0 1px rgba(0,0,0,.7);
}
.wall-item{border-bottom:1px solid #333;}
.wall-repost{border-left:2px solid #666;}
.wall-head-meta *{color:#666;}

.dd-title{color:#666;}
.dd{
    background: #333;
    box-shadow: 0px 1px 1px 1px rgba(0,0,0,0.7);
    border-top: 1px solid rgba(0,0,0,0.7);
}
.dd-titlefake {
    background: #222;
    color: #444!important;
}
.dd-item:hover{background: rgba(255, 255, 255, 0.05);}

.tab-sel{background:#333!important;}
.tab:hover{background:#333!important;border-radius:2px}
.gifts-head{border-bottom: 1px solid #666;}
.dialogs-item-wrap{border-bottom: 1px solid #333;}
.dialogs-right strong{color:#999!important;}
.dialogs-date{color:#999}
#_im-sel-type-attach .imdialog-attachment-arrow{border-bottom-color:#333;}
.imdialog-list{background:none;}
.imdialog-wrap{
	border:1px solid #333;
    background:rgba(0,0,0,.3);
}
.imdialog-my .imdialog-wrap{
	border: 1px solid #333;
	background:rgba(255,255,255,.1);
}
.friends-item{border-bottom:1px solid #333}
.modal{
    border:1px solid #333;
	background: rgba(0,0,0,.6);
    box-shadow:0 2px 2px 0 rgba(0,0,0,0.7),0 3px 1px -2px rgba(0,0,0,0.7),0 1px 5px 0 rgba(0,0,0,0.7);
}
.modal-title{
    border-bottom:1px solid #333;
    background:rgba(0,0,0,.9);
}
.modal-content{color:#fff;}
.modal-footer{border-top:1px solid #333;}
.modal-footer button{color:#fff!important;}
.im-fwds {border-left:2px solid #666;}
.imdialog-item.imdialog-unread{background:rgba(255, 255, 255, 0.06);}
.imdialog-item.imdialog-selected{background:rgba(255, 255, 255, 0.1);}
.imdialog-arrow{background: none;}
.imdialog-item {
    -webkit-transition: none;
    -o-transition: none;
    transition: none;
}
.imdialog-emotions {
    border:1px solid #333;
    background:#444;
}
.imdialog-emotions-tabs{background: #333;}
.imdialog-emotions-tab-active{background:#222;}
.imdialog-attachment-arrow{border-bottom:10px solid #333;}
.imdialog--fix .imdialog-attachment-arrow{border-top: 10px solid #333;}
.miniprofiles-item{border-bottom: 1px solid #333;}
.wall-likes span.likebutton-active{color:#aaa!important;}
.wall-likes span{color:#666;}
.wall-date, .wall-footer *{color: #666!important;}
.tip, .tip a{color: #aaa!important;}
.notifications-feedbacked {
    background: rgba(255, 255, 255, 0.1);
    box-shadow: 0 0 2px rgba(0,0,0,.7);
}
.notifications-feedbacked:before{border-color: rgba(255, 255, 255, 0.1) transparent;}
.notifications-item{border-bottom: 1px solid #333;}
.notifications-more, .button-block{
    background: #333;
    color: #fff;
}
.msg-loader {
    background-image: url(https://static.apidog.ru/im-attachload.gif);
    background-position: center center;
    background-repeat: no-repeat;
}
.groups-item:hover {
    background: rgba(0,0,0,.7);
	border-top: 1px solid #333;
}
*::-webkit-scrollbar {
    width: 15px !important;
    height: 10px !important;
    background: rgba(0, 0, 0, 0) !important;
    border: 1px solid rgba(0, 0, 0, 0) !important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px !important;
	background: #333333 !important;
}
::selection {
background-color:#ccc;
color:#222;
}
::-moz-selection {
background-color:#ccc;
color:#222 !important;
}
.groups-item{border-bottom: 1px solid #333;}
.dialogs-unread{background:#333;}
.dialogs-item-new{border-right: 5px solid #aaa;}
.m-head, .m-head-right{color:#fff;}
.m-head-wrap{border-bottom: 1px solid #333;}
.mail-tsrn, .mail-tsrt, .mail-tsrv, .mail-analyzer-summary td, .mail-analyzer-summary th{border: 1px solid #333;}
.pb-line{background:#aaa;}
.pb-wrap{background:#333;}

.albums-item{border-bottom: 1px solid #333;}
.albums-item:hover{
    background:rgba(0,0,0,.7);
    border-top:1px solid #333;
}
.topbtn {
    background:rgba(0,0,0,.1);
    border-bottom:1px solid #333;
}
.wall-extra-buttons{background:rgba(0,0,0,.5);}
.selectattachments-wrap{border:1px solid #333;}
.selectattachments-title{
    border-bottom: 1px solid #333;
    color: #fff;
    background: #333;
}
.changer-title{
    background: url(https://static.apidog.ru/flexArrow.png) no-repeat 0 0 #333;
    color: #888888;
}
.changer-opened{background: url(https://static.apidog.ru/flexArrow.png) no-repeat 0 -24px #333;}
.changer-content{border: 1px solid #333;}
.audio-item:hover{background:rgba(255,255,255,.1);}
.audio-wrap{background:none;}
.audio-panel-sel, .audio-panel-sel:hover{background:rgba(255,255,255,.1)!important;}
.audio-panel a:hover{background:rgba(255,255,255,.1);}
#audiolist, #audio-search, #audio-search-list{
    border-right:1px solid #333;
    background:none;
}
.audio-item-title{color:#999;}
.list-item:hover{background:rgba(255,255,255,.1);}
.list-item{border-bottom:1px solid #333;}
.audios-played,.audios-played:hover,.audio-playing,.audio-playing:hover{background: rgba(255,255,255,.06)!important;}

.videos-item{border-bottom:1px solid #333;}
.videos-item:hover{
    background:rgba(255,255,255,.1);
    border-top:1px solid #333;
}

.docs-ext-dale,.docs-ext{background: #333;}
.docs-item{border-bottom:1px solid #333;}
.docs-item:first-child{border-top:1px solid #333;}

.imdialog--fix .im-textfield-wrap{background:#222;}
.boards-item:hover{
    background:rgba(255,255,255,.1);
    border-top:1px solid #333;
}
.boards-item{border-bottom:1px solid #333;}
.comments, .comment-wrap {border-top: 1px solid #333;}

@media screen and (max-width: 855px){
.hat-profile{border-left: 1px solid #333;}
.hat-page-name, .head-player{
    border-left: 1px solid #333;
    border-right: 1px solid #333;
}
.hat-menu-icon:hover{background-color:rgba(0,0,0,.25);}
}
}
