css:
@-moz-document domain("agora.gg") {
/* Colores

Gris Claro Bordes : #4e5259
Fondo Oscuro: #1e2124
Fondo Semi-Oscuro: #2e3136
Fondo Claro: #36393e
Letra General: #999999
Azul: #7289da
Verde: #43b581
Rojo: #f04747
Lila: #a155e8

*/

body {
	background-color: #1e2124;
}

body, h2, input {
	color: #999999 !important;
}

a {
	color: #7289da !important;
}

.container {
	width: 1300px !important;
}
/* ------------------ NAV MENU ------------------ */
/* Bg Color */

.navbar, .open.active a:focus {
	background-color: #2e3136 !important;
}
/* Hover links Menu */

.nav li a:after {
	background-color: #7289da !important;
}
/* ------------------ FOOTER ------------------ */

footer {
	background-color: #2e3136;
}

footer .container .content {
	background-color: transparent;
	display: flex;
	justify-content: center;
	align-items: center;
}

footer .container .content .credit i {
	color: #7289da;
}

footer .container .content .subcredit {
	margin-left: 10px;
	font-size: 14px;
}
/* ------------------ LOADER ------------------ */
/* Loader Background Body */

.bootstrap-loader {
	background-color: #1e2124 !important;
}
/* Containter Loader Fetch */

.standard-loading {
	background-color: #36393e !important;
}
/* Color Text */

.info {
	color: #999 !important;
}
/* ------------------ HEADER PLAYER NAV ------------------ */
/* background */

[_nghost-c2] {
	background-color: #1e2124;
}
/* links */

[_ngcontent-c2] {
	background-color: #1e2124;
}
/* link active */

.player-navbar div ul li.active a, .player-navbar div ul li.active a:focus {
	background-color: #1e2124 !important;
}
/* Text */

.player-navbar div ul li.active a span, core-profile core-profile-header h2 {
	color: #7289da !important;
}
/* background Img*/

core-profile core-profile-header shared-hero-icon {
	background-color: transparent !important;
}
/* Border Img*/

core-profile core-profile-header shared-hero-icon i {
	border-radius: 50%;
	border: solid 2px #2e3136;
}

core-profile core-profile-header h2 {
	color: #7289da !important;
}
/* ------------------ PLAYER ELO ------------------ */

.section-header {
	border-bottom: none !important;
}

.player-elo {
	background-color: #36393e !important;
	border-top: none !important;
}

.previous-elo {
	border-top: none !important;
	margin-top: 5px !important;
}
/* ------------------ SEARCH ------------------ */
/* Serch Input */

.form-control {
	background-color: #36393e;
	border: 1px solid #4e5259;
}
/* button search iconfont */

.input-group-btn button {
	color: #999;
	background-color: #36393e;
	border: 1px solid #4e5259;
}

.input-group-btn button:hover {
	color: #7289da;
	background-color: #36393e;
	border: 1px solid #4e5259;
}
/* ------------------ BUTTONS ------------------ */
/*Buttons Toogle Charts.js*/

.section-header .controls button {
	background: #36393e;
	border-color: #4a4c51;
	color: #999;
	border-radius: 0;
}

.section-header .controls button.active {
	background: #7289da;
	border-color: #7289da;
}

.section-header .controls button:focus {
	background: #7289da;
	border-color: #7289da;
	color: white;
}
/*Button Profile*/

.header-right .login button, .header-right .user button {
	background-color: #7289da !important;
}
/* Button Show Lifetime Stats */

.load-more button {
	background-color: transparent;
	border: none;
	color: #7289da !important;
	border-radius: 0;
}

.load-more button:hover, .load-more button:active {
	background-color: #7289da;
	color: white !important;
}
/* Button Show More Matches */

.recent-summary button {
	background-color: #7289da;
	color: white !important;
	border: none;
	width: 30% !important;
	border-radius: 0;
}

.recent-summary button:hover {
	background-color: #5e78d5;
}
/* ------------------------------------ MATCH DETAILS ------------------------------------ */

.recent-summary {
	background-color: transparent !important;
	display: flex;
	align-items: center;
	justify-content: center;
	width: 100%;
	flex-grow: 1;
	flex-wrap: wrap;
}
/* ------------------ MATCH STATS ------------------ */

.stats-header {
	background-color: #2e3136 !important;
	border-bottom: none !important;
	padding: 10px 0 !important;
	text-align: center;
	text-transform: uppercase;
	width: 100%;
}

.stats-container {
	width: 100%;
	display: flex;
	justify-content: space-between;
	padding: 10px 25px !important;
	height: 100% !important;
	align-items: center;
}

.stats-container .elo {
	width: 30% !important;
	flex-grow: 1;
	padding-right: 0 !important;
}

#winloss {
	display: flex;
	font-size: 24px !important;
	width: 30% !important;
	flex-grow: 1;
}

#winloss .win, #winloss .loss {
	margin: 0 5px;
}

#winloss .pct {
	font-size: 14px !important;
	align-self: center;
	padding-left: 5px !important;
	font-weight: normal !important;
	padding-top: 5px !important;
}

#kda {
	display: flex;
	align-items: center;
	width: 30% !important;
	flex-grow: 1;
}

#kda .k, #kda .d, #kda .a {
	font-size: 24px !important;
	margin: 0 5px;
}

#kda .kda {
	order: 2;
	margin: 0 5px;
}

#kda .kp {
	order: 3;
	margin: 0 5px;
	font-size: 14px !important;
}
/* ------------------ MATCH HISTORY ------------------ */
/* Select Heros */

.section-header .controls select {
	background-color: #2e3136;
	color: #7289da;
}
/* ------------------ MATCH GAMEPLAY ------------------ */

.matches {
	width: 100%;
}

.matches shared-game div .match-mini .hero {
	display: flex;
	flex-direction: column;
	align-items: center;
}
/* Icon Heroes */

.matches shared-game div .match-mini .hero shared-hero-icon i, .matches shared-game div .match-mini .teams .team li shared-hero-icon i {
	border-radius: 50%;
}
/* Green Line */

shared-game .status-1 {
	border-left: 4px solid #43b581 !important;
}
/* Red Line */

shared-game .status-2 {
	border-left: 4px solid #f04747 !important;
}
/* button dropdown */

.info-right button, .info-right button:hover, .info-right button:focus {
	background-color: transparent !important;
	border: none !important;
}

.info-right button i:before {
	content: 'â–¼';
	color: #7289da !important;
}
/* Button Link Gameplay */

.info-right a, .info-right a:hover, .info-right a:focus {
	background-color: transparent !important;
	border: none !important;
}
/* title win/loss */

.match .info-bar .info span {
	text-transform: uppercase;
	font-weight: bold;
	letter-spacing: 1px;
	margin-left: 10px;
}
/* Background header info*/

.match-mini {
	background-color: #36393e !important;
	padding: 15px 10px !important;
	display: flex;
	justify-content: space-around;
	align-items: center;
}

.match-mini .hero {
	display: flex;
	position: initial !important;
}

.match-mini .info {
	margin-left: 0 !important;
}

.match-mini .teams {
	position: initial !important;
}

.match-mini .teams .team {
	display: flex;
}

.match-mini .teams .team-0 {
	margin-bottom: 10px !important;
}

.info-bar {
	margin-top: 1rem;
	background-color: #2e3136 !important;
	border-bottom: none !important;
}

.stats-container {
	background: #36393e;
}
/* ------------------ FULL MATCH GAMEPLAY ------------------ */

.match.status-1, .match.status-2 {
	background: #2e3136 !important;
}
/* Replay ID */

.replay-id {
	background: #2e3136 !important;
}

.replay-id input {
	background: #36393e !important;
	border: none !important;
	border-radius: 3px;
}
/* Link Player */

.highlight {
	color: #43b581 !important;
	text-decoration: none !important;
}
/* ------------------ MATCH TABLES DETAIL ------------------ */
/* Header Team Red */

table.team-0 > tbody > tr > th {
	background: #aa0e0e !important;
	border-bottom: 3px solid #f04747 !important;
	border-left: 2px solid #aa0e0e !important;
	border-top: 2px solid #aa0e0e !important;
}
/* Header Blue Red */

table.team-1 > tbody > tr > th {
	background: #253c8d !important;
	border-bottom: 3px solid #7289da !important;
	border-left: 2px solid #253c8d !important;
	border-top: 2px solid #253c8d !important;
}

.table tbody tr td {
	color: #999999 !important;
}
/*Row Tables*/

td, th {
	background: #36393e !important;
	border-right: 1px solid #2e3136 !important;
	border-bottom: 1px solid #2e3136 !important;
	border-left: 1px solid #2e3136 !important;
}
/*Header Boxes*/

.section-header {
	background: #2e3136;
	border-bottom: 1px solid #4a4c51;
}
/* Img hero */

.table tbody .player .player-name shared-hero-icon i {
	border-radius: 50%;
	margin: 5px;
}
/* name */

.table tbody .player .player-name span {
	left: 70px !important;
}
/* height row */

.table tbody .player .player-name {
	height: 60px !important;
}
/* ------------------ TABLES HEROES BREAKDOWN ------------------ */
/* Icon Hero */

core-profile-hero-stats .playtime .hero-container .hero a shared-hero-icon i {
	border-radius: 50% !important;
}
/* performance bar */

core-profile-hero-stats .playtime .hero-container .hero .performance-bar {
	height: 5px !important;
	background-color: #2e3136 !important;
	border-radius: 3px;
}
/* positive */

core-profile-hero-stats .playtime .hero-container .hero .positive .fill {
	height: 5px !important;
	background-color: #43b581 !important;
	border-radius: 3px;
}
/* positive rate % */

core-profile-hero-stats .playtime .hero-container .hero .positive .rate {
	color: #43b581 !important;
}
/* negative */

core-profile-hero-stats .playtime .hero-container .hero .negative .fill {
	height: 5px !important;
	background-color: #f04747 !important;
	border-radius: 3px;
}
/* negative rate % */

core-profile-hero-stats .playtime .hero-container .hero .negative .rate {
	color: #f04747 !important;
}
/* Games Color */

core-profile-hero-stats .playtime .hero-container .hero .games .played .w {
	color: #43b581 !important;
	margin-right: 3px;
}

core-profile-hero-stats .playtime .hero-container .hero .games .played .l {
	color: #f04747 !important;
	margin-left: 3px;
}
/* ------------------ TABLES RECENT TEAMMATES ------------------ */
/* title row */

core-profile-teammates .table tbody tr th {
	color: #999999 !important;
}
/* ------------------ TEXT COLOR ------------------ */

.loss, .d, .tag-worst, .text-status-2 {
	color: #f04747 !important;
}

.average {
	color: #999999 !important;
}

.a {
	color: #7289da !important;
}

.win, .k, .tag-best, .text-status-1 {
	color: #43b581 !important;
}

.kp {
	color: #999 !important;
}
/* Icon Color */

.fa-question-circle:before {
	color: #7289da !important;
}
/* ------------------ CHART JS ------------------ */

.charts, .playtime {
	background-color: #36393e !important;
}
/* ------------------ HIDEN DISPLAY ------------------ */

ggs-ad {
	display: none !important;
}

.heroes {
	display: none !important;
}
/* Text toggle and Heroes*/

.section-header .controls strong {
	display: none !important;
}
/* Info bar */

.fairness {
	display: none !important;
}

.supporter {
	display: none !important;
}
}
