css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wlingua.com") {
  body{
    background-color: #333;
    color: #f3f3f3;
  }
  .menu_top {
    background-color: #3a3a3a;
    border-color: #3a3a3a;
  }
  .portada_barra {
    background-color: #353535;
    border-color: #686868;
  }
  .menu {
    background-color: #303030;
    color: #f3f3f3;
  }
  .menu_datos {
    background-color: #303030;
    color: #eee;
  }
  .menu_datos_nombre {
    color: #29B2C6;
  }
  .menu_datos_type_account_date {
    color: #eee;
  }
  .menu_cat {
    background-color: #424242;
    color: #ddd;
  }
  .menu_opcion {
    background-color: #303030;
    color: #ccc !important;
  }
  .menu_opcion:hover {
    background-color: #363636;
  }
  .row1 {
    background-color: #313131;
    border-color: #686868 !important;
  }
  .row2 {
    background-color: #363636;
    border-color: #686868 !important;
  }
  .portada_leccion_img {
    opacity: 1;
  }

  .viewover_body {
    background-color: #333;
    color: #f3f3f3;
    height: 470px;
    z-index:2;
  }
  .viewover_header {
    background-color: #4185A8;
  }
  .viewover_content {
    height: 424px;
  }
  .reading_title {
    color: #d4dfe1;
  }
  .visor_visor {
    /*color: #A3BABF;*/
    color: #f0f0f0;
  }
  .visor_visor .p {
    line-height: 22px;
  }
  .reading_quiz {
    background-color: rgba(255,255,255,0.075);
    border-radius: 6px;
  }
  .progress_sup_lesson_name {
    color: #59C8D8;
  }
  .progress_sup_bar_item {
    background-color: #888;
  }
  .progress_sup_bar_item_green {
    background-color: #ADC86B;
  }
  .progress_sup_lesson_number {
    color: #f0f0f0;
    background-color: #969696;
  }
  .visor_panel {
    background-color: #404040;
  }
  .panel_titulo {
    color: #59C8D8;
  }
  .panel_normal .panel_titulo {
    border-bottom-color: #707070;
  }
  .visor_conjugation {
    background-color: rgba(255,255,255,0.2);
    border: solid 2px #96B95F;
  }
  .visor_conjugation_title_verb {

  }

  /**
  [widgetid^="app_ejercicios_boton_palabra_frase_]
  **/
  .exercise_letters_group {
    -moz-user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
  }
  .exercise_card {
    background-color: #ddd;
    border: solid 2px #666;
    color: #666;

  }
  .exercise_card[widgetid^=app_ejercicios_boton_palabra_frase_] {
    background-color: #ddd;
    border: solid 2px #666;
    color: #666;
  }
  .exercise_card_active {
    border-color: #00C3E4;
    background-color: #e6f8fb;
  }
  .progress_sup_help {
    color: #CBCBCB;
    background-color: #838383;
  }

  .exercise_card_activated:hover {
    opacity: 0.9;
  }

  .visor_word_gender_m {
    /*color: #B4C0FF;*/
  }
  .exercise_card_idi2 {
    /*color: #EDEDED;*/
  }
  .exercise_card_idi1 {
    /*color: #5FBECE;*/
  }
  /*********************/
  .exercise_card.exercise_card_fail,
  .exercise_card_fail {
    border-color: #FF5C5C;
    background-color: #FCC;
    animation: animPulseRedShadow linear 0.5s;
    animation-iteration-count: infinite;
    transform-origin: 50% 50%;
    -webkit-animation: animPulseRedShadow linear 0.5s;
    -webkit-animation-iteration-count: infinite;
    -webkit-transform-origin: 50% 50%;
    -moz-animation: animPulseRedShadow linear 0.5s;
    -moz-animation-iteration-count: infinite;
    -moz-transform-origin: 50% 50%;
    -o-animation: animPulseRedShadow linear 0.5s;
    -o-animation-iteration-count: infinite;
    -o-transform-origin: 50% 50%;
    -ms-animation: animPulseRedShadow linear 0.5s;
    -ms-animation-iteration-count: infinite;
    -ms-transform-origin: 50% 50%;
  }

	@keyframes animPulseRedShadow{
	  0% { box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	  50% { box-shadow: 0 0 10px rgba(229,184,184,0.0); }
	  100% { box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	}

	@-moz-keyframes animPulseRedShadow{
	  0% { box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	  50% { box-shadow: 0 0 10px rgba(229,184,184,0.0); }
	  100% { box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	}

	@-webkit-keyframes animPulseRedShadow {
	  0% { box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	  50% { box-shadow: 0 0 10px rgba(229,184,184,0.0); }
	  100% { box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	}

	@-o-keyframes animPulseRedShadow {
	  0% { -o-box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	  50% { -o-box-shadow: 0 0 10px rgba(229,184,184,0.0); }
	  100% { -o-box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	}

	@-ms-keyframes animPulseRedShadow {
	  0% { -ms-box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	  50% { -ms-box-shadow: 0 0 10px rgba(229,184,184,0.0); }
	  100% { -box-shadow: 0 0 10px rgba(229,184,184,0.9); }
	}
  /*********************/

  .position_indicator {
    color: #aaa;
    border: none;
    background-color: #515151;
  }
  .wbutton_next {
    background-color: #019BB0;
  }
  .wbutton_prev {
    background-color: #7A7676;
  }
  .visor_conjugation_title_verb {
    color: #6EDCF2;
  }
  .visor_conjugation_tense {
    color: #8AC3CC;
  }
  .visor_conjugation_odd {
    background-color: #F6FBEE1A;
  }
  .visor_conjugation_pronoun {
    color: #6EDCF2;
  }
  .visor_conjugation_verb_reg {
    color: #2CDD2C;
  }
  .visor_conjugation_verb_raiz {
    color: #6EDCE3;
  }
  .p_lang1 {
    color: #33E133;
  }
  .p_lang2 {
    color: #2A9BF0;
  }
  .play_icon_small {
    opacity: .9;
  }

  .visor_idi2 {
    color: #A6DDE9;
  }
  .visor_flag {
    opacity: 0.75;
  }

  .wbutton_download {
    background-color: #666;
  }
  .cuadro_descargar {
    background-color: #666;
    border: 1px solid #999;
  }

  .exercise_sentence_idi2 {
    color: #A9DFE9;
  }
  .exercise_options_word {
    color: rgb(136, 203, 216);
    border: dashed 1px #aaa;
    background-color: rgba(11,135,156,0.15);
  }
  .wbutton_blue {
    background-color: #3F9FB0 !important;
  }
  .exerciseg_negrita {
    color: #888;
  }
  .write_input {
    border: solid 1px #95D5E0;
    background-color: #FFFFFFE6;
  }
  .exercise_choose_option {
    background-color: #e0e0e0;
  }
  .estrella_trans {
    background-color: #333;
  }

  .exercise_ask > .exercise_card_idi1 {
    color: #5FBECE;
  }
  td.visor_table_celda:not(.visor_table_row_odd) {
    background-color: #F1F9E2CC;
  }
  .exercise_ok {
    background-color: #E2F2B1;
    border-color: #E2F2B1;
  }
  .exercise_fail {
    border-color: #FF8383;
    background-color: #FF8383;
    opacity: 0.5;
    animation: animPulseRedShadow linear 0.5s;
    animation-iteration-count: infinite;
    transform-origin: 50% 50%;
    -webkit-animation: animPulseRedShadow linear 0.5s;
    -webkit-animation-iteration-count: infinite;
    -webkit-transform-origin: 50% 50%;
    -moz-animation: animPulseRedShadow linear 0.5s;
    -moz-animation-iteration-count: infinite;
    -moz-transform-origin: 50% 50%;
    -o-animation: animPulseRedShadow linear 0.5s;
    -o-animation-iteration-count: infinite;
    -o-transform-origin: 50% 50%;
    -ms-animation: animPulseRedShadow linear 0.5s;
    -ms-animation-iteration-count: infinite;
    -ms-transform-origin: 50% 50%;
  }

  .exercise_ask > .exercise_card_idi1 {
    color: #5FBECE;
  }
  .exercise_card_img_grey {
    background-color: #bbb;
  }
  td.visor_table_celda:not(.visor_table_row_odd) {
    background-color: #F1F9E2CC;
  }
  .h1, .h2, .h3 {
    color: #65acb9;
  }
  .visor_idi1 {
    color: #84c3a7;
  }
  div[widgetid^=wlib_comun_Wplay_bar_] {
    background-color: #777;
    border-radius: 4px;
  }
  div[data-dojo-attach-point=dp_play_bar] {
    background-color: #97b9b6 !important;
  }

  .buscador {
    background-color: #3A3A3A;
    color: #eee;
  }
  .buscador_buscar input {
    color: #eee;
  }
  .buscador_resultado {
    background-color: #3A3A3A;
    color: #eee;
  }

  .imgm_pie {
    color: #A6DDE9;
  }

  .premium_header {
    background-color: #3A3A3A;
  }
  .premium_payment_desc {
    background-color: #3A3A3A;
  }
  .premium_box {
    background-color: #7a7777;
  }
  .premium_box_price {
    color: #eee;
  }
  .premium_comparativa, .premium_options_price {
    background-color: transparent;
  }
  .premium_option:nth-child(2n+1),
  .premium_option:nth-child(2n) {
    background-color: transparent;
  }
  .premium_option:hover {
    background-color: #467A8C !important;
  }

  div[style="text-align: center; border-bottom: 1px solid #CCCCCC; background-color: #EEEEEE;"],
  div[style="border-bottom: 1px solid rgb(204, 204, 204); background-color: rgb(238, 238, 238); display: block; font-size: 14px; padding: 5px 5px 0px 10px;"] {
    background-color: rgba(255,255,255,0.2) !important;
    border-color: transparent !important;
    padding-top: 4px;
    padding-bottom: 4px;
  }
  /****/
}
