css:
@-moz-document url-prefix("https://fantasy.premierleague.com") {
.ism-element-row--pitch img{
    height:50px;
}
.ism-element-row--pitch{
     padding-bottom: 0rem !important;    
}

 .ism-pitch{
          min-height: 290px !important;
}

.masthead
{
    display:none;
}

.sectionTitle, .ism-alert-wrap, .ism-alert-wrap, .ism-game-header{
    display:none !important;
}

.ism-pitch__unit{
    min-height: 45px !important;
}

.ism-round-pager{
 margin-top: 0rem !important; 
}
.ism-pager, .ism-round-pager{
    margin: 0rem 0 !important;   
}

.ism-pager--round{
    margin-top: -4rem !important;
}

.ism-element-row img{
    height:50px;
}

.ism-copy{
  display:none !important;
}

#aismr-scoreboard{
  overflow:scroll;
    max-height:100px;

}

.ism-scoreboard-points{
        margin-bottom: 0rem !important; 
}

.ism-scoreboard-points__dt {
     display:none !important;
}

.ism-scoreboard__panel--primary{
 	padding-top: 0rem !important; 
    padding-bottom: 0rem !important; 
}

.ism-scoreboard-points__primary
{
    padding-left:0rem !important; 
    padding-right: 0rem !important; 
	margin-top:  0rem !important; 
    margin-bottom:  0rem !important; 
}

.ism-main {
 
        padding-top: 0rem !important; 
}

.ism-scoreboard-points__secondary{
 	
    margin-top:  0rem !important; 
    max-height: 110px;
    
}

.ism-toggle-tabs__list--light-b{
  display:none !important;
}


/* Field */
.ism-squad-wrapper
{
     margin-bottom: 0rem !important; 
    padding-top: 0rem !important; 
}
/* Kit sidebar*/
#ismr-kit, #ismr-fan-league
{
    display: none !important;
}
}
