css:
/* i really want this to be global */

*,
::before,
::after {
    color: #cccccc !important;
    border-color: #cccccc !important;
    text-shadow: none !important;
    box-shadow: none !important;
    -moz-box-shadow: none !important;
    background: #2c2c2c !important;
    background-color: transparent !important;
}
html * {
    color: inherit !important;
}
p::first-letter,
h1::first-letter,
h2::first-letter,
p::first-line {
    color: inherit !important;
    background: none !important;
}

li {
color: #ccc !important;
}


b,
a.result-link {
    color: inherit !important;
}
u,
span[class=link-text] {
    color: #000 !important;
}
strong {
    color: #7c3d42 !important;
}
html,
body,
li ul,
ul li,
table,
header,
article,
section,
nav,
menu,
aside,
[class*="nav"],
[class*="open"],
[id*="ropdown"],
[class*="ropdown"],
div[class*="menu"],
[class*="tooltip"],
div[class*="popup"],
div[id*="popup"],
div[id*="detail"],
div[class*="detail"],
div[class*="note"],
span[class*="note"],
div[class*="description"],
div[class*="content"],
div[class*="container"],
div[style*="display: block"],
div[style*="visibility: visible"] {
    background-color: #2c2c2c !important
}
*:not(:empty):not(span):not([class="html5-volume-slider html5-draggable"]):not([class="html5-player-chrome html5-stop-propagation"]),
*::before,
*::after,
td:empty,
p:empty,
div:empty:not([role]):not([style*="flashblock"]):not([class^="html5"]):not([class*="noscriptPlaceholder"]) {
    background-image: none !important;
}
span:not(:empty):not([class*="icon"]):not([id*="icon"]):not([class*="star"]):not([id*="star"]):not([id*="rating"]):not([class*="rating"]):not([class*="prite"]) {
    background-image: none !important;
    text-indent: 0 !important;
}
html [id*="lightbox"] * {
    background-color: transparent !important;
}
html [id*="lightbox"] img {
    opacity: 1 !important;
}
div[class=lb-outercontainer] {
    border-style: solid;
    border-color: #ff0000 #0000ff;
}
a[class=lb-next],
a[class=lb-prev] {
    color: #758DA1 !important;
    opacity: .10 !important;
    text-indent: 0 !important;
}
a {
    color: #758DA1 !important;
    background-color: inherit !important;
    opacity: 1 !important;
    text-indent: 0 !important;
}
a:link,
a:active,
a:visited {
    color: #758DA1 !important;
}
a:hover {
    color: #ccc !important;
    background-color: #1f1f1f !important;
}
body > div {
    background-color: inherit !important;
}
summary,
details {
    background-color: inherit !important
}
kbd,
time,
label,
.date {
    color: #b5bd68 !important
}
acronym,
abbr {
    border-bottom: 1px dotted !important;
    cursor: help !important;
}
mark,
code,
pre,
samp {
    background-color: #5e5e5e !important;
}
cite,
div[class=quotebox],
blockquote {
    background-color: #404040 !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    background-image: none !important;
    border-radius: 5px !important;
    -moz-border-radius: 5px !important;
    -webkit-border-radius: 5px !important;
    text-indent: 0 !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    background-color: #2c2c2c !important;
}
h1,
h2 {
    color: #3d7c77 !important;
}
h3,
h4 {
    color: #427c3d !important;
}
h5,
h6 {
    color: #773d7c !important;
}
table table,
th,
caption {
    background: #2c2c2c !important;
}
::selection {
    color: #ccc !important;
    background: #1f1f1f !important;
}
button,
option,
optgroup {
    color: #c5c8c6 !important;
    background: none #303030 !important;
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
}
textarea,
input {
    color: #c5c8c6 !important;
    background: none #383838 !important;
}
input,
textarea,
button {
    border-color: #1a1a1a !important;
    border-width: 1px !important;
}
input[type="button"],
input[type="submit"],
input[type="reset"],
button {
    border-color: #000000 !important;
    outline-color: #000000 !important;
    background: #3d3d3d !important;
}
input[type="button"]:hover,
input[type="submit"]:hover,
input[type="reset"]:hover,
button:hover {
    border-color: #808080 !important;
    color: #cccccc !important;
    background: #242424 !important;
}
