@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plemiona.pl") {
#group_popup{
	width: 20.5vw!important;
	z-index: 40!important;
	height:100vh!important;
	top:14px!important;
	left: -15.5vw!important;
	transition: left 0.4s;
}
	#group_popup:hover{
		transition: 0.4s;
	z-index: 40!important;
			left: 0!important;
}
#group_list_content{
		height:calc(100vh - 120px)!important;
	
	}
#group_popup_content{
		height:100vh!important;
	
	}
}
