    position: relative;
    top: -9px;
}
.Handle a {
    display: block;
    margin: auto;
    height: 14px;
    background: #C1C1C1;
    width: 1097px;
    text-indent: -9999px;
    cursor: ns-resize;
}
.TableSmall tbody td span.Direction.Incoming .fa-long-arrow-left,
.DataTable tbody td span.Direction.Incoming .fa-long-arrow-left {
    position: absolute;
    left: auto;
