css:
@-moz-document domain("cda.pl") {
body {
    background: black !important;
    overflow-x:hidden;
    width:1270px;
    height:auto;
    font-family:Courier, 'Courier New', monospace;
}
div,a,td,tr,th,font,span{font-family:Courier, 'Courier New', monospace;}
.naviLogin{z-index:9999;}
.mrg-region-video{z-index:500;Width:100%;}
.center-div,.wrapper-container{display:none!important}
.button-default, .btn-mono-top-inl, .bttn-default, .edit-btn {
    border-radius: 1px !important;
    background-color: #424242;
    border-color: grey;
    color: #ffffff;
}

.head-top, .topheader-center {
    background: none;
    background-color: black;
    -webkit-box-shadow: 1px 2px 3px rgba(0, 0, 0, .4), inset 0 1px 1px rgba(255, 255, 255, .2);
    -moz-box-shadow: 1px 2px 3px rgba(0, 0, 0, .4), inset 0 1px 1px rgba(255, 255, 255, .2);
    box-shadow: 1px 2px 3px rgba(0, 0, 0, .4), inset 0 1px 1px rgba(255, 255, 255, .2);
    border: 1px solid grey;
}

.pg-wrap .pg-cc, .block-content, .m_cc {
    background-color: #212528;
    -webkit-box-shadow: 1px 2px 3px rgba(0, 0, 0, .4), inset 0 1px 1px rgba(255, 255, 255, .2);
    -moz-box-shadow: 1px 2px 3px rgba(0, 0, 0, .4), inset 0 1px 1px rgba(255, 255, 255, .2);
    box-shadow: 1px 2px 3px rgba(0, 0, 0, .4), inset 0 1px 1px rgba(255, 255, 255, .2);
    border: 1px solid grey;
    background-image: -webkit-linear-gradient(bottom, #1a1b1f, #212528);
    background-image: -moz-linear-gradient(bottom, #1a1b1f, #212528);
    background-image: -o-linear-gradient(bottom, #1a1b1f, #212528);
    background-image: -ms-linear-gradient(bottom, #1a1b1f, #212528);
    background-image: linear-gradient(to top, #1a1b1f, #212528);
    margin: 0 0px;
}

.menu-default ul li a .m_cc {
    margin: 0 2px;
}

.pg-wrap .pg-hd {
    height: 1px;
    position: relative;
}

.cover-description {
    background: none !important;
    text-align: center;
    display: block;
    color: #aaa;
    font-size: 11px;
    margin-bottom: 1px;
    padding: 5px;
}

.block-default .block-footer, .block-default .block-md-head, .block, .gry_spolecznosciowe, .smoke, .block-lg-head, .panel-footer, .panel-primary, .container, .paginationControl, .commentBorder, .small-profil-user,
.well, .media-heading, .well-default, span.btm, .smfooter, .caption-gallery, .graContainer, .pg-glue-menu, #center-wyl, .center-resp {
    background: black !important;
}
.pg-wrap .pg-cc, .block-content, .m_cc{    background: black !important;    background-image: black !important;border:1px solid grey;}
.block-default .block-container, .container, img.profile, .thumb-mini, .small-profil-user, .well, .big-cover {
    border: 0px !important;
}
.block.ostatnie_komentarze_video{margin-top:10px;}

/*----------fix----------*/

.naviLogin {
    background-color: #1e2125
}
.btn.btn-primary.block {
    background: url("http://static1.cda.pl/img/elements/bg-primary-btn-32.png?t=4") repeat-x center center #be3c0c !important;
}
.second-top {
    margin-top: -2px;
}
.counter-people {
    background: url(http://s9.postimg.org/rr1lwv1mn/ico_peoples2.png) no-repeat 0 0 transparent;
}
.cloudStatBt {
    background-color: black;
    border: 1px solid grey;
    border-radius: 10px;
}
.cloudStatBt .clStBtGray {
    background-color: black;
    color: #212121;
}
.header {
    padding: 42px !important;
    height:50px!important;
}
.adBoxRight {
    width: 0px;
}


/*----------OUT----------*/

div.pser-kob-pparw, .bok-right-fix, .bok-right, .bt, .rl, .rr, .tp, .top, .bottom, .m_tt, .m_rr, .m_rl, #ceneoaffcontainer34184, #gora > div.videoContainer-kas > div.rigthWrapColumn > div > div.block-container > div.block-footer > div:nth-child(3), .top-anonco-content, center#r_nad_menuu, .ads, .f-img, .pb-logo-premium, .arr-use-filter2,.kd_ns_ads,.bn-premium-white,#ostatnio_skom,.well-lite,.block-just,#r_p,.partnerzy,.second-top,div.block-content.filmy_darmowe.block-content-free {
    display: none !important;
}
.wrapp-resp,.wrapper{width:100%!important;}
#leftCol{width:800px!important}
#rightCol{border:1px solid grey;
height:700px!important;
    width:400px!important;
overflow-y: scroll;overflow-x: hidden;
background:black!important;}
.ostatnie_komentarze_video{
    border:1px solid #3381FF;
    height:400px!important;
    width:798px!important;
overflow-y: scroll;}
[style^="padding-left:7px;padding-bottom:10px"],.nfooter,[href^="?c"]{display:none}



[id^="mediaplayer"]{Width:780px!important;height:500px!important;}
div.media-show{background:black;border:1px solid grey;height:82px!important;width:385px!important;padding-top:3px;padding-bottom:3px;}
.head-top{height:35px}
.pb-player-content, .pb-player-html .pb-video-active .pb-bar-no-seeking .pb-playing {height:500px!important;}
.pb-video-player-wrap,.pb-video-player {min-height:485px!important;}
