css:
@-moz-document domain("tiss.tuwien.ac.at") {
/* variables */
html {
    --background-color: rgb(67, 72, 88);
    --window-color: rgb(77, 82, 98);
    --shadow-color: rgb(57, 62, 78);
    --text-color: rgb(225, 225, 225);
    --header-color: #393e4e;
    --headerBorder-color: #252525;
    --info-color: #f1f8fc;
    --lehre-color: #ce8630;;
}

.lehre a {
    color: var(--lehre-color);
}

/* remove img */
#header,
#glow_left,
#glow_right,
#headerInner,
#footerShadow,
#footerShadow-right,
#footerShadow-left
{ background: none; }

/* make logo dark */
#tuLogo { mix-blend-mode: multiply; }
.staticInfoMessage{ background-color: var(--info-color) }

/* right side nav */
.lehre #contentWrapper, 
.forschung #contentWrapper,
.organisation #contentWrapper
{ background: var(--header-color) top left repeat-y; }

#mainNav {
    padding-bottom: 30px;
    /* z-index: 1; */
}

html, .groupWrapper fieldset, .groupWrapper, .nodeTable-level-2 {
    background: var(--background-color);
}
#paperHead {
    border-bottom: 1px solid var(--shadow-color);
    background: var(--header-color);
}
.canceledCourse, .canceledCourse a {
    color: #9e9e9e !important;
}

/* content */

#content, #pageEnd, .ui-widget-content, 
#wrapper .ui-outputpanel fieldset, 

fieldset.styledFieldset legend, fieldset.tableFilter legend,
fieldset:not(.narrow), .course, .item {
    background: var(--window-color) !important;
}
/* table */
.ui-datatable thead th.ui-state-default, 
.ui-datatable thead td.ui-state-default, 
.ui-datatable tfoot td.ui-state-default,
table.standard thead tr th 
{ background: var(--background-color) !important; }

.ui-datatable .ui-datatable-even,
table.standard tbody tr td, 
table.standard tbody tr th 
{ background: var(--window-color); }

.ui-datatable .ui-datatable-odd { background: var(--background-color); }
.ui-datatable .ui-widget-content,
.ui-datatable thead th.ui-state-default, 
.ui-datatable thead td.ui-state-default, 
.ui-datatable tfoot td.ui-state-default,
table.standard tbody tr td, 
table.standard tbody tr th,
table.standard thead tr th
{ border: 1px solid var(--shadow-color) !important; }

.ui-state-default, .ui-datatable th { box-shadow: none !important; }

/* nav */
.groupWrapper {
    border-bottom: 1px solid var(--shadow-color);
}

#headerLeftInside {
    border-bottom: none;
}
#supNavHeading {
    z-index: 1;
}
.organisation #supNavHeader {
    background: none;
}
#headerInnerWrapper,
#supNavBody,
#headerLeft,
#footerContent,
#footer-left,
#footer-right {
    background: var(--header-color);
}
#footer {
    margin: 0 10px 0px 10px;
    background: none;
    z-index: -1;
}
#pageEnd {
    margin-right: 41px;
}

/* move dialog */
.ui-dialog { border-bottom: none; }
.ui-dialog .ui-dialog-titlebar .ui-dialog-title,
.ui-dialog .ui-dialog-titlebar {
 text-shadow: none;
    border: none;
    border-bottom: 1px solid var(--header-color);
    color: var(--text-color);
    background-color: var(--background-color);
    background-image: none;
}
.ui-dialog .ui-dialog-buttonpane:after, .ui-dialog .ui-dialog-content:after {
 	background: none;   
}

.ui-dialog .ui-dialog-content {
    background-color: var(--window-color);
    border: 1px solid var(--header-color);
    -webkit-box-shadow: 5px 5px 20px 0 var(--header-color);
    -moz-box-shadow: 5px 5px 20px 0 var(--header-color);
    box-shadow: 5px 5px 20px 0 var(--header-color);
}
.ui-widget-content {
 	color: white;   
}

#settings fieldset {
 	background: var(--window-color); 
}
.lehre .styledCommandBox {
    background: none;
}

/* text */
#wrapper .supNavMenuHeader,
#supNav .linkBlock a,
html,
#content h2,
#content h1,
legend,
fieldset:not(.narrow) li,
.ui-datatable thead th.ui-state-default,
.ui-datatable .ui-widget-content,
.ui-datatable tfoot td.ui-state-default,
.ui-state-default,
.ui-datatable th,
table.standard thead tr th,
.course,
.item {
    color: var(--text-color) !important;
    text-shadow: none;
}



}
