css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("developpez.com") {

  /* Body */
  body{
    background: #222;
  }
  
  /* Header */

  /* End Header */
  
  
  /* Section */
  .contenuPage{
    background: #222;
    
  }
  /* End Section */
  
  /* End Body */
  
  /* Gabarit */
  #gabarit_superieur a{
    color: #ccc;
  }
    #gabarit_superieur{
    background: #333;
  }
  #gabarit_menu, #gabarit_barre2,#gabarit_responsable{
    color: #ccc;
    background: #333;
    
  }
  #gabarit_responsable a,#gabarit_copyright{
    color: rgb(64, 120, 192);
  }
  .gabarit_sousmenu li{
        color: #ccc;
    background-color: #333 !important;
  }
  .gabarit_sousmenu{
    color: #ccc;
    background-color: #333 !important;
  }
  .gabarit_sousmenu li a{
    color: #ccc;
    
  }
  #gabarit_menu > li a.gabarit_selection, #gabarit_menu > li > a:hover, #gabarit_menu > li:hover > a{
    background: #111;
    color: #ccc;
  }
  /* End Gabarit */
  
  /* Header A la une */
  
  /*.annonceLink, .annonceLink:link{
    color: rgb(64, 120, 192);
    
  }*/
  
  div.contenuPage{
    color: #ccc;
  }
  
  h2.alauneType, h1.alauneType{
    background: #111;
    letter-spacing:0px;
      border:1px solid gray;
    border-bottom:0px; 
  }
  
  .alaune{
    background: #222;
    border:1px solid gray;
    border-top:0px; 
  }
  .alaune2{
    background: #333;
    border:1px solid gray;
    
  }
  .alauneTitle a,a.alauneSuite{
    color: rgb(64, 120, 192);
    
  }
  .alauneTitle a:hover{
    text-decoration: underline;
    
  }
  .alauneContent{
    color: #ccc;
  }
  
  .alaune .annonce table{
    background: #333;
    
  }
  .annonce{
    border-bottom: 1px solid gray;
    padding : 0px;
  }
  .alaune .annonce table td{
    padding: 2px;
  }
  .annonceLink, .annonceLink:link{
    color: #aaa;
  }
  .annonceLink, .annonceLink:link:hover{
    color: rgb(64, 120, 192);
  }
  /* end Header A la une */
  
  /* Article */
  .actuTitle{
    color: #ccc;
    
  }
  .date{
    color:gray;
  }
  .content{
    color:#bbb;
  }
  
  /* End Article */
  
  /* Left panel */
  .voiraussiTitle{
    background: #333;
    border-style:solid;
   border-color:gray;
    border-bottom : 0px;
      border-width: 1px;
  }
  .voiraussi{
    background: #333;
    border-top : 0px;
   border-color:gray;

  }
  #colonneDroite div.alaune,.cat_content{
    background: #333;
    
  }
  .cat_content li{
    margin: 0px 0px;
  }
  .cat_content a{
    color:#bbb;
    
  }
  h3.alauneType{
    background:#222;
    color:#bbb;
    border:1px solid gray;
    margin-top:3px;
  }
  .groupbox{
    background: #333;
    border-width:0px 3px 3px 3px;
    border:1px solid gray;
  }
  #topXcontents .bestofItem:hover{
    background: #444;
    border:1px solid #555;
  }
  /* end left panel */
  
  /* User */
  .postUser{
    background: #333;
  }
  .postComment{
    color: #ccc;
    
  }
  .postMessage{
    color: #ccc;
  }
  /* End User */
  
  /* Pagination */
  .pagination .currentPage, .pagination a:hover{
    background-color:#222;
  }
  .pagination a{
    background:#333;
    color:#ccc;
    border:1px solid #ccc;
  }
  /* End Pagination */
  
  /* Adds */
  #header > div:nth-of-type(3) > div:nth-of-type(2){
    display: none;
  }
  /* End adds*/
}
