css:
@-moz-document domain(vk.com) {
body {
    background: url(https://cdn1.savepice.ru/uploads/2017/12/1/84a5a3dedd3b6c017f1966dea22583f1-full.jpg) #020202 no-repeat fixed;
    background-size:100%;
}

.ads_left, div[id*=ads_ads_wrap], ._ads_promoted_post, #stories_feed_wrap, ._ads_promoted_post_data_w {
 display:none !important;   
}
.post:hover, #submit_post:hover, .post_field_wrap:hover, #post_field:hover, #submit_post_box:hover {
    background: #edf0f5;
}
.wall_module .reply_fakebox {
 background:none;   
}
.wr_header {
    background: #c12424;
    color: white;
    font-weight: bold; 
}
.wall_module .reply_box, .wall_module .reply_fakebox_wrap {
    background:none;
    background: rgba(226, 224, 255, 0.2);
}
#page_header_cont .back {
    background:linear-gradient(to bottom, rgba(0,0,0,0.2), rgba(0,0,0,0.5), rgba(0,0,0,0.7), rgba(0,0,0,0.8), rgba(0,0,0,0.7), rgba(0,0,0,0.6), rgba(0,0,0,0.3)) !important;
}
#side_bar ol li .left_row {
    color: #e6e1e1;
    
}

#side_bar ol li .left_row:hover {
    color: #dfe2ff;
}
div[id*="yandex"]{
    display:none !important;
}
div[id*="ads"], .stories_feed_not_nav_buttons{
    display:none !important;
}
.nim-dialog.nim-dialog_unread-out.nim-dialog_muted:not(.nim-dialog_failed) .nim-dialog--unread, .nim-dialog.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread {
    background: rgba(255, 0, 0, 0.9);
}
.stl_active.over #stl_bg, .stl_active:hover #stl_bg {
 	opacity:0.7;   
}

#im-page, .page_block, .box_layout, #pv_box {
   -webkit-box-shadow: 0px 0px 32px 1px rgba(255,255,255,1);
	-moz-box-shadow: 0px 0px 32px 1px rgba(255,255,255,1);
	box-shadow: 0px 0px 32px 1px rgba(255,255,255,1);
}
.ui_rmenu_item:hover, .ui_rmenu_subitem:hover, .ui_rmenu_item_sel, .ui_rmenu_item_sel:hover{
    background-color: #98bdd0;
    color:#000;
}
.post_author .author, .wall_module .author, .wall_module .copy_author{
    font-weight: 800;
}
.button_gray button, .flat_button.secondary {
    background-color: #000;
    color: #dcdcdc;
}

.button_gray button:hover, .flat_button.secondary:hover {
 	color: #fff;  
     background-color: #2f3235;
}
.media_selector .ms_item:before {
    background: url(http://wwwtest.ru/images/post_icon.png) no-repeat;
}
.wall_module .post_like_icon, .wall_module .post_share_icon {
    background: url(http://wwwtest.ru/images/post_icon.png) no-repeat 0 0;
}
.button_blue button.hover, .button_blue button:hover, .flat_button.hover, .flat_button:hover {
 	background:#2f3235;   
}
.button_blue button, .button_blue button, .flat_button, .flat_button {
     background:#000; 
}
#side_bar_inner {
 	position:fixed !important;
    margin-top: 42px !important;
}
.top_audio_player:hover, .top_nav_link:hover, .top_nav_btn:hover {
    background-color: #2f3235;
}
input.text.ts_input {
    background: #2f3235 url(http://vk.com/images/icons/dev_head_magglass.png) no-repeat;
    background-position: left 8px;
}
.fc_tab_head {
 	background-color: #000000;
    border: 1px solid #000000;   
}
#side_bar ol li .left_row:hover {
       background: rgba(0,0,0,.5);
    color:#fff;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected:before, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected:before {
    content:"" !important;
    display:block !important;
    position:absolute !important;
    height:0 !important;
    width:0 !important;
	border: 32px solid transparent !important;
	border-right: 30px solid #7293b6 !important;
    left:-45px !important;
    z-index:1000 !important;
}
div[data-ad-view*='-']{
 	display:none;   
}
.wall_copy_more, .wall_post_more, .wall_reply_more {
    font-weight: 900;
    opacity: 1;
}
.post_author .author, .wall_module .author, .wall_module .copy_author {
 	color: #000;   
}
.box_title_wrap {
 	background-color: #000000;   
}

.ts_wrap .input_back_content {
    color: #ffffff;
}
.audio_page_player .audio_page_player_btns .icon, .audio_page_player .audio_page_player_next .icon, .audio_page_player .audio_page_player_prev .icon, .audio_page_player .audio_page_player_play .icon {
    /*background-image: url("http://wwwtest.ru/images/audio_player_controls.png");*/
}
.audio_inline_player .slider .slider_amount, .audio_page_player .slider .slider_amount, .audio_inline_player .slider .slider_handler, .audio_page_player .slider .slider_handler {
 	background:#000;   
}
.audio_page_player .audio_page_player_play .icon, .audio_play {
 	background-color:#000;   
}
.left_menu_nav {
 	color:#000;   
}
.audio_page_player .audio_page_player_btns .icon {
 opacity:0.15;   
}
.chat_tab_wrap {
    display: inline-block;
}
#stl_left {
	margin-top: 0px;
    margin-right: calc((100% - 1140px)/2);
    margin-bottom: 20px;
    margin-left: auto;
	right: 0;
	top: inherit !important;
}
#stl_bg {
	height: 30px !important;
    width: 84px !important;
    padding-top: 0 !important;
	border-radius: 30px;
	float: right;
}
#stl_text {
	line-height: 30px;
    background: none !important;
	color: #000000;
	padding:0 !important;
	margin-left: 19px;
}
.fakeinput, div[contenteditable=true], input.big_text, input.dark, input.search, input.text, textarea {
    background:none !important;
}
.page_block {
    background: rgba(255, 255, 255, 0.79);
}
.im-page .im-page--history-new-bar, .profile_info_header, .im-page .im-page--history-new-bar>span {
    background:none;
}
.im-mess.im-mess_unread {
    background: rgba(7, 9, 32, 0.2);
    color: #fff;
}
.im-page .im-page--center-empty {
    background: url(http://icon-icons.com/icons2/806/PNG/128/chat-33_icon-icons.com_66016.png) transparent no-repeat top;
    background-size: 45%;   
}
.im-mess_unread * {
    color: #696969 !important;
    filter: grayscale(100%);
}
#page_header_cont .back {
    border: 0;
}
#top_nav input[type=text] {
    color: #fff;
}
.feed_blog_reminder {
    display: none;
}
.side_bar.fl_l {
    top: 0 !important;
}
}
