css:
@-moz-document url-prefix("https://secure.php.net/manual/"), url-prefix("http://php.net/manual/") {
/* font style for main article */

#layout-content {
    font-family: 'Yu Mincho', "YuMincho", "georgia", serif;
    font-weight: bolder;
    line-height: 1.25em;
    letter-spacing: 1px;
    color: white;
}
/* end of font style*/
/* you can change header's color */

nav#head-nav {
    background-color: #00bfff;
}
/* example:

#head-nav {
  background-color: red;
}

#head-nav {
  background-color: #d7b740;
}



*/
/* end of the header's color */

a:link {
    text-decoration: none;
}
.navbar {
    border-style: none;
}
#head-nav {
    height: 55px;
    display: block;
    margin: 0px;
    z-index: 10001;
}
ul.nav li {
    list-style: none;
    margin: 0px;
}
ul.nav li a {
    color: black;
    text-shadow: none;
}
ul.nav li a:hover {
    color: black;
}
ul.nav li.active a {
    color: white !important;
    text-shadow: none !important;
    background-color: black !important;
    box-shadow: none !important;
}
#top-search {
    height: 50px;
    margin: 0px;
    padding: 0px;
}
div.headsup {
    background-color: black;
    padding-top: 10px;
    padding-bottom: 2rem;
    margin: 0px;
    top: 0px;
    position: relative;
    display: block;
    border: none;
}
div.headsup a {
    color: #00bfff;
    border-style: none;
    font-family: "meiryo", "arial", sans-serif;
    font-weight: bolder;
}
#layout-content {
    background-color: black !important;
    position: relative;
    padding-top: 50px;
    top: 0px;
}
#layout-content:not(:only-child) {
    border-right: 0px solid black;
}
#layout-content .action a img,
div.foot a img {
    background-color: white;
}
#layout-content .page-tools + .sect1 {
    padding-top: 1em;
}
.info {
    padding-bottom: 1em;
}
h1,
h2,
h3 {
    font-family: "meiryo", "arial", sans-serif;
}
.example-contents {
    font-weight: normal;
}
#breadcrumbs a {
    font-family: "meiryo", "arial", sans-serif;
}
#breadcrumbs a:link {
    color: #00bfff;
}
#breadcrumbs a:hover {
    color: #336699;
    text-decoration: underline;
}
#breadcrumbs a:visited {
    color: #999999;
}
.layout-menu {
    opacity: 0.6;
}
.layout-menu:hover {
    opacity: 1;
}
.parent-menu-list > li {
    list-style: none;
}
.parent-menu-list > li > a {
    margin-bottom: 1rem !important;
    font-weight: bolder;
}
.parent-menu-list li ul a {
    border-style: none !important;
}
.parent-menu-list a {
    font-family: "meiryo", "arial", sans-serif;
}
.parent-menu-list a:link {
    color: #fff;
}
.parent-menu-list a:hover {
    color: #999999;
    text-decoration: underline;
}
.parent-menu-list a:visited {
    color: #999999;
}
.parent-menu-list li.current a {
    color: #00bfff!important;
}
.parent-menu-list .child-menu-list li {
    padding-bottom: 0.8em;
}
.parent-menu-list .child-menu-list li a {
    border-style: none;
}
header.title,
h1:after,
h2:after,
h3:after {
    border-style: none !important;
    display: block;
}
body {
    background-color: black;
}
pre {
    line-height: 1em;
}
li {
    list-style-type: disc;
}
ul.chunklist_book li,
ul.chunklist_set li,
#layout-content ol li {
    margin-top: 1em;
}
ul.chunklist_children,
ul.itemizedlist li {
    padding-top: 0.3em;
}
ul.chunklist_children li {
    margin-top: 0.2em;
    list-style-type: none;
}
.title,
h1 {
    color: #00bfff !important;
}
h2 {
    color: #00bfff !important;
}
h1,
h2,
h3 {
    margin-top: 2.5em;
    margin-bottom: 1em;
}
h1,
h2 {
    font-weight: normal;
}
#layout-content a {
    text-decoration: none;
    border-style: none;
    font-weight: normal;
}
#layout-content a:not(.genanchor):link {
    color: #00bfff !important;
}
#layout-content a:not(.genanchor):hover {
    color: #336699!important;
}
#layout-content a:not(.genanchor):visited {
    color: #cc33cc;
}
#layout-content a.genanchor:hover {
    color: #00bfff !important;
}
#layout-content strong {
    font-family: sans-serif;
    font-weight: bold !important;
}
#layout-content blockquote.note,
#layout-content .warning,
#layout-content div.tip,
#layout-content .caution,
#layout-content div.example-contents div,
#layout-content .inicode {
    background-color: #222;
    color: white;
}
#layout-content div.text,
#layout-content div.example-contents,
#layout-content .phpcode {
    background-color: #222!important;
}
#layout-content div.text .phpcode .html,
#layout-content div.example-content .phpcode .html,
#layout-content .phpcode span[style="color: #000000"] {
    color: white !important;
}
#layout-content * span[style="color: #0000BB"] {
    color: #3333ff !important;
}
#layout-content span[style="color: #007700"] {
    color: #11cc11 !important;
}
#layout-content div.example-contents p {
    background-color: black;
}
.doctable,
.informaltable {
    color: black;
}
.doctable > caption,
.informaltable > caption {
    color: white;
}
.classsynopsis,
.methodsynopsis {
    background-color: #222!important;
}
.classsynopsis span[style=" #000000"],
.methodsynopsis span[style=" #000000"],
.classsynopsis,
.methodsynopsis {
    color: white;
}
#layout-content .classname,
#layout-content strong.classname {
    color: #6666ff !important;
    font-weight: normal !important;
    font-family: "Fira Mono", "Source Code Pro", monospace!important;
}
#layout-content .varname {
    background-color: #333;
    color: white;
}
#layout-content .methodname,
#layout-content .methodname * {
    color: #aa00aa !important;
    font-family: "Fira Mono", "Source Code Pro", monospace!important;
    font-weight: normal !important;
}
.modifier {
    color: #ffd700 !important;
}
.parameter {
    color: #228b22!important;
    font-weight: normal!important;
}
.type {
    color: #e00 !important;
}
div.text {
    opacity: 1 !important;
}
#layout-content .cdata {
    background-color: #222;
    color: white;
}
#usernotes {
    margin-top: 6em;
}
#usernotes h3 {
    padding-top: 0em;
}
#usernotes .votes div.tally,
#usernotes strong.user em {
    color: #ccc !important;
}
.footmenu li a:link {
    color: #999999;
}
.footmenu li a:hover {
    color: #999999;
    text-decoration: underline;
}
.footmenu li a:visited {
    color: #cccccc;
}
/*  adjutifying the position of feedly popup */

#feedly-mini,
#feedly-mini-popup,
#feedly-mini-popup-arrow {
    margin-bottom: 44px !important;
    margin-right: 4px !important;
}
#toTop img {
    background-color: white;
    border-radius: 20px;
}
/* for table */

#layout-content table.doctable,
#layout-content table.doctable > *,
#layout-content table.doctable > * > tr,
#layout-content table.segmentedlist,
#layout-content table.segmentedlist> *,
#layout-content table.segmentedlist > * > tr {
    border: #666;
}
#layout-content .doctable,
#layout-content .segmentedlist {
    color: white;
}
#layout-content .doctable th,
#layout-content .segmentedlist th {
    background-color: #99a;
    color: black;
}
#layout-content table.doctable > * > tr > *,
#layout-content table.segmentedlist > * > tr > * {
    padding: 6px 10px;
}
#layout-content .doctable tbody tr:nth-child(odd),
#layout-content .segmentedlist tbody tr:nth-child(odd) {
    background-color: #1a1a1a;
}
#layout-content .doctable tbody tr:nth-child(even),
.doctable tbody tr:nth-child(even),
#layout-content .segmentedlist tbody tr:nth-child(even),
.segmentedlist tbody tr:nth-child(even) {
    background-color: #222!important;
}
}

@-moz-document regexp("https?://(secure\\.)?php.net/manual/[a-zA-Z^/]{1,6}/resource.php") {
/* closing and opening aside menu for some pages for fine layout */

.layout-menu:after {
    content: "▼　Open Menu list";
    display: block;
    color: white;
}
.layout-menu {
    background-color: black !important;
    opacity: 1 !important;
    display: block;
    height: 320px;
    position: relative;
    z-index: 10000;
}
.layout-menu ul::-webkit-scrollbar {
    background: black;
}
.layout-menu ul::-webkit-scrollbar-thumb {
    background: #666;
}
.layout-menu > ul {
    display: none;
}
.layout-menu:hover > ul {
    display: block;
    overflow-y: scroll;
    height: 300px;
}
.layout-menu:hover:after {
    display: none;
}
}
