css:
@-moz-document url-prefix("http://www.roblox.com/"), 
url-prefix("https://www.roblox.com/"), 
url-prefix("https://forum.roblox.com/"), 
url-prefix("http://forum.roblox.com/"), 
url-prefix("http://web.roblox.com/"), 
url-prefix("https://web.roblox.com/") {
@keyframes expand {
    0% {width: 30px; opacity: 0.2; background-color: transparent; border: 0px solid rgba(/*[[Mcolor-rgb]]*/,1);}
    10% {width: 30px; opacity: 0.6; background-color: transparent;}
    50% {width: 30px; opacity: 0.6; background-color: transparent; border: 0px solid rgba(/*[[Mcolor-rgb]]*/,1);}
    100% {width: 120px; opacity: 1; background-color: rgba(/*[[Mcolor-rgb]]*/, 1); border: 5px solid rgba(/*[[Mcolor-rgb]]*/, 1);}
  }

@keyframes filterSwap {
    0% {filter: brightness(0) grayscale(2);}
    50%{filter: brightness(0) grayscale(2);}
    75%{filter: brightness(500000) grayscale(2);}
    100%{filter: brightness(1000000) grayscale(2);}
}
@keyframes showerInfo{
    0% {background: rgba(/*[[Mcolor-rgb]]*/,0); color: rgba(255,255,255,0);}
    10% {background: rgba(/*[[Mcolor-rgb]]*/,0.1); color: rgba(255,255,255,0.1);}
    50% {background: rgba(/*[[Mcolor-rgb]]*/,0.1); color: rgba(255,255,255,0.1);}
    100% {background: /*[[Mcolor]]*/; color: white;}
}
@keyframes RobloxAnim{
    0% {filter: brightness(0.8) sepia(0) hue-rotate(0) saturate(0);}
    10% {filter: brightness(0.9) sepia(10) hue-rotate(0deg) saturate(100);}
    80% {filter: brightness(1) sepia(10) hue-rotate(290deg) saturate(100);}
    100% {filter: brightness(1) sepia(10) hue-rotate(290deg) saturate(200);}
}
.dialog-container .dialog-header, .chat-main .chat-header, .chat-friend-list .chat-party-avatar, .dialog-container .dialog-member-header .group-member .group-leader, body .forum-table-header, body .forum-table-header th, body .forum-table-footer{
	background-color:/*[[Mcolor]]*/ !important;
}
.dialog-container .dialog-message-container .dialog-message{
	background-color: /*[[Bcolor]]*/;
	color: #000 !important;
}
.dialog-container .dialog-message-container.message-cluster-master .dialog-triangle:before{
	border-color: transparent transparent transparent /*[[Bcolor]]*/;
}
.dialog-container .dialog-message-container.message-inbound .dialog-message{
	color: grey !important;
}
.dialog-container .dialog-message-container.message-inbound .dialog-triangle:before{
	border-color: transparent #fff transparent transparent;
}
.btn-secondary-xs, .btn-primary-lg, .btn-secondary-md, .notification-blue, .btn-primary-sm, .btn-primary-xs, .btn-secondary-sm{
	background-color: /*[[Mcolor]]*/ !important;
    border-color: /*[[Mcolor]]*/ !important;
    transition: background-color 0.2s, border-color 0.2s, border-radius 0.2s;
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading, .rbx-tabs-horizontal .rbx-tab-heading:hover, .rbx-tabs-horizontal .rbx-tab-heading:focus, .rbx-tabs-horizontal .rbx-tab-heading:active{
	box-shadow: 0 -4px 0 0 /*[[Mcolor]]*/ inset;
}
.dropdown-menu li:hover, .notification-stream-list .notification-stream-item:hover, .messages .messageDivider:hover, .menu-vertical .menu-option:hover, .menu-vertical .menu-option:focus, .menu-vertical .menu-option.active, .menu-vertical .menu-option .menu-secondary-option:hover, .menu-vertical .menu-option .menu-secondary-option:focus, .menu-vertical .menu-option .menu-secondary-option.active{
	box-shadow: 4px 0 0 0 /*[[Mcolor]]*/ inset;
    transition: box-shadow 0.2s, background-color 0.2s
}
.btn-secondary-xs:hover, .btn-primary-lg:hover, .btn-secondary-md:hover, .notification-blue:hover, .btn-primary-sm:hover{
	background-color: /*[[Bcolor]]*/ !important;
    transition: background-color 0.2s, border-color 0.2s, border-radius 0.2s;
    border-radius: 20px !important;
}
.btn-primary-lg:hover{
    background-color: /*[[Mcolor]]*/ !important;
    color: white !important;
    border: 10px solid /*[[Mcolor]]*/ !important;
	border-radius: 50px !important;
}
.btn-primary-lg{
    background-color: white !important;
    color: /*[[Mcolor]]*/ !important;
    border: 10px solid white !important;
	transition: border-radius 0.2s, color 0.15s, background-color 0.2s, border 0.2s !important;
}

.container-fluid{
    border-bottom: 2px solid /*[[Mcolor]]*/ !important;
    background-color: white !important;
    transition: border 0.2s;
}
.container-fluid:hover{
    border-bottom: 5px solid /*[[Mcolor]]*/ !important;
    transition: border 0.1s;
}
.rbx-header .rbx-navbar li .nav-menu-title{
	color: /*[[Mcolor]]*/ !important;
    border-radius: 0px;
    transition: background-color 0.2s, color 0.15s, border-radius 0.2s;
}
.rbx-header .rbx-navbar li .nav-menu-title:hover{
	color: white !important;
    background-color: /*[[Mcolor]]*/ !important;
    border-radius: 20px;
    transition: background-color 0.2s, color 0.15s, border-radius 0.2s;
}
.icon-logo, .icon-logo-r{
	filter: brightness(0.8);
    transition: filter 1s;
}
.icon-logo:hover, .icon-logo-r:hover{
	filter: brightness(1) sepia(10) hue-rotate(290deg) saturate(200);    
	animation-name: RobloxAnim;
	animation-duration: 1s;
}
.icon-default-navigation, .icon-search, .icon-settings, .icon-edit, .icon-inactive, .icon-male, .icon-female, .icon-page, .icon-brokenpage, .icon-audio, .icon-moreinfo, .icon-play, .icon-pause, .icon-menu, .icon-checkmark, .icon-checkmark-on, .icon-close, .icon-more, .icon-sorting, .icon-additem, .icon-carousel-right, .icon-carousel-left, .icon-games-carousel-right, .icon-games-carousel-left, .icon-games-carousel-right.hover, .icon-games-carousel-left.hover, .icon-clock, .icon-grid, .icon-slideshow, .icon-pastname, .icon-checkmark-white, .icon-checkmark-white-bold, .icon-close-white, .icon-sandglass, .icon-notifications, .icon-menu-black, .icon-checkmark-blue, .icon-default-navigation, .icon-nav-robux, .icon-nav-tix, .icon-nav-settings, .icon-nav-menu{
	filter: grayscale(1) brightness(0.8);
}
#nav-robux-icon:hover{
	filter: sepia(10) hue-rotate(40deg) saturate(120) brightness(0.75);
}
.rbx-left-col li{
	overflow: visible;
}
#navigation{
	background: transparent;
    box-shadow: none;
    border: none;
}
#navigation ul .rbx-divider{
	display: none;
}
#mCSB_1_container ul li a span + span {
    position: absolute;
    top: -1px;
    left: 40px;
}
#mCSB_1_container ul li {
    transition: width 0.5s, opacity 0.5s, background-color 0.5s, border 0.5s, border-radius 0.5s;
    width: 30px;
    opacity: 0.2;
    overflow: hidden;
    margin-bottom: 25px !important;
    border: none;
  }
#mCSB_1_container ul li:hover {
    opacity: 1;
    animation-name: expand;
    animation-duration: 0.75s;
    animation-timing-function: ease-in-out;
    width: 120px !important;
    border: 5px solid rgba(/*[[Mcolor-rgb]]*/,1);
    border-radius: 5px;
    background-color: rgba(/*[[Mcolor-rgb]]*/,1);
  }
#mCSB_1_container li:hover span[class^="icon"]{
	animation-name: filterSwap;
    animation-duration: 1.5s;
    animation-timing-function: ease-in-out;
    filter: brightness(100000) grayscale(2);
}
#navigation .rbx-scrollbar{
    width: -50px !important;
    height: 100% !important;
    overflow: visible !important;
  }
.rbx-left-col > ul li a, .rbx-scrollbar .rbx-upgrade-now{
	display: none !important;
}
  /*[[Ads]]*/
.rbx-header .rbx-navbar-icon-group li .rbx-menu-item, .rbx-header .rbx-navbar-icon-group li .rbx-menu-item:link, .rbx-header .rbx-navbar-icon-group li .rbx-menu-item:active, .rbx-header .rbx-navbar-icon-group li .rbx-menu-item:hover, .rbx-header .rbx-navbar-icon-group li .rbx-menu-item:visited, .rbx-header .rbx-navbar-icon-group li .rbx-menu-item:focus{
	color: #CCCCCC !important;
}
h1, h2, h3, h4, h5, p, .text-description, body{
	color: #777 !important;
}
.icon-nav-home, .icon-nav-profile, .icon-nav-message, .icon-nav-friends, .icon-nav-charactercustomizer, .icon-nav-inventory, .icon-nav-trade, .icon-nav-group, .icon-nav-forum, .icon-nav-blog, .icon-nav-shop{
	filter: brightness(0) grayscale(2);
}
#nav-home:hover, #nav-inventory:hover, #nav-profile:hover, #nav-character:hover, #nav-message:hover, #nav-friends:hover, #nav-trade:hover, #nav-group:hover, #nav-forum:hover, #nav-blog:hover, #nav-shop:hover, .rbx-left-col li a{
	color: #fff;
}
.alert-info{
	background: transparent;
    color: rgba(0,0,0,0);
    transition: background 1s, color 1s; 
}
.alert-info a:link, .alert-info a:visited, .alert-info a:hover, .alert-info a:focus, .alert-info a:active, .alert-info .alert-link:link, .alert-info .alert-link:visited, .alert-info .alert-link:hover, .alert-info .alert-link:focus, .alert-info .alert-link:active{
	color: inherit;
}
.alert-info:hover{
	background: /*[[Mcolor]]*/;
    color: white;
   	animation-name: showerInfo;
    animation-duration: 2s;
}
.rbx-nav-sponsor img, .rbx-nav-sponsor{
	overflow: visible !important;
}
.btn-neutral, .btn-neutral:link, .btn-neutral:active, .btn-neutral:visited{
	background-blend-mode: saturation; 
    background-color: /*[[Mcolor]]*/;
    border-color: /*[[Dcolor]]*/
}
.CustomizeCharacterContainer .AssetName a, .AttireCategory a.AttireCategorySelector, .AttireCategory .AttireCategorySelector_Selected, #ctl00_ctl00_cphRoblox_cphMyRobloxContent_cmdInvalidateThumbnails, .FooterPager a, .para-overflow-toggle .toggle-para, .text-link, .vlist .list-item .list-body .list-content a, .text-link:link, .vlist .list-item .list-body .list-content a:link, .text-link:hover, .vlist .list-item .list-body .list-content a:hover, .text-link:visited, .vlist .list-item .list-body .list-content a:visited, .text-link:active, .vlist .list-item .list-body .list-content a:active, .text-name, .text-name:link, .text-name:hover, .text-name:visited, .text-name:active, a.text-link, .InventoryItemName, #left-column .StandardBox a, #mid-column .StandardBox a, #right-column .StandardBox a, tr.forum-post > .forum-content-background:first-child .normalTextSmallBold{
	color: /*[[Mcolor]]*/;
}
a[disabled=disabled], a[disabled=disabled]:hover, a[disabled], a[disabled]:hover{
	color: grey;
}
.checkbox label:hover::before{
	border-color: /*[[Mcolor]]*/;
}
.game-card-container{
    transition: height 0.5s, top 0.5s;
	height: 180px !important;
    overflow: hidden;
    position: relative;
    top: 25px;
}
.game-card-container:hover{
    transition: height 0.5s, top 0.5s;
    height: 120% !important;
    top: 0px;
}
.chat-container.collapsed .chat-main, .chat-main, .dialog-container, .dialog-container.collapsed{
	transition: height 0.5s;
}
.chat-friend-list .chat-friend-container, .input-group-btn .dropdown-menu li a{
    transition: box-shadow 0.5s;
}
.chat-friend-list .chat-friend-container:hover, .input-group-btn .dropdown-menu li a:hover, .input-group-btn .dropdown-menu li a:focus, .input-group-btn .dropdown-menu li a.active{
	box-shadow: 4px 0 0 0 /*[[Mcolor]]*/ inset;
    background: transparent;
    transition: box-shadow 0.5s;
}
.input-dropdown-btn:hover, .open .input-dropdown-btn, .input-dropdown-btn:focus{
	background-color: /*[[Mcolor]]*/ !important;
    border-color: /*[[Mcolor]]*/ !important;
    outline: none;
    transition: background-color 0.5s, border-color 0.5s;
}
.input-group-btn .dropdown-menu li a{
	background-color: #fff !important;
}

}
