css:
@-moz-document domain("ottoneu.fangraphs.com") {
body {
	font-family: "Helvetica Neue", "Helvetica", "Roboto", "Arial", sans-serif;
	background-color: #fff;
	color: #333;
}

* {
	letter-spacing: normal;
}

.nav--primary li a,
.nav--primary li .reset-button {
	padding-bottom: 1.25em;
}

.tickercontainer {
	padding: 10px;
	height: 44px;
}

.newsticker {
	white-space: nowrap;
}

.newsticker li {
	float: none;
	display: inline-block;
}

.newsticker span {
	font-size: 14px;
	margin-right: 40px;
}

select {
	padding: 5px 8px;
}

main {
	margin: 16px;
}

#errors,
#errors a,
#errors a:focus,
#errors a:link,
#errors a:active,
#errors a:hover {
	color: #fff;
}

.error, #send_error, #email_notification, #errors {
	padding: 8px;
}

.message-count {
	font-family: "Helvetica Neue", "Helvetica", "Roboto", "Arial", sans-serif;
    font-size: 11px;
    font-weight: bold;
    color: #333;
}

@media screen and (min-width: 600px) {

	.error, #send_error, #email_notification, #errors {
		padding: 8px;
	}
	.header--primary {
		padding-top: 15px;
	}
	.ottoneu-logo {
		margin-bottom: 15px;
	}
	.team-switcher-menu {
		margin-top: 3.5rem;
	}
}

@media screen and (min-width: 365px) {
	.nav li > span, .nav li a, .nav li .reset-button {
		padding: 1em 16px;
	}
}

@media screen and (max-width: 1200px){
	.desktop-navigation {
		display: none;
	}
	.mobile-navigation {
		display: block;
	}
	.container {
		padding: 0 8px;
	}
}

@media screen and (min-width: 1200px){
	.mobile-navigation {
		display: none;
	}
	.desktop-navigation {
		display: block;
	}
	main {
		max-width: 1440px;
    	padding: 0px;
    }
}

@media screen and (min-width: 1400px) {
	main {
		margin: 16px;
	    width: auto;
    }
}

@media screen and (min-width: 1472px) {
	main {
		margin: 16px auto;
	    width: 100%;
    }
}

a, .reset-button {
	color: #3c7e57;
}
.messages-byline a,
.messages-byline .reset-button {
	color: #7d8686;
}
.messages-item:hover {
	background-color: #fef3e8;
}

.team-switcher-menu a.team,
.team-switcher-menu .reset-button.team {
	color: #f9dfa0;
}

.team-switcher-menu a.team:hover {
	text-decoration: underline;
}

.lineup-table tr:nth-child(even),
tr:nth-child(odd) {
	background-color: #ffffff;
}
.lineup-table tr:nth-child(odd),
tr:nth-child(even) {
	background-color: #ffffff;
}
th {
	background-color: #f1f0f0;
}
.alert,
.filterNote,
.site_note,
#notifications,
.notifications {
	background-color: #fcf2e2;
	border-color: #d2c2a2;
	padding: 10px;
}
#notifications:first-child {
	background-color: #c6e4d5;
}

#notifications {
	margin-bottom: -1px;
}

#notifications:last-child {
	margin-bottom: 16px;
}

h1,
h2 {
	margin-bottom: 16px;
	letter-spacing: 0;
}

p {
	font-size: 14px;
}

.pending-trade,
.pending-trade a {
	color: #e48c65;
}

.page-header {
	border-width: 0;
	margin-bottom: 16px;
}

.page-header.page-header__primary {
	border-width: 0;
}

.page-header__primary {
	border-width: 0;
	border-style: solid;
	border-color: #dbe2e2;
	position: relative;
}

.page-header__primary h1,
.page-header__primary h2,
.page-header__primary h3,
.page-header__primary h4 {
	margin-top: 0;
}

.page-header__primary h3 {
	color: #929b9b;
	margin-right: 0;
}

.page-header__section:not(:last-child),
.page-header__section--justify:not(:last-child),
.page-header__section--split:not(:last-child) {
	border-width: 1px 0 1px 1px;
}

.page-header__section,
.page-header__section--justify,
.page-header__section--split {
	border-width: 1px;
	border-style: solid;
	border-color: #dbe2e2;
	padding: 8px;
}

.page-header__secondary p, 
.page-header__secondary a, 
.page-header__secondary .reset-button {
	font-size: 13px;
}

.page-header__secondary a.button {
	display: inline-block;
    margin: 0 8px 8px;
}

header + .page-header__secondary {
	border-width: 0 1px;
}

.page-header__secondary {
	background-color: #ffffff;
	border-color: #dbe2e2;
	border-style: solid;
	border-width: 0;
	margin-top: -1px;
}

.page-header__secondary.page-header__section,
.page-header__secondary.page-header__section--justify,
.page-header__secondary.page-header__section--split {
	border-width: 1px;
}


h1, h2, h3, h4, h5, h6 {
	margin-bottom: 0.5em;
}

h1, h2, h3, h4, h5, h6,
.page-header__primary h2,
.page-header__primary h3,
.page-header__primary h4,
input, select, textarea,
[type="submit"], button, .button,
.nav--primary li,
.page-header__secondary h3,
.page-header__secondary p,
.page-header__secondary a,
.page-header__secondary .reset-button,
.page-header__secondary li,
.section-container-header__title-secondary,
.section-container-header__action,
.messages-byline,
.joinLeagueLink,
.page-header__section label,
.page-header__section--justify label,
.page-header__section--split label,
.trade-message,
.draft-page h4,
table {
	text-transform: none;
	letter-spacing: normal;
	font-family: "Helvetica Neue", "Helvetica", "Roboto", "Arial", sans-serif;
}
th {
	text-transform: none;
	letter-spacing: normal;
}
th,
td {
	padding: 7px 8px;
}
.table-container + p,
.table-container + h2 {
	margin-top: 16px;
}
.nav--primary li.here a:after,
.nav--primary li.here .reset-button:after {
	bottom: -14px;
}

.messages-navigation .here {
	font-weight: 600;
}

h1 form {
	margin: -9px 3px -9px -9px;
}

.page-header__primary h1 select {
	font-size: inherit;
	padding: 9px 8px 13px 4px;
}

.page-header__primary h1 select option {
	font-size: inherit;
	font-weight: 300;
}

.page-header__primary h1 select option:checked {
	font-weight: 600;
}

.team-switcher-menu li {
	padding: 12px 16px;
}
main .filterNote,
main .site_note,
main .notifications,
main #notifications {
	margin-bottom: 16px;
}

.section-container, .section-container--clear {
	margin-bottom: 16px;
}

.section-container-header {
	padding: 8px;
}

.section-container-header__title-secondary {
	padding-top: 4px;
	font-weight: 400;
	color: #828b8b;
}

.lineup-position tr:last-child td {
	border-bottom: 1px solid #dbe2e2;
}

.lineup__break {
	padding: 3px;
	background-color: #f1f0f0;
	border-bottom: 1px solid #dbe2e2;
}

.messages-byline h4 {
	font-size: 13px;
}

.unread-messages::after {
	top: 16px;
	left: 0;
}

.messages-navigation .unread-messages::after {
	top: 6px;
	left: -15px;
}

table.stats th {
	text-align: center;
}

.rotowire {
	width: auto;
	padding: 8px;
}

.trade-comment {
	clear: left;
	margin-top: 0;
	padding-top: 16px;
}

#createLeague fieldset {
	background-color: transparent;
	line-height: normal;
}

a[name=preamble] h3 {
	margin-top: 1rem;
}

.button + .button {
	margin-left: 8px;
}

/* Draft Manager */

#searchTable, #auctionWatchlistTable, #draftHistoryTable {
	width: 100%;
}

.draft_search {
	padding: 0;
}

#skipMe {
	padding-top: 6px;
    padding-left: 22px;
    text-indent: -22px;
}

/* Special Trade Block Overrides */

.page-header__section--split + .page-header__section--split + .page-header__section {
	position: absolute;
	right: 0px;
	border-width: 0 1px 0 0;
	border-style: solid;
	border-color: #dbe2e2;
	height: 100%;
}

main > div + form {
	margin-top: 16px;
}

/* Live Updates */

#liveDataWrapper {
	margin: 0;
	padding: 0;
}

#liveDataContent {
	border-width: 0;
}

#teamlist {
	width: 19%;
}

#teamlist li.active,
#teamlist li:hover {
	padding: 0;
}

#teamlist li:active a,
#teamlist li:hover a {
	color: #000;
	padding: 6px;
}

#teamlist li,
#teamlist li.top,
#teamlist li.bottom {
	font-size: 14px;
	line-height: normal;
	border-color: #dbe2e2;
}

#teamlist li.active a {
	color: #000;
	padding: 6px;
	cursor: default;
}

#teamlist li.active a:hover,
#teamlist li.active a:active {
    color: #000;
}

@media only screen and (max-width: 1024px) {
	#liveDataContent .table-container table {
		font-size: 13px;
	}
}

@media only screen and (max-width: 900px) {
	#liveDataContent .table-container table {
		font-size: 12px;
	}
}
}
