@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mozilla.org") {
  
  html, body {
    background: black;
    overflow-x: hidden;
  }
  body {
    color: #444;
  }
  img {
    max-width: auto !important;
  }
  .brand-popup {
    display: none;
  }
  
  #masthead {
    background: transparent !important;
    width: 200px !important;
  }
  .masthead {
    background: transparent !important;
    width: 200px !important;
    /*Правило повторяется для перегрузки*/
    padding: 0;
    position: absolute;
    top: 0;
    left: 0;
    
    z-index: 4;
    float: left;
  }
  #masthead div.content {
    padding: 0 !important;
    width: 200px !important;
}
  #masthead .masthead-logo {
    background: transparent;
    float: none;
    position: relative;
    margin: 0;
    padding: 0;
    height: 100%;
    width: auto; /*Важная строка*/
    text-align: left;
    line-height: 0;
  }
  #masthead .masthead-logo img {
    background: rgba(255,255,255,.4);
    height: 42px !important;
    position: relative;
    max-width: auto !important;
    width: 130px;
    box-shadow: -1px 2px 2px 0 black;
}
  #nav-download-firefox, #masthead .download-button {
    float: none;
    width: 130px;
  }
  #nav-download-firefox .button {
    padding: 10px;
  }
  .button, a.button:link {
    border: 2px solid #47a44b;
  }
  .masthead-nav-main {
    float: none;
    width: auto;
    position: relative;
    margin: 0;
  }
  .masthead-nav-main .nav-main-menu {
    background: transparent;
    width: 130px;
    text-align: center;
  }
  #nav-main-menu {
    display: block !important;
  }
  #nav-main-menu li {
    width: auto;
    border: 0 !important;
    float: none !important;
    padding: 0 !important;
  }
  .internet-health item, .technology-item, .donate-item {
    
  }
  
  #intro {
    display: none;
  }
  #impact-innovate-wrapper {
    margin: 0 auto;
    top: 40px;
    z-index: 1;
    max-width: 1350px;
    text-shadow: 0 0 2px black;
  }
  #impact, #innovate {
    background: transparent;
    color: #EAEAEA !important;
    max-width: 800px;
    width: 50%;
    z-index: 2;
  }
  #impact {
    padding-right: 10%;
  }
  #innovate {
    padding-left: 10%;
  }
  #impact .section-content, #innovate .section-content {
    background: transparent;
    padding: 10px 0 20px;
  }
   #impact .section-content:before, #innovate .section-content:before {
    background-size: 25%;
    height: 200px;
     margin-bottom: 10px;
  }
  
  .section-content .section-headline {
	font-size: 1.6rem;
}
  .section-content p {
    font-size: 1.2rem;
  }
  
  #impact .section-content p {
    text-shadow: -1px -1px 1px black;
    padding: 0;
  }
    #innovate .section-content p {
    text-shadow: 1px -1px 1px black;
    padding: 0;
      color: #D93A33;
  }
  .icon-links {
    width: 80%;
  }
  
#firefox.section {
	position: absolute;
	width: 100%;
	top: 0px;
	z-index: 0;
}
  
  #firefox .horizon .content {
    padding: 0 0 250px;
    z-index: 4;
  }
  #firefox .forest-container {
    height: 420px;
  }
  #firefox h2 {
	padding-top: 40px;
}
  #firefox .content a, #firefox .content a:hover {
	position: absolute;
	top: 0;
	left: 40.5%;
}
  
  .section-externals {
    margin-top: 30px;
  }
  .section-externals ul {
    padding: 10px 0;
  }
  .section-externals h3 {
    padding: 0;
    z-index: 2;
  }
  .section-externals h3::before {
    height: 78px;
    opacity: .3;
    z-index: 0;
  }
  .section-externals p {
    margin: 0;
    z-index: 4;
  }
  
  .section-newsletter .content {
    padding: 0;
  }
  
  /* Footer */
  #colophon {
    background: #2A2A2A;
    width: 100%;
    max-width: 1500px;
    margin: 0 auto;
    border-radius: 0 0 10px 10px;
    box-shadow: 0 0 20px 15px #2A2A2A;
    overflow-x: hidden;
    padding: 14px 0 0;
  }
  
  /*https://developer.mozilla.org/ru/docs/Project:MDN/Localizing/Localization_projects$edit*/
  .approved {
    width: 40%;
  }
  .localized {
    width: 60%
  }
  .editor-container .editor div {
    height: auto;
  }
  .editor-container .editor div div {
    width: auto;
    max-width: 750px;
  }
  .cke_toolbar_break {
    display: none;
  }
  /*https://developer.mozilla.org/en-US/docs/Glossary/Specification$locales*/
  #select-locale .locales li {
	max-width: 250px;
	display: inline-block;
	margin-left: 12px;
	line-height: 2;
}

  /*https://mail.mozilla.org/listinfo/dev-addons*/
  #utility a {
    font-size: 12pt;
  }
  
}
